        }

        .checkbox-group {
            display: flex;
            flex-wrap: wrap;
            gap: 15px;
            align-items: center;
        }

        .checkbox-item {
            display: flex;
            align-items: center;
            gap: 5px;
        }

        #visualization {
            position: relative;
            width: 100%;
            height: 600px;
        }

        #canvas-container {
            width: 100%;
            height: 100%;
            position: relative;
        }

        .info-box {
            position: absolute;
            background-color: rgba(255, 255, 255, 0.9);
            border: 1px solid #ddd;
            border-radius: 4px;
            padding: 10px;
            box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
            min-width: 150px;
        }

        #info-box-main {
            top: 20px;
            right: 20px;
        }

        #info-box-alpha {
            bottom: 20px;
            left: 20px;
            background-color: rgba(255, 229, 204, 0.9);
            border-color: #FFA500;
        }

        #info-box-beta {
            bottom: 20px;
            right: 20px;
            background-color: rgba(255, 229, 255, 0.9);
            border-color: #FF00FF;
        }

        .info-title {
            font-weight: bold;
            margin-bottom: 8px;
        }

        .info-line {
            margin: 3px 0;
        }

        .hidden {
            display: none;
        }
    </style>
</head>
<body>
    <div id="container">
        <div id="controls">
            <div class="view-buttons">
                <button id="btn-diagram" class="active">Diagram</button>
                <button id="btn-phases">Phases</button>
            </div>
            <div class="checkbox-group" id="diagram-controls">
                <div class="checkbox-item">
                    <input type="checkbox" id="check-grid-lines">
                    <label for="check-grid-lines">Grid lines</label>
                </div>
                <div class="checkbox-item">
                    <input type="checkbox" id="check-tie-lines" checked>
                    <label for="check-tie-lines">Tie lines</label>
                </div>
                <div class="checkbox-item">
                    <input type="checkbox" id="check-alpha-phase" checked>
                    <label for="check-alpha-phase">Alpha phase</label>
                </div>
                <div class="checkbox-item">
                    <input type="checkbox" id="check-beta-phase" checked>
                    <label for="check-beta-phase">Beta phase</label>
                </div>
            </div>
        </div>

        <div id="visualization">
            <div id="canvas-container"></div>

            <div id="info-box-main" class="info-box">
                <div class="info-title">Mass fractions</div>
                <div class="info-line">\(x_A = 0.42\)</div>
                <div class="info-line">\(x_B = 0.47\)</div>
                <div class="info-line">\(x_C = 0.11\)</div>
            </div>

            <div id="info-box-alpha" class="info-box hidden">
                <div class="info-title">Alpha phase</div>
                <div class="info-line">\(x_A^\alpha = 0.00\)</div>
                <div class="info-line">\(x_B^\alpha = 0.00\)</div>
                <div class="info-line">\(x_C^\alpha = 0.00\)</div>
            </div>

            <div id="info-box-beta" class="info-box hidden">
                <div class="info-title">Beta phase</div>
                <div class="info-line">\(x_A^\beta = 0.00\)</div>
                <div class="info-line">\(x_B^\beta = 0.00\)</div>
                <div class="info-line">\(x_C^\beta = 0.00\)</div>
            </div>
        </div>
    </div>

    <script>
        // Application state
        const state = {
            currentView: 'diagram',
            showGridLines: false,
            showTieLines: true,
            showAlphaPhase: true,
            showBetaPhase: true,
            userPoint: { xA: 0.42, xB: 0.47, xC: 0.11 },
            isTwoPhase: false,
            alphaPhase: { xA: 0, xB: 0, xC: 0 },
            betaPhase: { xA: 0, xB: 0, xC: 0 },

            // Hardcoded phase envelope data
            phaseEnvelope: [
                // Alpha curve points (from A vertex to plait point)
                {xA: 1.0, xB: 0.0, xC: 0.0},
                {xA: 0.9, xB: 0.05, xC: 0.05},
                {xA: 0.8, xB: 0.1, xC: 0.1},
                {xA: 0.7, xB: 0.15, xC: 0.15},
                {xA: 0.6, xB: 0.2, xC: 0.2},
                {xA: 0.5, xB: 0.25, xC: 0.25}, // plait point
                // Beta curve points (from plait point to B-C side)
                {xA: 0.4, xB: 0.35, xC: 0.25},
                {xA: 0.3, xB: 0.45, xC: 0.25},
                {xA: 0.2, xB: 0.55, xC: 0.25},
                {xA: 0.1, xB: 0.65, xC: 0.25},
                {xA: 0.0, xB: 0.75, xC: 0.25}
            ],

            // Hardcoded tie lines
            tieLines: [
                { alpha: {xA: 0.9, xB: 0.05, xC: 0.05}, beta: {xA: 0.1, xB: 0.65, xC: 0.25} },
                { alpha: {xA: 0.8, xB: 0.1, xC: 0.1}, beta: {xA: 0.2, xB: 0.55, xC: 0.25} },
                { alpha: {xA: 0.7, xB: 0.15, xC: 0.15}, beta: {xA: 0.3, xB: 0.45, xC: 0.25} },
                { alpha: {xA: 0.6, xB: 0.2, xC: 0.2}, beta: {xA: 0.4, xB: 0.35, xC: 0.25} }
            ],

            // Canvas dimensions
            canvasWidth: 0,
            canvasHeight: 0,
            margin: 50,

            // Triangle vertices (will be calculated in setup)
            pA: {x: 0, y: 0},
            pB: {x: 0, y: 0},
            pC: {x: 0, y: 0}
        };

        // DOM elements
        const elements = {
            btnDiagram: document.getElementById('btn-diagram'),
            btnPhases: document.getElementById('btn-phases'),
            checkGridLines: document.getElementById('check-grid-lines'),
            checkTieLines: document.getElementById('check-tie-lines'),
            checkAlphaPhase: document.getElementById('check-alpha-phase'),
            checkBetaPhase: document.getElementById('check-beta-phase'),
            diagramControls: document.getElementById('diagram-controls'),
            infoBoxMain: document.getElementById('info-box-main'),
            infoBoxAlpha: document.getElementById('info-box-alpha'),
            infoBoxBeta: document.getElementById('info-box-beta')
        };

        // Initialize event listeners
        function initEventListeners() {
            // View buttons
            elements.btnDiagram.addEventListener('click', () => switchView('diagram'));
            elements.btnPhases.addEventListener('click', () => switchView('phases'));

            // Checkboxes
            elements.checkGridLines.addEventListener('change', (e) => {
                state.showGridLines = e.target.checked;
                redraw();
            });

            elements.checkTieLines.addEventListener('change', (e) => {
                state.showTieLines = e.target.checked;
                redraw();
            });

            elements.checkAlphaPhase.addEventListener('change', (e) => {
                state.showAlphaPhase = e.target.checked;
                updateInfoBoxVisibility();
                redraw();
            });

            elements.checkBetaPhase.addEventListener('change', (e) => {
                state.showBetaPhase = e.target.checked;
                updateInfoBoxVisibility();
                redraw();
            });
        }

        // Switch between diagram and phases view
        function switchView(view) {
            state.currentView = view;

            // Update button states
            elements.btnDiagram.classList.toggle('active', view === 'diagram');
            elements.btnPhases.classList.toggle('active', view === 'phases');

            // Show/hide diagram controls
            elements.diagramControls.style.display = view === 'diagram' ? 'flex' : 'none';

            // Update info box visibility
            updateInfoBoxVisibility();

            redraw();
        }

        // Update info box visibility based on current state
        function updateInfoBoxVisibility() {
            if (state.currentView === 'phases') {
                elements.infoBoxMain.classList.add('hidden');
                elements.infoBoxAlpha.classList.add('hidden');
                elements.infoBoxBeta.classList.add('hidden');
            } else {
                elements.infoBoxMain.classList.remove('hidden');

                if (state.isTwoPhase) {
                    elements.infoBoxAlpha.classList.toggle('hidden', !state.showAlphaPhase);
                    elements.infoBoxBeta.classList.toggle('hidden', !state.showBetaPhase);
                } else {
                    elements.infoBoxAlpha.classList.add('hidden');
                    elements.infoBoxBeta.classList.add('hidden');
                }
            }
        }

        // Update info box content with current values
        function updateInfoBoxContent() {
            // Main composition
            elements.infoBoxMain.innerHTML = `
                <div class="info-title">Mass fractions</div>
                <div class="info-line">\\(x_A = ${state.userPoint.xA.toFixed(2)}\\)</div>
                <div class="info-line">\\(x_B = ${state.userPoint.xB.toFixed(2)}\\)</div>
                <div class="info-line">\\(x_C = ${state.userPoint.xC.toFixed(2)}\\)</div>
            `;

            // Alpha phase (if in two-phase region)
            if (state.isTwoPhase) {
                elements.infoBoxAlpha.innerHTML = `
                    <div class="info-title">Alpha phase</div>
                    <div class="info-line">\\(x_A^\\alpha = ${state.alphaPhase.xA.toFixed(2)}\\)</div>
                    <div class="info-line">\\(x_B^\\alpha = ${state.alphaPhase.xB.toFixed(2)}\\)</div>
                    <div class="info-line">\\(x_C^\\alpha = ${state.alphaPhase.xC.toFixed(2)}\\)</div>
                `;

                elements.infoBoxBeta.innerHTML = `
                    <div class="info-title">Beta phase</div>
                    <div class="info-line">\\(x_A^\\beta = ${state.betaPhase.xA.toFixed(2)}\\)</div>
                    <div class="info-line">\\(x_B^\\beta = ${state.betaPhase.xB.toFixed(2)}\\)</div>
                    <div class="info-line">\\(x_C^\\beta = ${state.betaPhase.xC.toFixed(2)}\\)</div>
                `;
            }

            // Render MathJax
            if (typeof MathJax !== 'undefined') {
                MathJax.typeset();
            }
        }

        // p5.js sketch
        let sketch = function(p) {
            let canvas;

            p.setup = function() {
                // Calculate canvas dimensions
                const container = document.getElementById('canvas-container');
                state.canvasWidth = container.offsetWidth;
                state.canvasHeight = container.offsetHeight;

                // Create canvas
                canvas = p.createCanvas(state.canvasWidth, state.canvasHeight);
                canvas.parent('canvas-container');

                // Calculate triangle vertices
                const size = Math.min(state.canvasWidth, state.canvasHeight) - 2 * state.margin;
                const centerX = state.canvasWidth / 2;
                const centerY = state.canvasHeight / 2 + size * 0.15;

                state.pA = { x: centerX, y: centerY - size * 0.577 }; // Top vertex
                state.pB = { x: centerX - size * 0.5, y: centerY + size * 0.289 }; // Bottom-left
                state.pC = { x: centerX + size * 0.5, y: centerY + size * 0.289 }; // Bottom-right

                // Set up mouse interaction
                canvas.mousePressed(handleCanvasClick);

                // Initial draw
                redraw();
            };

            p.draw = function() {
                p.background(255);

                // Draw the main triangle
                drawTriangle();

                if (state.currentView === 'diagram') {
                    drawDiagramView();
                } else {
                    drawPhasesView();
                }
            };

            function drawTriangle() {
                // Triangle outline
                p.stroke(0);
                p.strokeWeight(2);
                p.noFill();
                p.triangle(state.pA.x, state.pA.y, state.pB.x, state.pB.y, state.pC.x, state.pC.y);

                // Vertex labels
                p.textSize(16);
                p.textAlign(p.CENTER, p.CENTER);

                p.fill('#008800'); // A (green)
                p.text('A', state.pA.x, state.pA.y - 25);

                p.fill('#0000FF'); // B (blue)
                p.text('B', state.pB.x - 25, state.pB.y + 5);

                p.fill('#FF0000'); // C (red)
                p.text('C', state.pC.x + 25, state.pC.y + 5);

                // Axes ticks and labels
                p.textSize(12);
                for (let i = 1; i <= 9; i++) {
                    const frac = i * 0.1;

                    // A axis (top to bottom-left)
                    const aPos = p.lerp(state.pA.x, state.pB.x, frac);
                    const aPosY = p.lerp(state.pA.y, state.pB.y, frac);
                    p.stroke('#008800');
                    p.line(aPos, aPosY, aPos - 10, aPosY + 5);
                    p.fill('#008800');
                    p.text(frac.toFixed(1), aPos - 20, aPosY + 10);

                    // B axis (bottom-left to bottom-right)
                    const bPosX = p.lerp(state.pB.x, state.pC.x, frac);
                    const bPosY = p.lerp(state.pB.y, state.pC.y, frac);
                    p.stroke('#0000FF');
                    p.line(bPosX, bPosY, bPosX, bPosY + 10);
                    p.fill('#0000FF');
                    p.text(frac.toFixed(1), bPosX, bPosY + 25);

                    // C axis (bottom-right to top)
                    const cPosX = p.lerp(state.pC.x, state.pA.x, frac);
                    const cPosY = p.lerp(state.pC.y, state.pA.y, frac);
                    p.stroke('#FF0000');
                    p.line(cPosX, cPosY, cPosX + 10, cPosY + 5);
                    p.fill('#FF0000');
                    p.text(frac.toFixed(1), cPosX + 20, cPosY + 10);
                }
            }

            function drawDiagramView() {
                // Grid lines
                if (state.showGridLines) {
                    p.stroke(220);
                    p.strokeWeight(0.5);

                    for (let i = 1; i <= 9; i++) {
                        const frac = i * 0.1;

                        // Lines parallel to BC (from A to BC line)
                        const startA = {
                            x: p.lerp(state.pA.x, state.pB.x, frac),
                            y: p.lerp(state.pA.y, state.pB.y, frac)
                        };
                        const endA = {
                            x: p.lerp(state.pA.x, state.pC.x, frac),
                            y: p.lerp(state.pA.y, state.pC.y, frac)
                        };
                        p.line(startA.x, startA.y, endA.x, endA.y);

                        // Lines parallel to AC (from B to AC line)
                        const startB = {
                            x: p.lerp(state.pB.x, state.pA.x, frac),
                            y: p.lerp(state.pB.y, state.pA.y, frac)
                        };
                        const endB = {
                            x: p.lerp(state.pB.x, state.pC.x, frac),
                            y: p.lerp(state.pB.y, state.pC.y, frac)
                        };
                        p.line(startB.x, startB.y, endB.x, endB.y);

                        // Lines parallel to AB (from C to AB line)
                        const startC = {
                            x: p.lerp(state.pC.x, state.pA.x, frac),
                            y: p.lerp(state.pC.y, state.pA.y, frac)
                        };
                        const endC = {
                            x: p.lerp(state.pC.x, state.pB.x, frac),
                            y: p.lerp(state.pC.y, state.pB.y, frac)
                        };
                        p.line(startC.x, startC.y, endC.x, endC.y);
                    }
                }

                // Phase envelope
                p.strokeWeight(2);
                p.noFill();

                // Draw alpha curve (orange)
                p.stroke(255, 165, 0);
                for (let i = 0; i < state.phaseEnvelope.length / 2; i++) {
                    const point = state.phaseEnvelope[i];
                    const pixel = barycentricToPixel(point);
                    if (i === 0) {
                        p.beginShape();
                        p.vertex(pixel.x, pixel.y);
                    } else {
                        p.vertex(pixel.x, pixel.y);
                    }
                }
                p.endShape();

                // Draw beta curve (magenta)
                p.stroke(255, 0, 255);
                for (let i = Math.floor(state.phaseEnvelope.length / 2) - 1; i < state.phaseEnvelope.length; i++) {
                    const point = state.phaseEnvelope[i];
                    const pixel = barycentricToPixel(point);
                    if (i === Math.floor(state.phaseEnvelope.length / 2) - 1) {
                        p.beginShape();
                        p.vertex(pixel.x, pixel.y);
                    } else {
                        p.vertex(pixel.x, pixel.y);
                    }
                }
                p.endShape();

                // Tie lines
                if (state.showTieLines) {
                    p.stroke(150);
                    p.strokeWeight(1);

                    for (const tieLine of state.tieLines) {
                        const alphaPixel = barycentricToPixel(tieLine.alpha);
                        const betaPixel = barycentricToPixel(tieLine.beta);
                        p.line(alphaPixel.x, alphaPixel.y, betaPixel.x, betaPixel.y);
                    }
                }

                // User point
                const userPixel = barycentricToPixel(state.userPoint);
                p.fill(0);
                p.noStroke();
                p.circle(userPixel.x, userPixel.y, 10);

                // Projection lines
                p.strokeWeight(1);

                // A projection (green, parallel to BC)
                p.stroke(0, 128, 0);
                p.line(userPixel.x, userPixel.y, userPixel.x - userPixel.y * 0.577, 0);
                p.drawArrow(userPixel.x, userPixel.y, userPixel.x - userPixel.y * 0.577, 0);

                // B projection (blue, parallel to AC)
                p.stroke(0, 0, 255);
                const bProjX = userPixel.x + (state.canvasHeight - userPixel.y) * 0.577;
                const bProjY = state.canvasHeight;
                p.line(userPixel.x, userPixel.y, bProjX, bProjY);
                p.drawArrow(userPixel.x, userPixel.y, bProjX, bProjY);

                // C projection (red, parallel to AB)
                p.stroke(255, 0, 0);
                const cProjX = userPixel.x - (state.canvasHeight - userPixel.y) * 0.577;
                const cProjY = state.canvasHeight;
                p.line(userPixel.x, userPixel.y, cProjX, cProjY);
                p.drawArrow(userPixel.x, userPixel.y, cProjX, cProjY);

                // Check if point is in two-phase region
                state.isTwoPhase = isPointInPhaseEnvelope(state.userPoint);

                if (state.isTwoPhase) {
                    // Find the tie line the point is on (simplified interpolation)
                    const closestTieLine = findClosestTieLine(state.userPoint);

                    // Calculate alpha and beta phases (simplified)
                    const leverRatio = calculateLeverRatio(state.userPoint, closestTieLine);
                    state.alphaPhase = closestTieLine.alpha;
                    state.betaPhase = closestTieLine.beta;

                    // Draw connection line
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.drawingContext.setLineDash([5, 3]);
                    const alphaPixel = barycentricToPixel(state.alphaPhase);
                    const betaPixel = barycentricToPixel(state.betaPhase);
                    p.line(alphaPixel.x, alphaPixel.y, betaPixel.x, betaPixel.y);
                    p.drawingContext.setLineDash([]);

                    // Draw alpha phase point
                    if (state.showAlphaPhase) {
                        p.fill(255, 165, 0);
                        p.noStroke();
                        p.circle(alphaPixel.x, alphaPixel.y, 10);

                        // Dashed projection lines for alpha phase
                        p.stroke(255, 165, 0, 150);
                        p.strokeWeight(1);
                        p.drawingContext.setLineDash([3, 3]);

                        // A projection
                        p.line(alphaPixel.x, alphaPixel.y, alphaPixel.x - alphaPixel.y * 0.577, 0);

                        // B projection
                        const alphaBProjX = alphaPixel.x + (state.canvasHeight - alphaPixel.y) * 0.577;
                        p.line(alphaPixel.x, alphaPixel.y, alphaBProjX, state.canvasHeight);

                        // C projection
                        const alphaCProjX = alphaPixel.x - (state.canvasHeight - alphaPixel.y) * 0.577;
                        p.line(alphaPixel.x, alphaPixel.y, alphaCProjX, state.canvasHeight);

                        p.drawingContext.setLineDash([]);
                    }

                    // Draw beta phase point
                    if (state.showBetaPhase) {
                        p.fill(255, 0, 255);
                        p.noStroke();
                        p.circle(betaPixel.x, betaPixel.y, 10);

                        // Dashed projection lines for beta phase
                        p.stroke(255, 0, 255, 150);
                        p.strokeWeight(1);
                        p.drawingContext.setLineDash([3, 3]);

                        // A projection
                        p.line(betaPixel.x, betaPixel.y, betaPixel.x - betaPixel.y * 0.577, 0);

                        // B projection
                        const betaBProjX = betaPixel.x + (state.canvasHeight - betaPixel.y) * 0.577;
                        p.line(betaPixel.x, betaPixel.y, betaBProjX, state.canvasHeight);

                        // C projection
                        const betaCProjX = betaPixel.x - (state.canvasHeight - betaPixel.y) * 0.577;
                        p.line(betaPixel.x, betaPixel.y, betaCProjX, state.canvasHeight);

                        p.drawingContext.setLineDash([]);
                    }
                }

                // Update info boxes
                updateInfoBoxContent();
                updateInfoBoxVisibility();
            }

            function drawPhasesView() {
                // Fill one-phase region (light red)
                p.fill(251, 229, 230);
                p.noStroke();
                p.beginShape();
                p.vertex(state.pA.x, state.pA.y);
                p.vertex(state.pB.x, state.pB.y);
                p.vertex(state.pC.x, state.pC.y);
                p.endShape(p.CLOSE);

                // Fill two-phase region (light blue)
                p.fill(212, 234, 252);
                p.beginShape();
                for (const point of state.phaseEnvelope) {
                    const pixel = barycentricToPixel(point);
                    p.vertex(pixel.x, pixel.y);
                }
                p.endShape(p.CLOSE);

                // Labels
                p.textSize(16);
                p.fill(0);
                p.noStroke();

                // One-phase label
                p.textAlign(p.CENTER, p.CENTER);
                const onePhaseX = (state.pA.x + state.pB.x + state.pC.x) / 3;
                const onePhaseY = (state.pA.y + state.pB.y + state.pC.y) / 3;
                p.text("one phase", onePhaseX, onePhaseY - 50);

                // Two-phase label
                p.text("two phases", onePhaseX, onePhaseY + 30);

                // Draw phase envelope boundary
                p.strokeWeight(2);

                // Alpha curve (orange)
                p.stroke(255, 165, 0);
                for (let i = 0; i < state.phaseEnvelope.length / 2; i++) {
                    const point = state.phaseEnvelope[i];
                    const pixel = barycentricToPixel(point);
                    if (i === 0) {
                        p.beginShape();
                        p.vertex(pixel.x, pixel.y);
                    } else {
                        p.vertex(pixel.x, pixel.y);
                    }
                }
                p.endShape();

                // Beta curve (magenta)
                p.stroke(255, 0, 255);
                for (let i = Math.floor(state.phaseEnvelope.length / 2) - 1; i < state.phaseEnvelope.length; i++) {
                    const point = state.phaseEnvelope[i];
                    const pixel = barycentricToPixel(point);
                    if (i === Math.floor(state.phaseEnvelope.length / 2) - 1) {
                        p.beginShape();
                        p.vertex(pixel.x, pixel.y);
                    } else {
                        p.vertex(pixel.x, pixel.y);
                    }
                }
                p.endShape();

                // Plait point
                const plaitPoint = state.phaseEnvelope[Math.floor(state.phaseEnvelope.length / 2) - 1];
                const plaitPixel = barycentricToPixel(plaitPoint);
                p.fill(0);
                p.noStroke();
                p.circle(plaitPixel.x, plaitPixel.y, 8);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text("plait point", plaitPixel.x, plaitPixel.y - 10);

                // Draw arrow to plait point
                p.stroke(0);
                p.strokeWeight(1);
                p.noFill();
                p.drawArrow(plaitPixel.x + 50, plaitPixel.y - 50, plaitPixel.x + 5, plaitPixel.y - 5);

                // User point
                const userPixel = barycentricToPixel(state.userPoint);
                p.fill(0);
                p.noStroke();
                p.circle(userPixel.x, userPixel.y, 10);
            }

            // Helper function to draw arrows
            p.drawArrow = function(x1, y1, x2, y2) {
                p.line(x1, y1, x2, y2);
                const angle = p.atan2(y2 - y1, x2 - x1);
                p.push();
                p.translate(x2, y2);
                p.rotate(angle);
                p.line(0, 0, -10, -5);
                p.line(0, 0, -10, 5);
                p.pop();
            };

            // Convert barycentric coordinates to pixel coordinates
            function barycentricToPixel(point) {
                const x = point.xA * state.pA.x + point.xB * state.pB.x + point.xC * state.pC.x;
                const y = point.xA * state.pA.y + point.xB * state.pB.y + point.xC * state.pC.y;
                return { x, y };
            }

            // Convert pixel coordinates to barycentric coordinates
