                    case 'TwoOpt':
                        tour = solveTwoOpt(currentPoints);
                        break;
                    case 'CCA':
                        tour = solveCCA(currentPoints);
                        break;
                    case '3Opt':
                        tour = solve3Opt(currentPoints);
                        break;
                }

                const endTime = performance.now();
                const duration = (endTime - startTime) / 1000; // Convert to seconds
                const length = tourLength(currentPoints, tour);

                currentTours[method] = tour;
                currentResults[method] = {
                    time: duration,
                    length: length
                };

                // Update table
                document.getElementById(`timing-${method.toLowerCase()}`).textContent = duration.toFixed(6);
                document.getElementById(`result-${method.toLowerCase()}`).textContent = length.toFixed(5);
            });

            // Determine winner
            const mathMethods = ['OrZweig', 'OrOpt', 'TwoOpt', 'CCA'];
            const mathMin = Math.min(...mathMethods.map(m => currentResults[m].length));
            const opt3Result = currentResults['3Opt'].length;

            const epsilon = 1e-6;
            let winnerText = "Tie";
            if (mathMin < opt3Result - epsilon) {
                winnerText = "Mathematica wins";
            } else if (opt3Result < mathMin - epsilon) {
                winnerText = "3-Opt wins";
            }

            document.getElementById('winner-display').textContent = winnerText;

            // Redraw plots
            leftSketch.redraw();
            rightSketch.redraw();
        }

        // Event listeners
        document.getElementById('btn-random-set').addEventListener('click', function() {
            const newSeed = Math.floor(Math.random() * 99999) + 1;
            document.getElementById('seed-input').value = newSeed;
            calculateAndRender();
        });

        document.getElementById('btn-reset-seed').addEventListener('click', function() {
            document.getElementById('seed-input').value = 0;
            calculateAndRender();
        });

        document.getElementById('slider-points').addEventListener('input', function() {
            calculateAndRender();
        });

        document.querySelectorAll('input[name="math-method"]').forEach(radio => {
            radio.addEventListener('change', function() {
                document.getElementById('left-plot-title').textContent = this.value;
                leftSketch.redraw();
            });
        });

        // Initialize
        window.addEventListener('load', function() {
            setupSketches();
            calculateAndRender();
        });
    </script>
</body>
</html>
