                <label for="slider-max-steps">max steps</label>
                <input type="range" id="slider-max-steps" min="1" max="50" step="1" value="9">
                <span id="value-max-steps">+ 9</span>
            </div>

            <div class="control-group">
                <input type="checkbox" id="checkbox-cycle4">
                <label for="checkbox-cycle4">length 4 cycle</label>
            </div>
            <div class="control-group">
                <input type="checkbox" id="checkbox-cycle2">
                <label for="checkbox-cycle2">length 2 cycles</label>
            </div>
            <div class="control-group">
                <input type="checkbox" id="checkbox-cycle1">
                <label for="checkbox-cycle1">length 1 cycle points</label>
            </div>
            <div class="control-group">
                <input type="checkbox" id="checkbox-all-points">
