    <script>
        // Global variables to store the state
        let currentSequence = [];
        let cycleInfo = { found: false, length: 0, step: 0 };
        let canvasSize = 512;
        let fixedPoints = [];

        // Hardcoded cycles
        const length2Cycles = [
            [10922, 21845], [13107, 26214], [13653, 27306], 
            [15339, 30678], [21844, 43691], [26213, 52427], 
            [27305, 54611], [30677, 61355], [43690, 54613]
        ];

        const length4Cycle = [43690, 54612, 32768, 49152];

        // Function to reverse the lowest 15 bits of a number
        function bitReverse15(n) {
            let result = 0;
            for (let i = 0; i < 15; i++) {
                result = (result << 1) | (n & 1);
                n >>= 1;
            }
            return result;
        }

        // Automaton function
        function automatonFunction(n) {
            return (n + bitReverse15(Math.floor(n / 2))) % 65536;
        }

        // Generate sequence and detect cycles
        function generateSequence(initial, maxSteps) {
            const sequence = [initial];
            const seen = new Map();
            seen.set(initial, 0);

            let cycleFound = false;
            let cycleLength = 0;
            let cycleStep = 0;

            for (let i = 1; i <= maxSteps + 1; i++) {
                const next = automatonFunction(sequence[i-1]);
                sequence.push(next);

                if (seen.has(next)) {
                    cycleFound = true;
                    cycleStep = seen.get(next);
                    cycleLength = i - cycleStep;
                    break;
                }

                seen.set(next, i);
            }

            return {
                sequence,
                cycleInfo: {
                    found: cycleFound,
                    length: cycleLength,
                    step: cycleStep
                }
            };
        }

        // Find all fixed points (length 1 cycles)
        function findFixedPoints() {
            const points = [];
            for (let n = 0; n < 65536; n++) {
                if (automatonFunction(n) === n) {
                    points.push(n);
                }
            }
            return points;
        }

        // Convert number to coordinates
        function numberToCoords(n) {
            const x = n & 255;
            const y = (n >> 8) & 255;
            return { x, y };
        }

        // Map logical coordinates to canvas space
        function mapToCanvas(x, y) {
            const scale = canvasSize / 256;
            return {
                x: x * scale,
                y: (255 - y) * scale // Flip y-axis to match standard coordinate system
            };
        }

        // p5.js sketch
        function sketch(p) {
            p.setup = function() {
                const canvas = p.createCanvas(canvasSize, canvasSize);
                canvas.parent('canvas-container');
                p.noLoop();

                // Find all fixed points once
                fixedPoints = findFixedPoints();

                // Initial sequence generation
                const result = generateSequence(462, 9);
                currentSequence = result.sequence;
                cycleInfo = result.cycleInfo;

                updateInfoText();
            };

            p.draw = function() {
                // Background
                p.background(224, 240, 224);

                // Draw all map points if checkbox is checked
                if (document.getElementById('checkbox-all-points').checked) {
                    p.stroke(128, 128, 128);
                    p.strokeWeight(1);

                    for (let n = 0; n < 65536; n++) {
                        const next = automatonFunction(n);
                        const { x, y } = numberToCoords(next);
                        const canvasPos = mapToCanvas(x, y);
                        p.point(canvasPos.x, canvasPos.y);
                    }
                }

                // Draw length 4 cycle if checkbox is checked
                if (document.getElementById('checkbox-cycle4').checked) {
                    p.stroke(0, 128, 0);
                    p.strokeWeight(1);
                    p.noFill();

                    // Draw cycle path
                    const points = length4Cycle.map(n => {
                        const { x, y } = numberToCoords(n);
                        return mapToCanvas(x, y);
                    });

                    // Draw lines connecting the cycle
                    for (let i = 0; i < points.length; i++) {
                        const next = (i + 1) % points.length;
                        p.line(points[i].x, points[i].y, points[next].x, points[next].y);
                    }

                    // Draw bounding box
                    let minX = Infinity, minY = Infinity, maxX = -Infinity, maxY = -Infinity;
                    points.forEach(pos => {
                        minX = p.min(minX, pos.x);
                        minY = p.min(minY, pos.y);
                        maxX = p.max(maxX, pos.x);
                        maxY = p.max(maxY, pos.y);
                    });

                    p.stroke(255, 165, 0);
                    p.rect(minX, minY, maxX - minX, maxY - minY);

                    // Draw perpendicular bisectors (simplified)
                    for (let i = 0; i < points.length; i++) {
                        const next = (i + 1) % points.length;
                        const midX = (points[i].x + points[next].x) / 2;
                        const midY = (points[i].y + points[next].y) / 2;

                        const dx = points[next].x - points[i].x;
                        const dy = points[next].y - points[i].y;

                        // Perpendicular vector
                        const perpX = -dy;
                        const perpY = dx;

                        // Normalize and scale
                        const len = p.sqrt(perpX * perpX + perpY * perpY);
                        const scale = 30 / len;

                        p.stroke(0, 128, 0);
                        p.line(
                            midX - perpX * scale, 
                            midY - perpY * scale,
                            midX + perpX * scale, 
                            midY + perpY * scale
                        );
                    }
                }

                // Draw length 2 cycles if checkbox is checked
                if (document.getElementById('checkbox-cycle2').checked) {
                    p.stroke(0, 128, 0);
                    p.strokeWeight(1);

                    length2Cycles.forEach(pair => {
                        const p1 = numberToCoords(pair[0]);
                        const p2 = numberToCoords(pair[1]);
                        const canvasP1 = mapToCanvas(p1.x, p1.y);
                        const canvasP2 = mapToCanvas(p2.x, p2.y);

                        p.line(canvasP1.x, canvasP1.y, canvasP2.x, canvasP2.y);
                    });
                }

                // Draw length 1 cycle points if checkbox is checked
                if (document.getElementById('checkbox-cycle1').checked) {
                    p.stroke(0, 255, 255);
                    p.strokeWeight(3);

                    fixedPoints.forEach(n => {
                        const { x, y } = numberToCoords(n);
                        const canvasPos = mapToCanvas(x, y);
                        p.point(canvasPos.x, canvasPos.y);
                    });
                }

                // Draw automaton path
                if (currentSequence.length > 1) {
                    // Draw path lines
                    p.stroke(128, 0, 128);
                    p.strokeWeight(2);
                    p.noFill();

                    const path = [];
                    for (let i = 0; i < currentSequence.length; i++) {
                        const { x, y } = numberToCoords(currentSequence[i]);
                        path.push(mapToCanvas(x, y));
                    }

                    p.beginShape();
                    path.forEach(pos => p.vertex(pos.x, pos.y));
                    p.endShape();

                    // Draw start point (red)
                    p.fill(255, 0, 0);
                    p.noStroke();
                    p.ellipse(path[0].x, path[0].y, 8, 8);

                    // Draw end point (blue)
                    p.fill(0, 0, 255);
                    p.ellipse(path[path.length-1].x, path[path.length-1].y, 8, 8);
                }
            };
        }

        // Create p5 instance
        new p5(sketch);

        // Update info text based on current state
        function updateInfoText() {
            const initValue = document.getElementById('slider-initialization').value;
            const maxSteps = document.getElementById('slider-max-steps').value;
            const infoElement = document.getElementById('info-text');

            if (cycleInfo.found) {
                infoElement.textContent = `result of initial condition ${initValue} repeats in ${cycleInfo.length} steps`;
            } else {
                infoElement.textContent = `result of initial condition ${initValue} does not repeat in ${parseInt(maxSteps) + 1} steps`;
            }
        }

        // Event listeners
        document.getElementById('slider-initialization').addEventListener('input', function() {
            const value = this.value;
            document.getElementById('value-initialization').textContent = `+ ${value}`;

            const maxSteps = document.getElementById('slider-max-steps').value;
            const result = generateSequence(parseInt(value), parseInt(maxSteps));
            currentSequence = result.sequence;
            cycleInfo = result.cycleInfo;

            updateInfoText();
            redrawCanvas();
        });

        document.getElementById('slider-max-steps').addEventListener('input', function() {
            const value = this.value;
            document.getElementById('value-max-steps').textContent = `+ ${value}`;

            const initValue = document.getElementById('slider-initialization').value;
            const result = generateSequence(parseInt(initValue), parseInt(value));
            currentSequence = result.sequence;
            cycleInfo = result.cycleInfo;

            updateInfoText();
            redrawCanvas();
        });

        // Checkbox event listeners
        document.getElementById('checkbox-cycle4').addEventListener('change', redrawCanvas);
        document.getElementById('checkbox-cycle2').addEventListener('change', redrawCanvas);
        document.getElementById('checkbox-cycle1').addEventListener('change', redrawCanvas);
        document.getElementById('checkbox-all-points').addEventListener('change', redrawCanvas);
        document.getElementById('checkbox-modify').addEventListener('change', function() {
            // Track state but no visual change needed
        });

        // Helper function to redraw canvas
        function redrawCanvas() {
            const canvas = document.querySelector('#canvas-container canvas');
            if (canvas) {
                const p5instance = canvas._p5;
                if (p5instance) {
                    p5instance.redraw();
                }
            }
        }
    </script>
</body>
</html>
