                el.addEventListener('change', updatePlot);
            });

            // Plot type buttons
            document.querySelectorAll('.plot-type-btn').forEach(btn => {
                btn.addEventListener('click', function() {
                    document.querySelectorAll('.plot-type-btn').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');

                    // Show/hide component selector
                    const componentSelector = document.getElementById('component-selector-container');
                    if (this.id === 'btn-composition') {
                        componentSelector.style.display = 'block';
                    } else {
                        componentSelector.style.display = 'none';
                    }

                    updatePlot();
                });
            });

            // Initial plot
            updatePlot();
        });

        // Main function to update the plot
        function updatePlot() {
            // Get all control values
            const stepType = document.querySelector('input[name="step-type"]:checked').value;
            const stepPercent = parseFloat(document.getElementById('step-percent').value);
            const stage = document.querySelector('input[name="stage"]:checked').value;
            const plotType = document.querySelector('.plot-type-btn.active').id.replace('btn-', '');
            const component = plotType === 'composition' ? document.querySelector('input[name="component"]:checked').value : null;

            // Get simulation parameters
            let params;
            if (plotType === 'composition') {
                params = simulationParams[stepType][plotType][component][stage];
            } else {
                params = simulationParams[stepType][plotType][stage];
            }

            // Generate time and y data
            const timeArray = [];
            const yArray = [];
            for (let t = 0; t <= 20; t += 0.1) {
                timeArray.push(t);

                if (t < 10) {
                    yArray.push(params.initial);
                } else {
                    const y = params.initial + params.Kp * stepPercent * (1 - Math.exp(-(t - 10) / params.tau));
                    yArray.push(y);
                }
            }

            // Determine line color
            let lineColor;
            if (plotType === 'composition') {
                switch(component) {
                    case 'benzene': lineColor = '#FF0000'; break;
                    case 'toluene': lineColor = '#0000FF'; break;
                    case 'pxylene': lineColor = '#008B8B'; break;
                }
            } else {
                lineColor = '#000000';
            }

            // Determine y-axis label
            let yAxisLabel;
            switch(plotType) {
                case 'composition': yAxisLabel = `${component} mole fraction`; break;
                case 'temperature': yAxisLabel = 'temperature (C)'; break;
                case 'molar-hold-up': yAxisLabel = 'molar hold-up (kmol)'; break;
            }

            // Create trace
            const trace = {
                x: timeArray,
                y: yArray,
                type: 'scatter',
                mode: 'lines',
                line: { color: lineColor, width: 2 },
                name: `${stage} stage`
            };

            // Layout
            const layout = {
                xaxis: { title: 'time in minutes', gridcolor: '#CCCCCC', range: [0, 20] },
                yaxis: { title: yAxisLabel, gridcolor: '#CCCCCC' },
                margin: { l: 80, r: 20, t: 20, b: 50 },
                paper_bgcolor: 'rgba(0,0,0,0)',
                plot_bgcolor: 'rgba(0,0,0,0)'
            };

            // Update plot
            Plotly.react('plot-container', [trace], layout);
        }
