        // Create celestial sphere
        function createCelestialSphere() {
            const geometry = new THREE.SphereGeometry(SPHERE_RADIUS, 64, 64);

            // Create vertex colors for gradient effect
            const positions = geometry.attributes.position;
            const colors = [];

            for (let i = 0; i < positions.count; i++) {
                const y = positions.getY(i) / SPHERE_RADIUS; // Normalized y coordinate

                // Create gradient from orange (near ecliptic) to blue (near poles)
                const t = Math.abs(y);
                const r = 0.3 + 0.7 * (1 - t);
                const g = 0.4 * (1 - t);
                const b = 0.5 + 0.5 * t;

                colors.push(r, g, b);
            }

            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));

            const material = new THREE.MeshBasicMaterial({
                vertexColors: true,
                transparent: true,
                opacity: 0.4,
                side: THREE.DoubleSide
            });

            scene.sphere = new THREE.Mesh(geometry, material);
            scene.add(scene.sphere);
        }

        // Create celestial equator and ecliptic
        function createEquatorAndEcliptic() {
            // Celestial equator (blue)
            const equatorGeometry = new THREE.BufferGeometry();
            const equatorPoints = [];
            const segments = 100;

            for (let i = 0; i <= segments; i++) {
                const theta = (i / segments) * Math.PI * 2;
                equatorPoints.push(new THREE.Vector3(
                    SPHERE_RADIUS * Math.cos(theta),
                    0,
                    SPHERE_RADIUS * Math.sin(theta)
                ));
            }

            equatorGeometry.setFromPoints(equatorPoints);
            const equatorMaterial = new THREE.LineBasicMaterial({ color: 0x0000ff });
            scene.equator = new THREE.LineLoop(equatorGeometry, equatorMaterial);
            scene.add(scene.equator);

            // Ecliptic (reddish-purple)
            const eclipticGeometry = new THREE.BufferGeometry();
            const eclipticPoints = [];

            for (let i = 0; i <= segments; i++) {
                const theta = (i / segments) * Math.PI * 2;
                eclipticPoints.push(new THREE.Vector3(
                    SPHERE_RADIUS * Math.cos(theta),
                    SPHERE_RADIUS * Math.sin(theta) * Math.sin(ECLIPTIC_OBLIQUITY),
                    SPHERE_RADIUS * Math.sin(theta) * Math.cos(ECLIPTIC_OBLIQUITY)
                ));
            }

            eclipticGeometry.setFromPoints(eclipticPoints);
            const eclipticMaterial = new THREE.LineBasicMaterial({ color: 0xaa00aa });
            scene.ecliptic = new THREE.LineLoop(eclipticGeometry, eclipticMaterial);
            scene.add(scene.ecliptic);
        }

        // Create stars (simplified - in a real app you'd load actual star data)
        function createStars() {
            const starCount = 1000;
            const positions = new Float32Array(starCount * 3);
            const sizes = new Float32Array(starCount);

            // Random star positions (in a real app, use actual star catalog data)
            for (let i = 0; i < starCount; i++) {
                // Random spherical coordinates
                const ra = Math.random() * Math.PI * 2;
                const dec = (Math.random() - 0.5) * Math.PI;

                // Convert to Cartesian coordinates
                positions[i * 3] = SPHERE_RADIUS * Math.cos(dec) * Math.cos(ra);
                positions[i * 3 + 1] = SPHERE_RADIUS * Math.sin(dec);
                positions[i * 3 + 2] = SPHERE_RADIUS * Math.cos(dec) * Math.sin(ra);

                // Random size
                sizes[i] = 0.05 + Math.random() * 0.1;
            }

            const geometry = new THREE.BufferGeometry();
            geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
            geometry.setAttribute('size', new THREE.BufferAttribute(sizes, 1));

            const material = new THREE.PointsMaterial({
                color: 0xffffff,
                size: 0.1,
                sizeAttenuation: true
            });

            scene.stars = new THREE.Points(geometry, material);
            scene.add(scene.stars);

            // Placeholder for constellation lines (would be populated based on actual data)
