                    constellationSelect.value = 'None';
                } else if (family === 'Zodiac') {
                    // Add zodiac constellations
                    constellationSelect.add(new Option('Aries', 'Aries'));
                    constellationSelect.add(new Option('Taurus', 'Taurus'));
                    constellationSelect.add(new Option('Gemini', 'Gemini'));
                    constellationSelect.add(new Option('Cancer', 'Cancer'));
                    constellationSelect.add(new Option('Leo', 'Leo'));
                    constellationSelect.add(new Option('Virgo', 'Virgo'));
                    constellationSelect.add(new Option('Libra', 'Libra'));
                    constellationSelect.add(new Option('Scorpius', 'Scorpius'));
                    constellationSelect.add(new Option('Sagittarius', 'Sagittarius'));
                    constellationSelect.add(new Option('Capricornus', 'Capricornus'));
                    constellationSelect.add(new Option('Aquarius', 'Aquarius'));
                    constellationSelect.add(new Option('Pisces', 'Pisces'));
                    constellationSelect.add(new Option('Cygnus', 'Cygnus'));

                    // Set to first option (after "None")
                    constellationSelect.selectedIndex = 1;
                } else {
                    // For other families, just add some sample constellations
                    constellationSelect.add(new Option('Ursa Major', 'Ursa Major'));
                    constellationSelect.add(new Option('Orion', 'Orion'));
                    constellationSelect.add(new Option('Perseus', 'Perseus'));

                    // Set to first option (after "None")
                    constellationSelect.selectedIndex = 1;
                }

                // Trigger constellation selection change
                constellationSelect.dispatchEvent(new Event('change'));
            });

            // Constellation dropdown
            document.getElementById('select-constellation').addEventListener('change', function() {
                const constellation = this.value;

                // Clear existing constellation lines and highlighted stars
                scene.constellationLines.clear();
                scene.highlightedStars.geometry.dispose();

                if (constellation !== 'None') {
                    // In a real app, you would draw the actual constellation lines here
                    // For this demo, we'll just draw a simple shape

                    // Create some random points around the sphere
                    const points = [];
                    const lineCount = 5;

                    for (let i = 0; i < lineCount; i++) {
                        const ra1 = Math.random() * Math.PI * 2;
                        const dec1 = (Math.random() - 0.5) * Math.PI;
                        const ra2 = ra1 + (Math.random() - 0.5) * Math.PI / 2;
                        const dec2 = dec1 + (Math.random() - 0.5) * Math.PI / 2;

                        const x1 = SPHERE_RADIUS * Math.cos(dec1) * Math.cos(ra1);
                        const y1 = SPHERE_RADIUS * Math.sin(dec1);
                        const z1 = SPHERE_RADIUS * Math.cos(dec1) * Math.sin(ra1);

                        const x2 = SPHERE_RADIUS * Math.cos(dec2) * Math.cos(ra2);
                        const y2 = SPHERE_RADIUS * Math.sin(dec2);
                        const z2 = SPHERE_RADIUS * Math.cos(dec2) * Math.sin(ra2);

                        points.push(new THREE.Vector3(x1, y1, z1), new THREE.Vector3(x2, y2, z2));
                    }

                    // Create line segments
                    const lineGeometry = new THREE.BufferGeometry().setFromPoints(points);
                    const lineMaterial = new THREE.LineBasicMaterial({ color: 0xff0000 });
                    const lines = new THREE.LineSegments(lineGeometry, lineMaterial);
                    scene.constellationLines.add(lines);

                    // Highlight some stars (random selection for demo)
                    const highlightPositions = [];
                    const highlightCount = 10;

                    for (let i = 0; i < highlightCount; i++) {
                        const ra = Math.random() * Math.PI * 2;
                        const dec = (Math.random() - 0.5) * Math.PI;

                        highlightPositions.push(
                            SPHERE_RADIUS * Math.cos(dec) * Math.cos(ra),
                            SPHERE_RADIUS * Math.sin(dec),
                            SPHERE_RADIUS * Math.cos(dec) * Math.sin(ra)
                        );
                    }

                    const highlightGeometry = new THREE.BufferGeometry();
                    highlightGeometry.setAttribute('position', new THREE.Float32BufferAttribute(highlightPositions, 3));
                    scene.highlightedStars.geometry = highlightGeometry;
                }
            });

            // Checkbox event listeners
            document.getElementById('check-sphere').addEventListener('change', function() {
                scene.sphere.visible = this.checked;
            });

            document.getElementById('check-stars').addEventListener('change', function() {
                scene.stars.visible = this.checked;
                scene.highlightedStars.visible = this.checked && 
                    document.getElementById('select-constellation').value !== 'None';
            });

            document.getElementById('check-equator').addEventListener('change', function() {
                scene.equator.visible = this.checked;
            });

            document.getElementById('check-ecliptic').addEventListener('change', function() {
                scene.ecliptic.visible = this.checked;
            });

            document.getElementById('check-zenith').addEventListener('change', function() {
                scene.zenith.visible = this.checked;
            });

            document.getElementById('check-aries').addEventListener('change', function() {
                scene.ariesPoint.visible = this.checked;
            });

            // Slider event listeners
            document.getElementById('slider-ra').addEventListener('input', function() {
                const ra = parseFloat(this.value);
                const dec = parseFloat(document.getElementById('slider-dec').value);
                updateZenithPosition(ra, dec);
            });

            document.getElementById('slider-dec').addEventListener('input', function() {
                const ra = parseFloat(document.getElementById('slider-ra').value);
                const dec = parseFloat(this.value);
                updateZenithPosition(ra, dec);
            });

            // Window resize
            window.addEventListener('resize', function() {
                const width = window.innerWidth - 300;
                const height = window.innerHeight;

                camera.aspect = width / height;
                camera.updateProjectionMatrix();

                renderer.setSize(width, height);
                labelRenderer.setSize(width, height);
            });
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
            labelRenderer.render(scene, camera);
        }

        // Initialize the visualization
        init();
    </script>
</body>
</html>
