    <script>
        // Global variables
        let normalizedMag = [];
        let isDBScale = false;

        // Initialize on DOM load
        document.addEventListener('DOMContentLoaded', function() {
            // Set up event listeners
            document.getElementById('slider-c1').addEventListener('input', updateSimulation);
            document.getElementById('slider-c2').addEventListener('input', updateSimulation);
            document.getElementById('reset-c1').addEventListener('click', function() {
                document.getElementById('slider-c1').value = 0;
                document.getElementById('c1-value-display').textContent = '0';
                updateSimulation();
            });
            document.getElementById('reset-c2').addEventListener('click', function() {
                document.getElementById('slider-c2').value = 0;
                document.getElementById('c2-value-display').textContent = '0';
                updateSimulation();
            });

            document.getElementById('checkbox-clipping').addEventListener('change', function() {
                const levelGroup = document.getElementById('level-control-group');
                levelGroup.style.display = this.checked ? 'block' : 'none';
                updateSimulation();
            });

            document.getElementById('slider-level').addEventListener('input', updateSimulation);

            document.getElementById('btn-linear').addEventListener('click', function() {
                if (!this.classList.contains('active')) {
                    this.classList.add('active');
                    document.getElementById('btn-decibel').classList.remove('active');
                    isDBScale = false;
                    updateSpectrumScale();
                }
            });

            document.getElementById('btn-decibel').addEventListener('click', function() {
                if (!this.classList.contains('active')) {
                    this.classList.add('active');
                    document.getElementById('btn-linear').classList.remove('active');
                    isDBScale = true;
                    updateSpectrumScale();
                }
            });

            // Initialize plots
            initializePlots();
            updateSimulation();
        });

        function initializePlots() {
            // Initialize signal plot
            Plotly.newPlot('plot-signal', [{
                x: [],
                y: [],
                type: 'scatter',
                mode: 'lines'
            }], {
                title: 'signal',
                xaxis: { title: 'time[s]', range: [0, 0.005] },
                yaxis: { title: '', range: [-2, 2] },
                margin: { l: 30, r: 20, b: 40, t: 40 }
            });

            // Initialize spectrum plot
            Plotly.newPlot('plot-spectrum', [{
                x: [],
                y: [],
                type: 'bar',
                marker: { width: 5 }
            }], {
                title: 'magnitude spectrum (DFT)',
                xaxis: { title: 'frequency[Hz]', range: [0, 8000] },
                yaxis: { title: '' },
                margin: { l: 30, r: 20, b: 40, t: 40 }
            });
        }

        function updateSimulation() {
            // Read control values
            const c1 = parseFloat(document.getElementById('slider-c1').value);
            const c2 = parseFloat(document.getElementById('slider-c2').value);
            const isClipping = document.getElementById('checkbox-clipping').checked;
            const clipLevel = parseFloat(document.getElementById('slider-level').value);

            // Update value displays
            document.getElementById('c1-value-display').textContent = c1.toFixed(1);
            document.getElementById('c2-value-display').textContent = c2.toFixed(1);
            document.getElementById('level-value-display').textContent = clipLevel.toFixed(2);

            // Define constants
            const f = 1000; // Hz
            const A = 1;
            const N = 2048;
            const fs = 32000; // Hz

            // Generate time vector
            const t = [];
            for (let i = 0; i < N; i++) {
                t.push(i / fs);
            }

            // Generate input signal
            const x = t.map(ti => A * Math.sin(2 * Math.PI * f * ti));

            // Calculate output signal with nonlinear terms
            const y = x.map(xi => xi + c1 * Math.pow(xi, 2) + c2 * Math.pow(xi, 3));

            // Apply clipping if enabled
            if (isClipping) {
                for (let i = 0; i < y.length; i++) {
                    y[i] = Math.max(-clipLevel, Math.min(clipLevel, y[i]));
                }
            }

            // Update signal plot (only show first part)
            const displaySamples = Math.min(160, N); // Show about 0.005s at fs=32000
            Plotly.react('plot-signal', [{
                x: t.slice(0, displaySamples),
                y: y.slice(0, displaySamples),
                type: 'scatter',
                mode: 'lines'
            }], {
                title: 'signal',
                xaxis: { title: 'time[s]', range: [0, 0.005] },
                yaxis: { title: '', range: [-2, 2] },
                margin: { l: 30, r: 20, b: 40, t: 40 }
            });

            // Perform FFT
            const fftOut = fft(y, new Array(N).fill(0));

            // Calculate magnitude spectrum
            const mag = [];
            for (let k = 0; k < N/2; k++) {
                mag.push(Math.sqrt(Math.pow(fftOut.real[k], 2) + Math.pow(fftOut.imag[k], 2)));
            }

            // Normalize spectrum
            normalizedMag = [];
            normalizedMag.push(mag[0] / N); // DC component
            for (let k = 1; k < mag.length; k++) {
                normalizedMag.push(2 * mag[k] / N);
            }

            // Update spectrum plot
            updateSpectrumScale();
        }

        function updateSpectrumScale() {
            if (normalizedMag.length === 0) return;

            // Create frequency vector
            const N = 2048;
            const fs = 32000;
            const freq = [];
            for (let k = 0; k < N/2; k++) {
                freq.push(k * fs / N);
            }

            // Process y-data based on scale
            let yData, yRange;
            if (isDBScale) {
                // Convert to dB with floor at -100 dB
                yData = normalizedMag.map(mag => {
                    const db = 20 * Math.log10(Math.max(mag, 1e-5));
                    return Math.max(db, -100);
                });
                yRange = [-100, 0];
            } else {
                yData = normalizedMag;
                yRange = [0, 1.1];
            }

            // Update spectrum plot
            Plotly.react('plot-spectrum', [{
                x: freq,
                y: yData,
                type: 'bar',
                marker: { width: 5 }
            }], {
                title: 'magnitude spectrum (DFT)',
                xaxis: { title: 'frequency[Hz]', range: [0, 8000] },
                yaxis: { title: '', range: yRange },
                margin: { l: 30, r: 20, b: 40, t: 40 }
            });
        }

        // Radix-2 FFT implementation
        function fft(real, imag) {
            const N = real.length;

            // Bit-reversal permutation
            let j = 0;
            for (let i = 0; i < N; i++) {
                if (j > i) {
                    [real[i], real[j]] = [real[j], real[i]];
                    [imag[i], imag[j]] = [imag[j], imag[i]];
                }

                let m = N >> 1;
                while (m >= 1 && j >= m) {
                    j -= m;
                    m >>= 1;
                }
                j += m;
            }

            // Cooley-Tukey FFT
            for (let L = 2; L <= N; L <<= 1) {
                const angle = -2 * Math.PI / L;
                const wL_real = Math.cos(angle);
                const wL_imag = Math.sin(angle);

                for (let k = 0; k < N; k += L) {
                    let w_real = 1;
                    let w_imag = 0;

                    for (let j = 0; j < L/2; j++) {
                        const t_real = w_real * real[k + j + L/2] - w_imag * imag[k + j + L/2];
                        const t_imag = w_real * imag[k + j + L/2] + w_imag * real[k + j + L/2];

                        const u_real = real[k + j];
                        const u_imag = imag[k + j];

                        real[k + j] = u_real + t_real;
                        imag[k + j] = u_imag + t_imag;
                        real[k + j + L/2] = u_real - t_real;
                        imag[k + j + L/2] = u_imag - t_imag;

                        const next_w_real = w_real * wL_real - w_imag * wL_imag;
                        const next_w_imag = w_real * wL_imag + w_imag * wL_real;
                        w_real = next_w_real;
                        w_imag = next_w_imag;
                    }
                }
            }

            return { real, imag };
        }
    </script>
