            let beamWidth = 1.25;
            let incidentAngle = -45;
            let refractionRatio = 2.0;
            let showInjected = true;
            let showRefracted = true;
            let showReflected = true;
            let showEjected = true;
            let showNormals = false;
            let showIntersections = false;
            let showBeamTop = true;
            let showBeamBottom = true;

            p.setup = function() {
                let canvas = p.createCanvas(p.select('#canvas-container').width, p.windowHeight * 0.8);
                canvas.parent('canvas-container');
                p.angleMode(p.DEGREES);

                // Setup event listeners for sliders
                p.select('#slider-ray-count').input(function() {
                    rayCount = parseInt(this.value());
                    p.select('#value-ray-count').html(rayCount);
                });

