                    beamWidth = parseFloat(this.value());
                    p.select('#value-beam-width').html(beamWidth.toFixed(2));
                });

                p.select('#slider-incident-angle').input(function() {
                    incidentAngle = parseInt(this.value());
                    p.select('#value-incident-angle').html(incidentAngle);
                });

                p.select('#slider-refraction-ratio').input(function() {
                    refractionRatio = parseFloat(this.value());
                    p.select('#value-refraction-ratio').html(refractionRatio.toFixed(2));
                });

                // Setup event listeners for checkboxes
                p.select('#checkbox-injected').changed(function() {
                    showInjected = this.checked();
                });

                p.select('#checkbox-refracted').changed(function() {
                    showRefracted = this.checked();
                });

                p.select('#checkbox-reflected').changed(function() {
                    showReflected = this.checked();
                });

                p.select('#checkbox-ejected').changed(function() {
                    showEjected = this.checked();
                });

                p.select('#checkbox-normals').changed(function() {
                    showNormals = this.checked();
                });

                p.select('#checkbox-intersections').changed(function() {
                    showIntersections = this.checked();
                });

                p.select('#checkbox-beam-top').changed(function() {
                    showBeamTop = this.checked();
                });

                p.select('#checkbox-beam-bottom').changed(function() {
                    showBeamBottom = this.checked();
                });

                p.windowResized = function() {
                    p.resizeCanvas(p.select('#canvas-container').width, p.windowHeight * 0.8);
                };
            };

            p.draw = function() {
                p.background(255);
                p.translate(p.width/2, p.height/2);

                // Scale to fit -2.5 to 2.5 in both axes
                const scaleFactor = p.min(p.width, p.height) / 5;
                p.scale(scaleFactor, -scaleFactor);

                // Draw axes
                p.stroke(200);
                p.strokeWeight(1/scaleFactor);
                p.line(-2.5, 0, 2.5, 0); // x-axis
                p.line(0, -2.5, 0, 2.5); // y-axis

                // Draw axis ticks and labels
                for (let i = -2; i <= 2; i++) {
                    if (i === 0) continue;
                    p.line(i, -0.05, i, 0.05);
                    p.line(-0.05, i, 0.05, i);

                    p.push();
                    p.scale(1, -1);
                    p.textSize(12/scaleFactor);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.noStroke();
                    p.fill(100);
                    p.text(i, i, -0.1);
                    p.text(i, 0.1, -i);
                    p.pop();
                }

                // Draw sphere
                p.stroke(0);
                p.strokeWeight(1/scaleFactor);
                p.noFill();
                p.circle(0, 0, 2);

                // Calculate ray directions
                const angleRad = p.radians(incidentAngle);
                const centralDir = p.createVector(p.cos(angleRad), p.sin(angleRad));
                const perpendicularDir = p.createVector(-centralDir.y, centralDir.x);

                // Calculate ray spacing
                const spacing = beamWidth / (rayCount - 1);

                // Draw rays
                for (let i = 0; i < rayCount; i++) {
                    // Skip if this ray is in the hidden half
                    const isTopHalf = i < rayCount / 2;
                    if ((isTopHalf && !showBeamTop) || (!isTopHalf && !showBeamBottom)) {
                        continue;
                    }

                    // Calculate ray offset from center
                    const offset = (i - (rayCount - 1)/2) * spacing;

                    // Calculate starting position (outside sphere)
                    const startPos = p.createVector(
                        -2.5 * centralDir.x + offset * perpendicularDir.x,
                        -2.5 * centralDir.y + offset * perpendicularDir.y
                    );

                    // Trace the ray through the sphere
                    traceRay(startPos, centralDir, scaleFactor);
                }
            };

            function traceRay(startPos, direction, scaleFactor) {
                // First intersection with sphere (injection)
                const firstIntersection = intersectLineCircle(startPos, direction);
                if (!firstIntersection) return;

                if (showInjected) {
                    p.stroke(255, 0, 0); // red
                    p.strokeWeight(1/scaleFactor);
                    p.line(startPos.x, startPos.y, firstIntersection.x, firstIntersection.y);
                }

                if (showIntersections) {
                    p.fill(255, 0, 0);
                    p.noStroke();
                    p.circle(firstIntersection.x, firstIntersection.y, 5/scaleFactor);
                }

                // Calculate normal at first intersection
                const firstNormal = p5.Vector.normalize(firstIntersection);

                if (showNormals) {
                    p.stroke(150);
                    p.strokeWeight(1/scaleFactor);
                    p.drawingContext.setLineDash([3/scaleFactor, 3/scaleFactor]);
                    p.line(
                        firstIntersection.x, firstIntersection.y,
                        firstIntersection.x + firstNormal.x * 0.3,
                        firstIntersection.y + firstNormal.y * 0.3
                    );
                    p.drawingContext.setLineDash([]);
                }

                // Refraction at first surface (in -> refracted)
                const refractedDir = refract(direction, firstNormal, 1.0, refractionRatio);
                if (!refractedDir) return; // total internal reflection

                // Second intersection with sphere (internal)
                const secondIntersection = intersectLineCircle(firstIntersection, refractedDir, true);
                if (!secondIntersection) return;

                if (showRefracted) {
                    p.stroke(0, 255, 0); // green
                    p.strokeWeight(1/scaleFactor);
                    p.line(firstIntersection.x, firstIntersection.y, secondIntersection.x, secondIntersection.y);
                }

                if (showIntersections) {
                    p.fill(0, 255, 0);
                    p.noStroke();
                    p.circle(secondIntersection.x, secondIntersection.y, 5/scaleFactor);
                }

                // Calculate normal at second intersection
                const secondNormal = p5.Vector.normalize(secondIntersection);

                if (showNormals) {
                    p.stroke(150);
                    p.strokeWeight(1/scaleFactor);
                    p.drawingContext.setLineDash([3/scaleFactor, 3/scaleFactor]);
                    p.line(
                        secondIntersection.x, secondIntersection.y,
                        secondIntersection.x + secondNormal.x * 0.3,
                        secondIntersection.y + secondNormal.y * 0.3
                    );
                    p.drawingContext.setLineDash([]);
                }

                // Reflection at second surface (refracted -> reflected)
                const reflectedDir = reflect(refractedDir, secondNormal);

                // Third intersection with sphere (exit)
                const thirdIntersection = intersectLineCircle(secondIntersection, reflectedDir, true);
                if (!thirdIntersection) return;

                if (showReflected) {
                    p.stroke(0, 0, 255); // blue
                    p.strokeWeight(1/scaleFactor);
                    p.line(secondIntersection.x, secondIntersection.y, thirdIntersection.x, thirdIntersection.y);
                }

                if (showIntersections) {
                    p.fill(0, 0, 255);
                    p.noStroke();
                    p.circle(thirdIntersection.x, thirdIntersection.y, 5/scaleFactor);
                }

                // Calculate normal at third intersection
                const thirdNormal = p5.Vector.normalize(thirdIntersection);

                if (showNormals) {
                    p.stroke(150);
                    p.strokeWeight(1/scaleFactor);
                    p.drawingContext.setLineDash([3/scaleFactor, 3/scaleFactor]);
                    p.line(
                        thirdIntersection.x, thirdIntersection.y,
                        thirdIntersection.x + thirdNormal.x * 0.3,
                        thirdIntersection.y + thirdNormal.y * 0.3
                    );
                    p.drawingContext.setLineDash([]);
                }

                // Refraction at third surface (reflected -> ejected)
                const ejectedDir = refract(reflectedDir, thirdNormal, refractionRatio, 1.0);
                if (!ejectedDir) return; // total internal reflection (shouldn't happen here)

                // Calculate exit point (far side of canvas)
                const exitPos = p.createVector(
                    thirdIntersection.x + ejectedDir.x * 2.5,
                    thirdIntersection.y + ejectedDir.y * 2.5
                );

                if (showEjected) {
                    p.stroke(0); // black
                    p.strokeWeight(1/scaleFactor);
                    p.line(thirdIntersection.x, thirdIntersection.y, exitPos.x, exitPos.y);
                }
            }

            function intersectLineCircle(point, direction, fromInside = false) {
                // Line-circle intersection: (p + t*d - c)² = r²
                // Where c is center (0,0), r is radius (1)
                // Solve quadratic equation: a*t² + b*t + c = 0
                const a = direction.x * direction.x + direction.y * direction.y;
                const b = 2 * (point.x * direction.x + point.y * direction.y);
                const c = point.x * point.x + point.y * point.y - 1;

                const discriminant = b * b - 4 * a * c;
                if (discriminant < 0) return null;

                const sqrtDiscriminant = p.sqrt(discriminant);
                const t1 = (-b - sqrtDiscriminant) / (2 * a);
                const t2 = (-b + sqrtDiscriminant) / (2 * a);

                // Choose the appropriate intersection point based on direction
                let t;
                if (fromInside) {
                    // We're inside the circle, so we want the positive solution
                    t = t1 > 0 ? t1 : t2;
                } else {
                    // We're outside the circle, so we want the first intersection
                    t = t1 > 0 ? t1 : (t2 > 0 ? t2 : null);
                }

                if (t === null || t <= 0) return null;

                return p.createVector(
                    point.x + t * direction.x,
                    point.y + t * direction.y
                );
            }

            function reflect(incident, normal) {
                // r = i - 2(i·n)n
                const dot = incident.x * normal.x + incident.y * normal.y;
                return p.createVector(
                    incident.x - 2 * dot * normal.x,
                    incident.y - 2 * dot * normal.y
                ).normalize();
            }

            function refract(incident, normal, n1, n2) {
                // Snell's law: n1 sinθ1 = n2 sinθ2
                const n = n1 / n2;
                const iDotN = incident.x * normal.x + incident.y * normal.y;
                const sin2t = n * n * (1 - iDotN * iDotN);

                if (sin2t > 1) {
                    // Total internal reflection
                    return null;
                }

                const cosT = p.sqrt(1 - sin2t);
                const term = n * iDotN - cosT;

                return p.createVector(
                    n * incident.x - term * normal.x,
                    n * incident.y - term * normal.y
                ).normalize();
            }
        };

        new p5(sketch);
    </script>
</body>
</html>
```

This HTML document includes:

1. A responsive layout with a control panel on the left and visualization canvas on the right
2. All specified sliders with their correct ranges, default values, and value displays
3. All specified checkboxes with their default states
4. A p5.js canvas that renders:
   - Coordinate axes with labels
   - A unit circle representing the sphere
   - Ray tracing visualization that responds to all control inputs
5. Proper implementation of:
   - Ray generation based on beam parameters
   - Intersection calculations
   - Reflection and refraction using Snell's Law
   - Visual toggles for different ray types and elements

The visualization updates in real-time as you adjust any of the controls. The code is self-contained and should work when opened directly in a web browser.

Here's the complete HTML document implementing the specified visualization:

