    </style>
</head>
<body>
    <div id="controls-container">
        <div class="control-group">
            <span>size of bipartite graph</span>
            <div id="size-controls" class="button-group">
                <button id="size-6">6</button>
                <button id="size-10">10</button>
                <button id="size-14">14</button>
                <button id="size-18" class="active">18</button>
                <button id="size-22">22</button>
                <button id="size-26">26</button>
                <button id="size-30">30</button>
            </div>
        </div>

        <div class="control-group">
            <span>maximum degree of lower part</span>
            <div id="degree-controls" class="button-group">
                <button id="degree-2">2</button>
                <button id="degree-3" class="active">3</button>
                <button id="degree-4">4</button>
                <button id="degree-5">5</button>
                <button id="degree-6">6</button>
            </div>
        </div>

        <div class="control-group">
            <span>algorithm step</span>
            <div id="step-controls" class="button-group">
                <button id="step-1" class="active">1</button>
            </div>
        </div>

        <button id="reset-button" title="Generate new graph">(+)</button>
    </div>

    <div id="visualization-container">
        <p id="status-text">Initializing...</p>
        <div id="canvas-wrapper">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script>
        // Global state
        let currentStep = 1;
        let history = [];
        let currentSize = 18;
        let currentDegree = 3;
        let p5Instance;

        // DOM elements
        const sizeControls = document.getElementById('size-controls');
        const degreeControls = document.getElementById('degree-controls');
        const stepControls = document.getElementById('step-controls');
        const resetButton = document.getElementById('reset-button');
        const statusText = document.getElementById('status-text');

        // Initialize p5.js sketch
        function initP5() {
            const sketch = (p) => {
                p.setup = () => {
                    const canvas = p.createCanvas(800, 500);
                    canvas.parent('canvas-wrapper');
                    canvas.elt.id = 'main-canvas';
                    p.noLoop();
                    generateGraphAndHistory();
                };

                p.draw = () => {
                    p.background(255);

                    if (history.length === 0 || currentStep < 1 || currentStep > history.length) {
                        return;
                    }

                    const currentState = history[currentStep - 1];
                    statusText.textContent = currentState.statusText;

                    // Draw the bipartite graph on the right side
                    drawBipartiteGraph(p, currentState);

                    // Draw the augmenting path tree on the left side
                    drawAugmentingPathTree(p, currentState);
                };
            };

            p5Instance = new p5(sketch);
        }

        // Generate a random bipartite graph and compute algorithm steps
        function generateGraphAndHistory() {
            // Reset history
            history = [];
            currentStep = 1;

            // Generate a random bipartite graph
            const graph = generateRandomBipartiteGraph(currentSize, currentDegree);

            // Simulate the Hungarian algorithm steps
            simulateAlgorithmSteps(graph);

            // Update step controls
            updateStepControls();

            // Redraw
            if (p5Instance) p5Instance.redraw();
        }

        // Generate a random bipartite graph
        function generateRandomBipartiteGraph(size, maxDegree) {
            // Simple implementation - in a real app this would be more sophisticated
            const n = Math.floor(size / 2);
            const graph = {
                U: Array.from({length: n}, (_, i) => i + 1),
                V: Array.from({length: n}, (_, i) => i + n + 1),
                edges: []
            };

            // Connect each vertex in V to 1 to maxDegree vertices in U
            for (const v of graph.V) {
                const degree = Math.floor(Math.random() * maxDegree) + 1;
                const shuffledU = [...graph.U].sort(() => Math.random() - 0.5);

                for (let i = 0; i < Math.min(degree, n); i++) {
                    graph.edges.push({u: shuffledU[i], v: v});
                }
            }

            return graph;
        }

        // Simulate algorithm steps and populate history
        function simulateAlgorithmSteps(graph) {
            // This is a simplified simulation - a real implementation would actually
            // perform the Hungarian algorithm and capture each step

            // Initial state
            history.push({
                matching: [],
                cover: [],
                bfsTree: {},
                highlightedPath: [],
                statusText: `Initial state. Matching: 0 edges, Cover: 0 vertices`
            });

            // Simulate some steps
            const steps = Math.min(10, graph.U.length * 2);

            for (let i = 1; i <= steps; i++) {
                // Randomly add some edges to matching
                const matching = [...history[i-1].matching];
                const cover = [...history[i-1].cover];
                const bfsTree = {...history[i-1].bfsTree};
                const highlightedPath = [...history[i-1].highlightedPath];

                if (i % 2 === 0 && graph.edges.length > 0) {
                    const randomEdge = graph.edges[Math.floor(Math.random() * graph.edges.length)];
                    if (!matching.some(e => e.u === randomEdge.u || e.v === randomEdge.v)) {
                        matching.push(randomEdge);
                    }
                }

                if (i % 3 === 0 && graph.U.length > 0) {
                    const randomU = graph.U[Math.floor(Math.random() * graph.U.length)];
                    if (!cover.includes(randomU)) {
                        cover.push(randomU);
                    }
                }

                // Simulate BFS tree growth
                if (i % 2 === 1 && graph.U.length > 0) {
                    const root = graph.U[Math.floor(Math.random() * graph.U.length)];
                    bfsTree[root] = {
                        children: graph.edges
                            .filter(e => e.u === root)
                            .map(e => e.v)
                            .slice(0, 3)
                    };

                    highlightedPath.length = 0;
                    if (bfsTree[root].children.length > 0) {
                        highlightedPath.push({u: root, v: bfsTree[root].children[0]});
                    }
                }

                history.push({
                    matching,
                    cover,
                    bfsTree,
                    highlightedPath,
                    statusText: `Step ${i}. Matching: ${matching.length} edges, Cover: ${cover.length} vertices`
                });
            }
        }

        // Update step controls based on history length
        function updateStepControls() {
            stepControls.innerHTML = '';

            for (let i = 1; i <= history.length; i++) {
                const button = document.createElement('button');
                button.id = `step-${i}`;
                button.textContent = i.toString();
                if (i === currentStep) button.classList.add('active');

                button.addEventListener('click', () => {
                    currentStep = i;
                    document.querySelectorAll('#step-controls button').forEach(b => b.classList.remove('active'));
                    button.classList.add('active');
                    if (p5Instance) p5Instance.redraw();
                });

                stepControls.appendChild(button);
            }
        }

        // Draw the bipartite graph
        function drawBipartiteGraph(p, state) {
            const canvasWidth = p.width;
            const canvasHeight = p.height;
            const graphWidth = canvasWidth * 0.6;
            const graphX = canvasWidth - graphWidth - 20;

            // Draw title
            p.fill(0);
            p.textSize(16);
            p.textAlign(p.CENTER);
            p.text("Bipartite Graph", graphX + graphWidth/2, 30);

            // Calculate vertex positions
            const n = state.matching.length > 0 ? 
                Math.max(...state.matching.flatMap(e => [e.u, e.v])) : 10;
            const uCount = Math.ceil(n / 2);
            const vCount = Math.floor(n / 2);

            const uY = canvasHeight * 0.3;
            const vY = canvasHeight * 0.7;
            const uSpacing = graphWidth / (uCount + 1);
            const vSpacing = graphWidth / (vCount + 1);

            // Draw edges first (so they're behind vertices)
            p.stroke(200);
            p.strokeWeight(1);
            for (const edge of state.matching) {
                const uX = graphX + (edge.u % uCount) * uSpacing + uSpacing;
                const vX = graphX + (edge.v % vCount) * vSpacing + vSpacing;

                // Highlighted path edges
                if (state.highlightedPath.some(e => e.u === edge.u && e.v === edge.v)) {
                    p.stroke(255, 0, 0);
                    p.strokeWeight(3);
                    p.line(uX, uY, vX, vY);
                    p.stroke(200);
                    p.strokeWeight(1);
                } else {
                    p.line(uX, uY, vX, vY);
                }
            }

            // Draw matching edges on top
            p.stroke(160, 160, 255);
            p.strokeWeight(4);
            for (const edge of state.matching) {
                const uX = graphX + (edge.u % uCount) * uSpacing + uSpacing;
                const vX = graphX + (edge.v % vCount) * vSpacing + vSpacing;
                p.line(uX, uY, vX, vY);
            }

            // Draw U vertices (top row)
            p.textAlign(p.CENTER, p.CENTER);
            p.textSize(12);
            for (let i = 1; i <= uCount; i++) {
                const x = graphX + i * uSpacing;

                // Vertex color based on state
                if (state.cover.includes(i)) {
                    p.fill(0, 192, 0); // Green for cover
                } else if (Object.keys(state.bfsTree).includes(i.toString())) {
                    p.fill(255, 0, 0); // Red for BFS root
                } else {
                    p.fill(240); // Default gray
                }

                p.stroke(0);
                p.ellipse(x, uY, 30, 30);
                p.fill(0);
                p.noStroke();
                p.text(i.toString(), x, uY);
            }

            // Draw V vertices (bottom row)
            for (let i = 1; i <= vCount; i++) {
                const x = graphX + i * vSpacing;
                const vId = i + uCount;

                // Vertex color based on state
                if (state.cover.includes(vId)) {
                    p.fill(0, 192, 0); // Green for cover
                } else {
                    p.fill(240); // Default gray
                }

                p.stroke(0);
                p.ellipse(x, vY, 30, 30);
                p.fill(0);
                p.noStroke();
                p.text(vId.toString(), x, vY);
            }
        }

        // Draw the augmenting path tree
        function drawAugmentingPathTree(p, state) {
            const canvasWidth = p.width;
            const treeWidth = canvasWidth * 0.35;

            // Draw title
            p.fill(0);
            p.textSize(16);
            p.textAlign(p.CENTER);
            p.text("Augmenting Path Tree", treeWidth/2, 30);

            if (Object.keys(state.bfsTree).length === 0) {
                p.fill(100);
                p.textAlign(p.CENTER, p.CENTER);
                p.text("No active BFS tree", treeWidth/2, p.height/2);
                return;
            }

            // Simple tree drawing - in a real app this would be more sophisticated
            const root = Object.keys(state.bfsTree)[0];
            const rootX = treeWidth / 2;
            const rootY = 80;
            const levelHeight = 80;

            // Draw root node
            p.fill(255, 165, 0);
            p.stroke(0);
            p.strokeWeight(1);
            p.rectMode(p.CENTER);
            p.rect(rootX, rootY, 40, 40);
            p.fill(0);
            p.noStroke();
            p.textAlign(p.CENTER, p.CENTER);
            p.text(root, rootX, rootY);

            // Draw children
            const children = state.bfsTree[root];
            if (children && children.length > 0) {
                const childSpacing = treeWidth / (children.length + 1);

                for (let i = 0; i < children.length; i++) {
                    const childX = (i + 1) * childSpacing;
                    const childY = rootY + levelHeight;

                    // Edge to child
                    p.stroke(255, 0, 0);
                    p.strokeWeight(1);
                    p.line(rootX, rootY + 20, childX, childY - 20);

                    // Child node
                    p.fill(255, 165, 0);
                    p.stroke(0);
                    p.rect(childX, childY, 40, 40);
                    p.fill(0);
                    p.noStroke();
                    p.text(children[i], childX, childY);

                    // Highlight if part of the path
                    if (state.highlightedPath.some(e => e.v === children[i])) {
                        p.stroke(255, 0, 0);
                        p.strokeWeight(3);
                        p.noFill();
                        p.rect(childX, childY, 45, 45);
                    }
                }
            }
        }

        // Event listeners
        document.addEventListener('DOMContentLoaded', () => {
            // Size controls
            sizeControls.querySelectorAll('button').forEach(button => {
                button.addEventListener('click', () => {
                    currentSize = parseInt(button.textContent);
                    sizeControls.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                    button.classList.add('active');
                    generateGraphAndHistory();
                });
            });

            // Degree controls
            degreeControls.querySelectorAll('button').forEach(button => {
                button.addEventListener('click', () => {
                    currentDegree = parseInt(button.textContent);
                    degreeControls.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                    button.classList.add('active');
                    generateGraphAndHistory();
                });
            });

            // Reset button
