            display: flex;
            align-items: center;
            margin-bottom: 20px;
            gap: 10px;
        }

        #slider-xD {
            flex-grow: 1;
        }

        #label-xD {
            min-width: 40px;
            text-align: right;
            font-family: monospace;
        }

        #plot {
            width: 100%;
            height: 400px;
            border: 1px solid #ccc;
            background-color: white;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-xD">distillate composition</label>
            <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
            <span id="label-xD">0.84</span>
        </div>
        <div id="plot"></div>
    </div>

    <script>
        // Initialize the plot when the page loads
        document.addEventListener('DOMContentLoaded', function() {
            const initialXD = parseFloat(document.getElementById('slider-xD').value);
            updatePlot(initialXD);

            // Set up slider event listener
            document.getElementById('slider-xD').addEventListener('input', function() {
                const xD = parseFloat(this.value);
                document.getElementById('label-xD').textContent = xD.toFixed(2);
                updatePlot(xD);
            });
        });

        function updatePlot(xD) {
            // Constants
            const a = 9;
            const b = -0.6;

            // VLE function
            function y(x) {
                return (a * x) / (1 + (a - 1) * x + b * x * (1 - x));
            }

            // Reflux ratio function
            function r(x, xD) {
                const yx = y(x);
                return (xD - yx) / (yx - x);
            }

            // Generate data for the curve
            const x_vals = [];
            const r_vals = [];
            const step = 0.001;

            for (let x = 0.001; x < xD; x += step) {
                const current_r = r(x, xD);
                if (isFinite(current_r) && current_r >= 0 && current_r < 5) {
                    x_vals.push(x);
                    r_vals.push(current_r);
                }
            }

            // Find tangent pinch point (local maximum of r)
            const pinch_x = [];
            const pinch_y = [];

            for (let i = 1; i < r_vals.length - 1; i++) {
                if (r_vals[i] > r_vals[i-1] && r_vals[i] > r_vals[i+1]) {
                    pinch_x.push(x_vals[i]);
                    pinch_y.push(r_vals[i]);
                    break; // Just take the first local maximum
                }
            }

            // Create traces
            const traces = [
                {
                    x: x_vals,
                    y: r_vals,
                    mode: 'lines',
                    type: 'scatter',
                    line: {
                        color: '#8B0000',
                        width: 2.5
                    }
                }
            ];

            if (pinch_x.length > 0) {
                traces.push({
                    x: pinch_x,
                    y: pinch_y,
                    mode: 'markers',
                    type: 'scatter',
                    marker: {
                        color: 'royalblue',
                        size: 10
                    }
                });
            }

            // Layout configuration
            const layout = {
                xaxis: {
                    range: [0, 1.0],
                    gridcolor: '#e0e0e0',
                    dtick: 0.2
                },
                yaxis: {
                    range: [0, 3.0],
                    gridcolor: '#e0e0e0',
                    dtick: 0.5
                },
                showlegend: false,
                margin: {l: 40, r: 20, b: 40, t: 20},
                plot_bgcolor: 'white',
                paper_bgcolor: 'white'
            };

            // Update the plot
            Plotly.react('plot', traces, layout);
        }
    </script>
</body>
</html>
