    <script>
        // Global variables
        let sceneObjects = [];
        let selectedTool = null;
        let activeObject = null;
        let interactionMode = null;
        let mouseOffsetX = 0;
        let mouseOffsetY = 0;
        let initialAngle = 0;
        let canvas, ctx;
        let canvasWidth, canvasHeight;
        const ROTATION_THRESHOLD = 25;

        // Initialize p5.js sketch
        function sketch(p) {
            p.setup = function() {
                const container = document.getElementById('canvas-container');
                canvasWidth = container.clientWidth * 0.9;
                canvasHeight = container.clientHeight * 0.9;

                canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('canvas-container');
                canvas.id('drawing-canvas');
                ctx = p._renderer.drawingContext;

                p.background(255);
                p.textAlign(p.CENTER, p.CENTER);
            };

            p.draw = function() {
                p.background(255);

                // Draw all objects in the scene
                sceneObjects.forEach(obj => {
                    p.push();
                    p.translate(obj.x, obj.y);
                    p.rotate(obj.rotation);

                    if (obj.type === 'atom' || obj.type === 'charge') {
                        p.textSize(16);
                        p.text(obj.value, 0, 0);
                    } else if (obj.type === 'structure') {
                        drawStructure(p, obj.value);
                    }

                    p.pop();
                });

                // Draw preview of selected tool
                if (selectedTool) {
                    p.push();
                    p.translate(p.mouseX, p.mouseY);
                    p.fill(200, 200, 200, 150);

                    if (selectedTool.type === 'atom' || selectedTool.type === 'charge') {
                        p.textSize(16);
                        p.text(selectedTool.value, 0, 0);
                    }

                    p.pop();
                }
            };

            p.mousePressed = function() {
                if (p.mouseX < 0 || p.mouseX > p.width || p.mouseY < 0 || p.mouseY > p.height) {
                    return;
                }

                // Check if we're adding a new object
                if (selectedTool) {
                    const newObj = {
                        type: selectedTool.type,
                        value: selectedTool.value,
                        x: p.mouseX,
                        y: p.mouseY,
                        rotation: 0
                    };
                    sceneObjects.push(newObj);

                    // Deselect the tool
                    document.querySelector('.selected')?.classList.remove('selected');
                    selectedTool = null;
                    return;
                }

                // Check for object interaction
                for (let i = sceneObjects.length - 1; i >= 0; i--) {
                    const obj = sceneObjects[i];
                    const dx = p.mouseX - obj.x;
                    const dy = p.mouseY - obj.y;
                    const distance = p.sqrt(dx * dx + dy * dy);

                    if (distance < 30) { // Simple hit detection
                        activeObject = obj;

                        if (distance < ROTATION_THRESHOLD) {
                            interactionMode = 'rotate';
                            initialAngle = p.atan2(dy, dx) - obj.rotation;
                        } else {
                            interactionMode = 'translate';
                            mouseOffsetX = p.mouseX - obj.x;
                            mouseOffsetY = p.mouseY - obj.y;
                        }
                        break;
                    }
                }
            };

            p.mouseDragged = function() {
                if (activeObject && interactionMode === 'translate') {
                    activeObject.x = p.mouseX - mouseOffsetX;
                    activeObject.y = p.mouseY - mouseOffsetY;
                } else if (activeObject && interactionMode === 'rotate') {
                    const dx = p.mouseX - activeObject.x;
                    const dy = p.mouseY - activeObject.y;
                    activeObject.rotation = p.atan2(dy, dx) - initialAngle;
                }
            };

            p.mouseReleased = function() {
                activeObject = null;
                interactionMode = null;
            };
        }

        // Helper function to draw structures
        function drawStructure(p, structure) {
            p.stroke(0);
            p.strokeWeight(2);
            p.textSize(16);

            switch (structure) {
                case 'bond':
                    p.line(-15, 0, 15, 0);
                    break;

                case 'water':
                    p.text('O', 0, 0);
                    p.text('H', -20, -20);
                    p.text('H', 20, -20);
                    p.line(-7, -5, -15, -15);
                    p.line(7, -5, 15, -15);
                    break;

                case 'cyclopropyl_ethane':
                    // Simplified representation
                    p.text('C', 0, 0);
                    p.text('C', -20, -20);
                    p.text('C', 20, -20);
                    p.line(0, 0, -15, -15);
                    p.line(0, 0, 15, -15);
                    p.line(-15, -15, 15, -15);
                    p.text('C', 0, 30);
                    p.line(0, 0, 0, 20);
                    break;

                case 'borazine':
                    // Simplified representation
                    const radius = 30;
                    for (let i = 0; i < 6; i++) {
                        const angle = p.PI / 3 * i;
                        const x = p.cos(angle) * radius;
                        const y = p.sin(angle) * radius;

                        if (i % 2 === 0) {
                            p.text('B', x, y);
                        } else {
                            p.text('N', x, y);
                        }

                        const nextAngle = p.PI / 3 * ((i + 1) % 6);
                        const nextX = p.cos(nextAngle) * radius;
                        const nextY = p.sin(nextAngle) * radius;
                        p.line(x, y, nextX, nextY);
                    }
                    break;

                case 'isopropylamine':
                    // Simplified representation
                    p.text('N', 0, 0);
                    p.text('C', -20, 20);
                    p.text('C', 20, 20);
                    p.text('C', -40, 40);
                    p.text('C', 0, 40);
                    p.line(0, 0, -15, 15);
                    p.line(0, 0, 15, 15);
                    p.line(-15, 15, -30, 35);
                    p.line(-15, 15, 0, 35);
                    break;
            }
        }

        // Initialize the application
        document.addEventListener('DOMContentLoaded', function() {
            new p5(sketch);

            // Toolbar button event listeners
            const toolbarButtons = document.querySelectorAll('#toolbar button');
            toolbarButtons.forEach(button => {
                button.addEventListener('click', function() {
                    // Remove selected class from all buttons
                    toolbarButtons.forEach(btn => btn.classList.remove('selected'));

                    // Add selected class to clicked button
                    this.classList.add('selected');

                    // Set selected tool based on button ID
                    const id = this.id;
                    if (id.startsWith('btn-charge')) {
                        selectedTool = {
                            type: 'charge',
                            value: this.textContent
                        };
                    } else if (id.startsWith('btn-element')) {
                        selectedTool = {
                            type: 'atom',
                            value: this.textContent
                        };
                    }
                });
            });

            // Undo button
            document.getElementById('btn-undo').addEventListener('click', function() {
                if (sceneObjects.length > 0) {
                    sceneObjects.pop();
                }
            });

            // Add structure button
            document.getElementById('btn-add').addEventListener('click', function() {
                const select = document.getElementById('select-structure');
                const selectedValue = select.options[select.selectedIndex].value;

                if (selectedValue) {
                    sceneObjects.push({
                        type: 'structure',
                        value: selectedValue,
                        x: canvasWidth / 2,
                        y: canvasHeight / 2,
                        rotation: 0
                    });
                }
            });
        });
    </script>
</body>
