    <script>
        // Wave parameters
        let A = 1.5;       // amplitude
        let f = 0.3;       // frequency
        let λ = 4.5;       // wavelength
        let t = 2.5;       // time
        let ϕ₀ = -Math.PI/2; // initial phase

        // Derived parameters
        let k = 0;         // wavenumber (2π/λ)
        let ω = 0;         // angular frequency (2πf)

        // Canvas dimensions
        const canvasWidth = 800;
        const canvasHeight = 400;

        // Scaling factors
        const scaleX = 50;  // pixels per unit in x
        const scaleY = 50;  // pixels per unit in y

        let myp5;

        function setupP5() {
            myp5 = new p5(function(sketch) {
                sketch.setup = function() {
                    const canvas = sketch.createCanvas(canvasWidth, canvasHeight);
                    canvas.parent('canvas-container');
                    sketch.noLoop();
                };

                sketch.draw = function() {
                    sketch.background(255);
                    sketch.translate(50, canvasHeight/2);
                    sketch.scale(1, -1);  // Flip y-axis so positive is up

                    // Draw axes
                    sketch.stroke(0);
                    sketch.strokeWeight(1);

                    // x-axis
                    sketch.line(0, 0, canvasWidth - 100, 0);
                    sketch.line(canvasWidth - 100, 0, canvasWidth - 110, -5);
                    sketch.line(canvasWidth - 100, 0, canvasWidth - 110, 5);
                    sketch.textSize(12);
                    sketch.scale(1, -1);  // Temporarily flip back for text
                    sketch.text('x', canvasWidth - 90, 20);
                    sketch.scale(1, -1);

                    // y-axis
                    sketch.line(0, -canvasHeight/2 + 50, 0, canvasHeight/2 - 50);
                    sketch.line(0, canvasHeight/2 - 50, -5, canvasHeight/2 - 60);
                    sketch.line(0, canvasHeight/2 - 50, 5, canvasHeight/2 - 60);
                    sketch.scale(1, -1);
                    sketch.text('y', -15, -canvasHeight/2 + 70);
                    sketch.scale(1, -1);

                    // Draw grid lines and ticks
                    sketch.stroke(200, 255, 200);
                    sketch.strokeWeight(1);
                    sketch.drawingContext.setLineDash([2, 2]);

                    for (let x = 1; x <= 16; x++) {
                        // Grid line
                        sketch.line(x * scaleX, -canvasHeight/2 + 50, x * scaleX, canvasHeight/2 - 50);

                        // Tick mark
                        sketch.stroke(0);
                        sketch.drawingContext.setLineDash([]);
                        sketch.line(x * scaleX, -5, x * scaleX, 5);

                        // Label
                        sketch.scale(1, -1);
                        sketch.text(x.toString(), x * scaleX - 3, 20);
                        sketch.scale(1, -1);

                        sketch.stroke(200, 255, 200);
                        sketch.drawingContext.setLineDash([2, 2]);
                    }

                    sketch.drawingContext.setLineDash([]);

                    // Draw wave
                    sketch.stroke(0, 0, 255);
                    sketch.strokeWeight(2);
                    sketch.noFill();

                    sketch.beginShape();
                    for (let px = 0; px < canvasWidth - 100; px++) {
                        const x = px / scaleX;
                        const y = A * Math.cos(ω * t - k * x + ϕ₀);
                        sketch.vertex(px, y * scaleY);
                    }
                    sketch.endShape();

                    // Draw points and vectors
                    for (let x = 1; x <= 16; x++) {
                        const y = A * Math.cos(ω * t - k * x + ϕ₀);
                        const v = -A * ω * Math.sin(ω * t - k * x + ϕ₀);  // velocity
                        const a = -A * ω * ω * Math.cos(ω * t - k * x + ϕ₀);  // acceleration

                        // Draw point
                        sketch.fill(0);
                        sketch.noStroke();
                        sketch.circle(x * scaleX, y * scaleY, 8);

                        // Draw velocity vector (red)
                        drawArrow(sketch, x * scaleX, y * scaleY, v * 15, [255, 0, 0], '\\vec{v}');

                        // Draw acceleration vector (black)
                        drawArrow(sketch, x * scaleX, y * scaleY, a * 5, [0, 0, 0], '\\vec{a}');
                    }
                };

                function drawArrow(sketch, baseX, baseY, vectorY, color, label) {
                    const arrowLength = vectorY;
                    const arrowHeadSize = 8;

                    sketch.stroke(color[0], color[1], color[2]);
                    sketch.fill(color[0], color[1], color[2]);
                    sketch.strokeWeight(1.5);

                    // Arrow shaft
                    sketch.line(baseX, baseY, baseX, baseY + arrowLength);

                    // Arrow head
                    if (arrowLength > 0) {
                        sketch.triangle(
                            baseX - arrowHeadSize/2, baseY + arrowLength - arrowHeadSize,
                            baseX + arrowHeadSize/2, baseY + arrowLength - arrowHeadSize,
                            baseX, baseY + arrowLength
                        );
                    } else {
                        sketch.triangle(
                            baseX - arrowHeadSize/2, baseY + arrowLength + arrowHeadSize,
                            baseX + arrowHeadSize/2, baseY + arrowLength + arrowHeadSize,
                            baseX, baseY + arrowLength
                        );
                    }

                    // Label
                    sketch.scale(1, -1);
                    sketch.textSize(12);
                    sketch.fill(color[0], color[1], color[2]);
                    sketch.text(label, baseX - 15, -(baseY + arrowLength + 15));
                    sketch.scale(1, -1);
                }
            });
        }

        function updateParameters() {
            A = parseFloat(document.getElementById('slider-amplitude').value);
            f = parseFloat(document.getElementById('slider-frequency').value);
            λ = parseFloat(document.getElementById('slider-wavelength').value);
            t = parseFloat(document.getElementById('slider-time').value);
            ϕ₀ = parseFloat(document.getElementById('select-phase').value);

            // Update displayed values
            document.getElementById('value-amplitude').textContent = A.toFixed(2);
            document.getElementById('value-frequency').textContent = f.toFixed(2);
            document.getElementById('value-wavelength').textContent = λ.toFixed(2);
            document.getElementById('value-time').textContent = t.toFixed(2);

            // Calculate derived parameters
            k = 2 * Math.PI / λ;
            ω = 2 * Math.PI * f;

            // Update formula display
            updateFormula();

            // Redraw canvas
            if (myp5) myp5.redraw();
        }

        function updateFormula() {
            let formula;

            if (Math.abs(ϕ₀ - Math.PI/2) < 0.01) {
                formula = `A \\sin(kx - \\omega t)`;
            } else if (Math.abs(ϕ₀ + Math.PI/2) < 0.01) {
                formula = `-A \\sin(kx - \\omega t)`;
            } else if (Math.abs(ϕ₀ - Math.PI) < 0.01 || Math.abs(ϕ₀ + Math.PI) < 0.01) {
                formula = `-A \\cos(kx - \\omega t)`;
            } else if (Math.abs(ϕ₀) < 0.01) {
                formula = `A \\cos(kx - \\omega t)`;
            } else {
                formula = `A \\cos(\\omega t - kx + \\phi_0)`;
            }

            // Replace placeholders with actual values
            formula = formula
                .replace('A', A.toFixed(2))
                .replace('k', k.toFixed(2))
                .replace('\\omega', ω.toFixed(2))
                .replace('\\phi_0', ϕ₀.toFixed(2));

            document.getElementById('formula-display').innerHTML = `$$y(x,t) = ${formula}$`;

            // Tell MathJax to typeset the new content
            if (typeof MathJax !== 'undefined') {
                MathJax.typeset();
            }
        }

        // Initialize
        document.addEventListener('DOMContentLoaded', function() {
            setupP5();
            updateParameters();

            // Add event listeners
            document.getElementById('slider-amplitude').addEventListener('input', updateParameters);
            document.getElementById('slider-frequency').addEventListener('input', updateParameters);
            document.getElementById('slider-wavelength').addEventListener('input', updateParameters);
            document.getElementById('slider-time').addEventListener('input', updateParameters);
            document.getElementById('select-phase').addEventListener('change', updateParameters);
        });
    </script>
</body>
</html>
