<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-container">
                <label for="slider-eccentricity">eccentricity ε</label>
                <input type="range" id="slider-eccentricity" min="0" max="1.5" step="0.01" value="0">
                <span id="value-eccentricity">0.00</span>
            </div>
        </div>

        <div id="visualization-area">
            <h3>Kepler orbits</h3>
            <div id="canvas-container"></div>
            <div id="energy-display">energy = <span id="energy-value">\\(-\\frac{1}{4}\\)</span></div>
        </div>
    </div>

    <script>
        let canvas;
        let p = 2; // semi-latus rectum
        let ε = 0; // eccentricity
        let scaleFactor = 30;
        let originX, originY;

        function setup() {
            canvas = createCanvas(700, 500);
            canvas.parent('canvas-container');
            originX = width * 0.7;
            originY = height / 2;

            // Set up slider event listener
            const slider = document.getElementById('slider-eccentricity');
            const valueDisplay = document.getElementById('value-eccentricity');
            const energyValue = document.getElementById('energy-value');

            slider.addEventListener('input', function() {
                ε = parseFloat(this.value);
                valueDisplay.textContent = ε.toFixed(2);

                // Update energy display
                const E = -0.25 * (1 - ε * ε);
                if (ε === 0) {
                    energyValue.innerHTML = '\\(-\\frac{1}{4}\\)';
                    if (typeof MathJax !== 'undefined') {
                        MathJax.typeset();
                    }
                } else {
                    energyValue.textContent = E.toFixed(2);
                }

                // Trigger redraw
                redraw();
            });
        }

        function draw() {
            background(240);
            translate(originX, originY);
            scale(1, -1); // Flip y-axis

            // Draw axes
            drawAxes();

            // Draw central body
            fill(0);
            noStroke();
            circle(0, 0, 6);

            // Draw orbit path
            drawOrbit();
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(1);

            // X-axis
            line(-15 * scaleFactor, 0, 5 * scaleFactor, 0);
            // Y-axis
            line(0, -7 * scaleFactor, 0, 7 * scaleFactor);

            // X-axis ticks and labels
            for (let x = -15; x <= 5; x++) {
                line(x * scaleFactor, -5, x * scaleFactor, 5);
                push();
                scale(1, -1); // Temporarily flip back for text
                textAlign(CENTER, CENTER);
                textSize(12);
                noStroke();
                fill(0);
                text(x.toString(), x * scaleFactor, 15);
                pop();
            }

            // Y-axis ticks and labels
            for (let y = -7; y <= 7; y++) {
                if (y !== 0) {
                    line(-5, y * scaleFactor, 5, y * scaleFactor);
                    push();
                    scale(1, -1); // Temporarily flip back for text
                    textAlign(RIGHT, CENTER);
                    textSize(12);
                    noStroke();
                    fill(0);
                    text(y.toString(), -10, y * scaleFactor);
                    pop();
                }
            }

            // Axis labels
            push();
            scale(1, -1);
            textAlign(CENTER, CENTER);
            textSize(14);
            text("Global'x", 5 * scaleFactor, 30);
            text("Global'y", -40, -7 * scaleFactor);
            pop();
        }

        function drawOrbit() {
            stroke('#4a80b5');
            strokeWeight(2);
            noFill();

            beginShape();

            if (ε === 0) {
                // Circular orbit (special case)
                for (let θ = 0; θ <= TWO_PI; θ += 0.01) {
                    const x = 2 * cos(θ) - 2;
                    const y = 2 * sin(θ);
                    vertex(x * scaleFactor, y * scaleFactor);
                }
            } else if (ε > 0 && ε < 1) {
                // Elliptical orbit
                for (let θ = 0; θ <= TWO_PI; θ += 0.01) {
                    const r = p / (1 + ε * cos(θ));
                    const x = r * cos(θ);
                    const y = r * sin(θ);
                    vertex(x * scaleFactor, y * scaleFactor);
                }
            } else if (ε === 1) {
                // Parabolic orbit
                const limit = 2.2; // To keep within canvas bounds
                for (let θ = -limit; θ <= limit; θ += 0.01) {
                    const r = p / (1 + ε * cos(θ));
                    const x = r * cos(θ);
                    const y = r * sin(θ);
                    vertex(x * scaleFactor, y * scaleFactor);
                }
            } else if (ε > 1) {
                // Hyperbolic orbit
                const maxθ = acos(-1/ε) * 0.99; // Stay within valid range
                for (let θ = -maxθ; θ <= maxθ; θ += 0.01) {
                    const r = p / (1 + ε * cos(θ));
                    const x = r * cos(θ);
                    const y = r * sin(θ);
                    vertex(x * scaleFactor, y * scaleFactor);
                }
            }

            endShape();
        }
    </script>
</body>
