    <script>
        // Constants
        const N = 64;
        const solutionCount = 2;

        // State variables
        let solutions = [];
        let conflictNumbers = Array(N).fill(0);
        let currentAlgorithm = 'heuristic';
        let totalSteps = 10;
        let currentStep = 0;
        let probabilityPlotMode = 'conflicts';

        // Initialize the application
        function init() {
            // Generate random solutions
            solutions = [];
            while (solutions.length < solutionCount) {
                const candidate = Math.floor(Math.random() * N);
                if (!solutions.includes(candidate)) {
                    solutions.push(candidate);
                }
            }

            // Calculate conflict numbers (Hamming distance to nearest solution)
            for (let i = 0; i < N; i++) {
                if (solutions.includes(i)) {
                    conflictNumbers[i] = 0;
                } else {
                    let minDistance = Infinity;
                    for (const sol of solutions) {
                        const distance = Math.abs(i - sol);
                        if (distance < minDistance) {
                            minDistance = distance;
                        }
                    }
                    conflictNumbers[i] = minDistance;
                }
            }

            // Set up event listeners
            document.getElementById('btn-unstructured').addEventListener('click', () => setAlgorithm('unstructured'));
            document.getElementById('btn-adiabatic').addEventListener('click', () => setAlgorithm('adiabatic'));
            document.getElementById('btn-discrete-adiabatic').addEventListener('click', () => setAlgorithm('discrete-adiabatic'));
            document.getElementById('btn-heuristic').addEventListener('click', () => setAlgorithm('heuristic'));

            document.getElementById('slider-total-steps').addEventListener('input', (e) => {
                totalSteps = parseInt(e.target.value);
                document.getElementById('display-total-steps').textContent = totalSteps;

                // Update current step slider max
                const currentStepSlider = document.getElementById('slider-current-step');
                currentStepSlider.max = totalSteps;

                // If current step is beyond new max, set to max
                if (currentStep > totalSteps) {
                    currentStep = totalSteps;
                    currentStepSlider.value = currentStep;
                    document.getElementById('display-current-step').textContent = currentStep;
                }

                // Update current step label
                document.getElementById('label-current-step').textContent = `current step (0 to ${totalSteps})`;

                update();
            });

            document.getElementById('slider-current-step').addEventListener('input', (e) => {
                currentStep = parseInt(e.target.value);
                document.getElementById('display-current-step').textContent = currentStep;
                update();
            });

            document.getElementById('btn-conflicts').addEventListener('click', () => {
                probabilityPlotMode = 'conflicts';
                document.getElementById('btn-conflicts').classList.add('active');
                document.getElementById('btn-eigenvalues').classList.remove('active');
                update();
            });

            document.getElementById('btn-eigenvalues').addEventListener('click', () => {
                probabilityPlotMode = 'eigenvalues';
                document.getElementById('btn-eigenvalues').classList.add('active');
                document.getElementById('btn-conflicts').classList.remove('active');
                update();
            });

            // Initial update
            update();
        }

        // Set the current algorithm
        function setAlgorithm(algorithm) {
            currentAlgorithm = algorithm;

            // Update button styles
            document.querySelectorAll('.algorithm-buttons button').forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById(`btn-${algorithm}`).classList.add('active');

            // Set default total steps based on algorithm
            switch (algorithm) {
                case 'unstructured':
                    totalSteps = 5;
                    break;
                case 'adiabatic':
                case 'discrete-adiabatic':
                    totalSteps = 200;
                    break;
                case 'heuristic':
                    totalSteps = 10;
                    break;
            }

            // Update UI
            const totalStepsSlider = document.getElementById('slider-total-steps');
            totalStepsSlider.value = totalSteps;
            document.getElementById('display-total-steps').textContent = totalSteps;

            const currentStepSlider = document.getElementById('slider-current-step');
            currentStepSlider.max = totalSteps;
            currentStepSlider.value = 0;
            currentStep = 0;
            document.getElementById('display-current-step').textContent = currentStep;
            document.getElementById('label-current-step').textContent = `current step (0 to ${totalSteps})`;

            update();
        }

        // Calculate the quantum state vector based on current settings
        function calculateStateVector() {
            const s = currentStep / totalSteps;

            // Initial state: uniform superposition
            const initialAmplitude = math.divide(1, math.sqrt(N));
            let state = math.zeros(N, 1).map(() => math.complex(initialAmplitude, 0));

            switch (currentAlgorithm) {
                case 'unstructured':
                    return calculateGroverState(state);
                case 'adiabatic':
                    return calculateAdiabaticState(s);
                case 'discrete-adiabatic':
                case 'heuristic':
                    return calculateDiscreteAdiabaticState(s);
            }

            return state;
        }

        // Calculate state for Grover's algorithm
        function calculateGroverState(initialState) {
            if (currentStep === 0) return initialState;

            // Create oracle matrix
            const oracleMatrix = math.identity(N);
            for (const sol of solutions) {
                oracleMatrix.subset(math.index(sol, sol), -1);
            }

            // Create diffusion matrix (2|ψ0><ψ0| - I)
            const psi0 = math.ones(N, 1).map(() => math.complex(initialAmplitude, 0));
            const outerProduct = math.multiply(psi0, math.transpose(psi0));
            const diffusionMatrix = math.subtract(
                math.multiply(2, outerProduct),
                math.identity(N)
            );

            // Grover operator
            const groverOperator = math.multiply(diffusionMatrix, oracleMatrix);

            // Apply operator k times
            let state = initialState;
            for (let i = 0; i < currentStep; i++) {
                state = math.multiply(groverOperator, state);
            }

            return state;
        }

        // Calculate state for adiabatic algorithm
        function calculateAdiabaticState(s) {
            // Hamiltonian H(s) = (1-s)*H0 + s*Hp
            // H0 = -|ψ0><ψ0|
            const initialAmplitude = math.divide(1, math.sqrt(N));
            const psi0 = math.ones(N, 1).map(() => math.complex(initialAmplitude, 0));
            const H0 = math.multiply(-1, math.multiply(psi0, math.transpose(psi0)));

            // Hp = diag(conflictNumbers)
            const Hp = math.diag(conflictNumbers);

            // Interpolated Hamiltonian
            const H = math.add(
                math.multiply(1 - s, H0),
                math.multiply(s, Hp)
            );

            // Find ground state (eigenvector with smallest eigenvalue)
            const eigs = math.eigs(H);
            let minEigValue = Infinity;
            let minEigIndex = 0;

            for (let i = 0; i < eigs.values.length; i++) {
                if (math.re(eigs.values[i]) < minEigValue) {
                    minEigValue = math.re(eigs.values[i]);
                    minEigIndex = i;
                }
            }

            return eigs.vectors.map(row => row[minEigIndex]);
        }

        // Calculate state for discrete adiabatic algorithm
        function calculateDiscreteAdiabaticState(s) {
            if (currentStep === 0) {
                const initialAmplitude = math.divide(1, math.sqrt(N));
                return math.zeros(N, 1).map(() => math.complex(initialAmplitude, 0));
            }

            // Same Hamiltonian as adiabatic case
            const initialAmplitude = math.divide(1, math.sqrt(N));
            const psi0 = math.ones(N, 1).map(() => math.complex(initialAmplitude, 0));
            const H0 = math.multiply(-1, math.multiply(psi0, math.transpose(psi0)));
            const Hp = math.diag(conflictNumbers);

            let state = math.zeros(N, 1).map(() => math.complex(initialAmplitude, 0));

            for (let k = 0; k < currentStep; k++) {
                const sk = k / totalSteps;
                const H = math.add(
                    math.multiply(1 - sk, H0),
                    math.multiply(sk, Hp)
                );

                // Evolution operator U = exp(-i * H * dt)
                const dt = 1 / totalSteps;
                const U = math.expm(math.multiply(math.complex(0, -1), math.multiply(H, dt)));

                state = math.multiply(U, state);
            }

            return state;
        }

        // Update all visualizations
        function update() {
            const stateVector = calculateStateVector();
            updateAmplitudesPlot(stateVector);
            updateProbabilityPlot(stateVector);
        }

        // Update the amplitudes plot
        function updateAmplitudesPlot(stateVector) {
            // Prepare data for scatter plot
            const realParts = [];
            const imagParts = [];
            const colors = [];
            const sizes = [];
            const texts = [];

            for (let i = 0; i < N; i++) {
                const amplitude = stateVector[i];
                realParts.push(math.re(amplitude));
                imagParts.push(math.im(amplitude));

                if (solutions.includes(i)) {
                    colors.push('black');
                    sizes.push(10);
                    texts.push(`Solution state ${i}`);
                } else if (i === 0 && currentStep === 0) {
                    // Highlight one non-solution state at step 0
                    colors.push('red');
                    sizes.push(8);
                    texts.push(`Initial state ${i}`);
                } else {
                    // Color based on conflict number
                    const conflict = conflictNumbers[i];
                    const hue = 30 + (1 - conflict / Math.max(...conflictNumbers)) * 210;
                    colors.push(`hsl(${hue}, 100%, 50%)`);
                    sizes.push(6);
                    texts.push(`State ${i}, conflicts: ${conflict}`);
                }
            }

            const trace = {
                x: realParts,
                y: imagParts,
                mode: 'markers',
                type: 'scattergl',
                marker: {
                    color: colors,
                    size: sizes,
                    opacity: 0.8
                },
                text: texts,
                hoverinfo: 'text'
            };

            const layout = {
                title: '',
                xaxis: {
                    title: 'Re',
                    zeroline: true
                },
                yaxis: {
                    title: 'Im',
                    scaleanchor: 'x',
                    scaleratio: 1
                },
                margin: { l: 50, r: 20, t: 20, b: 50 },
                showlegend: false
            };

            Plotly.newPlot('plot-amplitudes', [trace], layout);
        }

        // Update the probability plot
        function updateProbabilityPlot(stateVector) {
            if (probabilityPlotMode === 'conflicts') {
                updateConflictsPlot(stateVector);
            } else {
                updateEigenvaluesPlot(stateVector);
            }
        }

        // Update the conflicts mode of the probability plot
        function updateConflictsPlot(stateVector) {
            // Group probabilities by conflict number
            const conflictGroups = {};
            for (let i = 0; i < N; i++) {
                const conflict = conflictNumbers[i];
                const probability = math.pow(math.abs(stateVector[i]), 2);

                if (!conflictGroups[conflict]) {
                    conflictGroups[conflict] = 0;
                }
                conflictGroups[conflict] += probability;
            }

            // Prepare data for bar plot
            const conflicts = Object.keys(conflictGroups).map(Number).sort((a, b) => a - b);
            const probabilities = conflicts.map(c => conflictGroups[c]);

            const trace = {
                x: conflicts,
                y: probabilities,
                type: 'bar',
                marker: {
                    color: 'rgba(55, 128, 191, 0.7)',
                    line: {
                        color: 'rgba(55, 128, 191, 1.0)',
                        width: 1
                    }
                }
            };

            const layout = {
                title: '',
                xaxis: {
                    title: 'number of conflicts'
                },
                yaxis: {
                    title: 'probability',
                    range: [0, 1]
                },
                margin: { l: 50, r: 20, t: 20, b: 50 },
                bargap: 0.1
            };

            Plotly.newPlot('plot-probability', [trace], layout);
        }

        // Update the eigenvalues mode of the probability plot
        function updateEigenvaluesPlot(stateVector) {
            // For simplicity, we'll use the same data as conflicts plot in this example
            // In a real implementation, you would calculate the actual eigenvalues and projections

            const conflictGroups = {};
            for (let i = 0; i < N; i++) {
                const conflict = conflictNumbers[i];
                const probability = math.pow(math.abs(stateVector[i]), 2);

                if (!conflictGroups[conflict]) {
                    conflictGroups[conflict] = 0;
                }
                conflictGroups[conflict] += probability;
            }

            const conflicts = Object.keys(conflictGroups).map(Number).sort((a, b) => a - b);
            const probabilities = conflicts.map(c => conflictGroups[c]);

            const trace = {
                x: conflicts,
                y: probabilities,
                type: 'bar',
                marker: {
                    color: 'rgba(191, 55, 128, 0.7)',
                    line: {
                        color: 'rgba(191, 55, 128, 1.0)',
                        width: 1
                    }
                }
            };

            const layout = {
                title: '',
                xaxis: {
                    title: currentAlgorithm === 'unstructured' ? 'arg(eigenvalue)' : 'eigenvalue'
                },
                yaxis: {
                    title: 'probability',
                    range: [0, 1]
                },
                margin: { l: 50, r: 20, t: 20, b: 50 },
                bargap: 0.1
            };

            Plotly.newPlot('plot-probability', [trace], layout);
        }

        // Initialize when the page loads
        window.addEventListener('load', init);
