            const Tsat = 5818 / (8.65 - Math.log10(P)) - 230;

            // Generate temperature range
            const Tmin = 280;
            const Tmax = 400;
            const Tstep = 1;
            const temperatures = [];
            for (let T = Tmin; T <= Tmax; T += Tstep) {
                temperatures.push(T);
