    <!-- CDN Includes -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <script>
        // Global variables
        let theta = 0.44879; // ≈ π/7
        let cVector = { x: 0.3, y: 0.7 };
        let canvasSize = 400;
        let radius = 150;
        let isDragging = false;
        let showMetricCircle = false;
        let showMetricRegion = false;
        let showFirstOrderVec = false;
        let showFirstOrderRegion = false;
        let showOptimalPair = false;
        let showOptimalRegion = false;

        // p5.js sketch for the Bloch sphere slice
        function setup() {
            let canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('canvas-container');
            canvas.elt.id = 'canvas-bloch';
            angleMode(RADIANS);
        }

        function draw() {
            background(255);
            translate(width/2, height);
            scale(1, -1); // Flip Y-axis to have origin at bottom center

            // Draw axes
            stroke(200);
            line(-width/2, 0, width/2, 0); // X-axis
            line(0, 0, 0, height); // Y-axis

            // Draw semi-circle
            noFill();
            stroke(0);
            arc(0, 0, radius*2, radius*2, 0, PI);

            // Calculate vectors
            let a = {
                x: radius * sin(theta),
                y: radius * cos(theta)
            };
            let b = {
                x: -radius * sin(theta),
                y: radius * cos(theta)
            };
            let c = {
                x: cVector.x * radius,
                y: cVector.y * radius
            };

            // Constrain c to be within the semi-circle
            let cMag = sqrt(c.x*c.x + c.y*c.y);
            if (cMag > radius) {
                c.x = c.x * radius / cMag;
                c.y = c.y * radius / cMag;
                cVector.x = c.x / radius;
                cVector.y = c.y / radius;
            }

            // Draw vectors
            drawArrow(0, 0, a.x, a.y, color(0, 0, 255), 'a');
            drawArrow(0, 0, b.x, b.y, color(255, 0, 0), 'b');
            drawArrow(0, 0, c.x, c.y, color(0, 200, 0), 'c');

            // Calculate optimal d vector
            let dOpt = calculateOptimalD(a, b, c);

            // Draw additional elements based on checkboxes
            if (showMetricCircle) {
                drawMetricCircle(a, c);
            }

            if (showMetricRegion || showFirstOrderRegion || showOptimalRegion) {
                drawMetricRegions(a, b, c, dOpt);
            }

            if (showFirstOrderVec) {
                drawFirstOrderVector(b, dOpt);
            }

            if (showOptimalPair) {
                drawOptimalPair(a, b, theta);
            }

            // Update the error plot
            updateErrorPlot(a, b, c, dOpt);
        }

        function mousePressed() {
            let canvasX = mouseX - width/2;
            let canvasY = height - mouseY; // Invert Y coordinate

            // Check if mouse is near the tip of vector c
            let cX = cVector.x * radius;
            let cY = cVector.y * radius;
            let distToC = sqrt((canvasX - cX)*(canvasX - cX) + (canvasY - cY)*(canvasY - cY));

            if (distToC < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                let canvasX = mouseX - width/2;
                let canvasY = height - mouseY; // Invert Y coordinate

                // Update c vector (constrained to semi-circle)
                let newX = canvasX / radius;
                let newY = canvasY / radius;
                let mag = sqrt(newX*newX + newY*newY);

                if (mag > 1) {
                    newX /= mag;
                    newY /= mag;
                }

                // Ensure y is positive (upper semi-circle)
                if (newY < 0) {
                    newY = 0;
                }

                cVector.x = newX;
                cVector.y = newY;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        // Helper functions
        function drawArrow(x1, y1, x2, y2, col, label) {
            push();
            stroke(col);
            fill(col);
            line(x1, y1, x2, y2);

            // Arrow head
            let angle = atan2(y2 - y1, x2 - x1);
            translate(x2, y2);
            rotate(angle);
            let arrowSize = 10;
            triangle(0, 0, -arrowSize, -arrowSize/2, -arrowSize, arrowSize/2);

            // Label
            translate(-arrowSize - 5, 0);
            rotate(-angle);
            textSize(14);
            text(label, 0, 0);
            pop();
        }

        function calculateOptimalD(a, b, c) {
            // Simplified calculation for demonstration
            // In a real implementation, this would involve more complex math
            let dOpt = {
                x: b.x * 0.8,
                y: b.y * 0.8
            };
            return dOpt;
        }

        function drawMetricCircle(a, c) {
            push();
            noFill();
            stroke(0, 0, 255);
            drawingContext.setLineDash([5, 5]);
            let distance = dist(a.x, a.y, c.x, c.y);
            circle(a.x, a.y, distance * 2);
            drawingContext.setLineDash([]);
            pop();
        }

        function drawMetricRegions(a, b, c, dOpt) {
            push();
            noFill();
            drawingContext.setLineDash([5, 5]);

            if (showMetricRegion) {
                stroke(0, 200, 0);
                arc(0, 0, dist(0, 0, c.x, c.y) * 2, dist(0, 0, c.x, c.y) * 2, 0, PI);
            }

            if (showFirstOrderRegion) {
                stroke(255, 165, 0);
                arc(0, 0, dist(0, 0, dOpt.x, dOpt.y) * 2, dist(0, 0, dOpt.x, dOpt.y) * 2, 0, PI);
            }

            drawingContext.setLineDash([]);
            pop();
        }

        function drawFirstOrderVector(b, dOpt) {
            push();
            stroke(255, 165, 0);
            fill(255, 165, 0);
            drawingContext.setLineDash([5, 5]);
            line(b.x, b.y, dOpt.x, dOpt.y);
            drawingContext.setLineDash([]);

            // Label
            textSize(14);
            text('d_err', (b.x + dOpt.x)/2, (b.y + dOpt.y)/2);
            pop();
        }

        function drawOptimalPair(a, b, theta) {
            // Calculate optimal pair (simplified for demonstration)
            let cOpt = {
                x: radius * sin(2*theta)/2,
                y: radius * cos(2*theta)/2
            };
            let dOpt = {
                x: -radius * sin(2*theta)/2,
                y: radius * cos(2*theta)/2
            };

            // Draw vectors
            drawArrow(0, 0, cOpt.x, cOpt.y, color(100, 200, 100), 'c_opt');
            drawArrow(0, 0, dOpt.x, dOpt.y, color(200, 100, 100), 'd_opt');
        }

        // Plotly.js functions
        function initializeErrorPlot() {
            let layout = {
                title: 'Error Trade-off',
                xaxis: { title: 'D(C, A)', range: [0, 1.5] },
                yaxis: { title: 'D(D, B)', range: [0, 1.5] },
                margin: { t: 40, l: 60, r: 40, b: 60 },
                showlegend: false
            };

            Plotly.newPlot('plot-error', [], layout);
        }

        function updateErrorPlot(a, b, c, dOpt) {
            // Calculate errors (simplified for demonstration)
            let dCA = 0.5 * pow(dist(a.x, a.y, c.x, c.y) / radius, 2);
            let dDB = 0.5 * pow(dist(b.x, b.y, dOpt.x, dOpt.y) / radius, 2);

            // Calculate boundary curve
            let xValues = [];
            let yValues = [];
            let steps = 50;
            let maxX = pow(sin(2*theta), 2);

            for (let i = 0; i <= steps; i++) {
                let x = i * maxX / steps;
                xValues.push(x);
                yValues.push(pow(sin(2*theta) - sqrt(x), 2));
            }

            // Calculate optimal point
            let optimalValue = pow(sin(2*theta)/2, 2);

            // Create traces
            let traces = [
                {
                    x: xValues,
                    y: yValues,
                    mode: 'lines',
                    line: { color: 'blue' }
                },
                {
                    x: [dCA],
                    y: [dDB],
                    mode: 'markers',
                    marker: { color: 'red', size: 10 }
                }
            ];

            if (showOptimalPair) {
                traces.push({
                    x: [optimalValue],
                    y: [optimalValue],
                    mode: 'markers',
                    marker: { color: 'green', size: 10 }
                });
            }

            Plotly.react('plot-error', traces, {
                title: 'Error Trade-off',
                xaxis: { title: 'D(C, A)', range: [0, 1.5] },
                yaxis: { title: 'D(D, B)', range: [0, 1.5] }
            });
        }

        // Event listeners for controls
        document.getElementById('slider-theta').addEventListener('input', function() {
            theta = parseFloat(this.value);
            document.getElementById('span-theta-value').textContent = theta.toFixed(4);
            document.getElementById('span-sinsq-theta-value').textContent = pow(sin(theta), 2).toFixed(4);
