        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <h4>Choose 'regular points' below, then the number of regular points,</h4>
            <div id="regular-points-selector">
                <button id="btn-regular-3">3</button>
                <button id="btn-regular-4" class="active">4</button>
                <button id="btn-regular-5">5</button>
            </div>
            <p>and drag them anywhere.</p>
            <p>Once these points are chosen, they will be placed at the vertices of a regular polygon. You can then drag them to new positions to explore different configurations.</p>

            <h4>Now choose 'Steiner points' and how many you want to use.</h4>
            <div class="radio-group">
                <input type="radio" name="point-mode" id="radio-regular">
                <label for="radio-regular">regular points</label>
                <input type="radio" name="point-mode" id="radio-steiner" checked>
                <label for="radio-steiner">Steiner points</label>
            </div>
            <div id="steiner-points-selector">
                <button id="btn-steiner-0">0</button>
                <button id="btn-steiner-1">1</button>
                <button id="btn-steiner-2" class="active">2</button>
                <button id="btn-steiner-3">3</button>
            </div>
            <p>You can drag them anywhere.</p>
            <p>Can you find an optimal position for the Steiner points that minimizes the total length of the network?</p>

            <hr>

            <div>
                <label for="checkbox-show-angles">show the angles</label>
                <input type="checkbox" id="checkbox-show-angles" checked>
            </div>

            <hr>

            <div>
                <p>The total length of the network is:</p>
                <h3 id="text-total-length">6.01064</h3>
            </div>
        </div>

        <div id="canvas-container">
            <div id="main-canvas"></div>
        </div>
    </div>

    <script>
        // Application state
        const state = {
            numRegularPoints: 4,
            numSteinerPoints: 2,
            pointMode: 'steiner',
            showAngles: true,
            regularPoints: [],
            steinerPoints: [],
            mstEdges: [],
            totalLength: 0,
            draggingPoint: null,
            canvasSize: { width: 0, height: 0 }
        };

        // Initialize p5.js sketch
        function sketch(p) {
            p.setup = function() {
                const canvasContainer = document.getElementById('canvas-container');
                const width = canvasContainer.clientWidth - 20;
                const height = Math.min(window.innerHeight - 60, width * 0.8);
                state.canvasSize = { width, height };

                const canvas = p.createCanvas(width, height);
                canvas.parent('main-canvas');

                initializePoints();
                calculateMST();
            };

            p.draw = function() {
                p.background(238);
                drawCompleteGraph();
                drawMST();
                drawPoints();

                if (state.showAngles) {
                    drawAngles();
                }
            };

            p.mousePressed = function() {
                const mouseX = p.mouseX;
                const mouseY = p.mouseY;

                // Check which point is being dragged based on current mode
                const pointsToCheck = state.pointMode === 'regular' ? state.regularPoints : state.steinerPoints;

                for (let i = 0; i < pointsToCheck.length; i++) {
                    const point = pointsToCheck[i];
                    const dist = p.dist(mouseX, mouseY, point.x, point.y);

                    if (dist <= 10) { // 10px hit radius
                        state.draggingPoint = { index: i, point: point };
                        return;
                    }
                }
            };

            p.mouseDragged = function() {
                if (state.draggingPoint) {
                    state.draggingPoint.point.x = p.mouseX;
                    state.draggingPoint.point.y = p.mouseY;
                    calculateMST();
                }
            };

            p.mouseReleased = function() {
                state.draggingPoint = null;
            };

            function initializePoints() {
                // Clear existing points
                state.regularPoints = [];
                state.steinerPoints = [];

                // Create regular points in a polygon pattern
                const centerX = state.canvasSize.width / 2;
                const centerY = state.canvasSize.height / 2;
                const radius = Math.min(state.canvasSize.width, state.canvasSize.height) * 0.35;

                for (let i = 0; i < state.numRegularPoints; i++) {
                    const angle = p.TWO_PI * i / state.numRegularPoints - p.HALF_PI;
                    const x = centerX + radius * p.cos(angle);
                    const y = centerY + radius * p.sin(angle);
                    state.regularPoints.push({ x, y });
                }

                // Create Steiner points at center
                for (let i = 0; i < state.numSteinerPoints; i++) {
                    // Slightly offset Steiner points from exact center
                    const offsetX = i % 2 === 0 ? -20 : 20;
                    const offsetY = i < 2 ? -20 : 20;
                    state.steinerPoints.push({
                        x: centerX + offsetX,
                        y: centerY + offsetY
                    });
                }
            }

            function calculateMST() {
                // Combine all points
                const allPoints = [...state.regularPoints, ...state.steinerPoints];

                // Create all possible edges with their weights
                const edges = [];
                for (let i = 0; i < allPoints.length; i++) {
                    for (let j = i + 1; j < allPoints.length; j++) {
                        const p1 = allPoints[i];
                        const p2 = allPoints[j];
                        const weight = p.dist(p1.x, p1.y, p2.x, p2.y);
                        edges.push({ from: i, to: j, weight });
                    }
                }

                // Sort edges by weight
                edges.sort((a, b) => a.weight - b.weight);

                // Kruskal's algorithm for MST
                const parent = Array(allPoints.length).fill().map((_, i) => i);

                function find(u) {
                    if (parent[u] !== u) {
                        parent[u] = find(parent[u]);
                    }
                    return parent[u];
                }

                function union(u, v) {
                    const rootU = find(u);
                    const rootV = find(v);
                    if (rootU !== rootV) {
                        parent[rootV] = rootU;
                        return true;
                    }
                    return false;
                }

                const mstEdges = [];
                let totalLength = 0;

                for (const edge of edges) {
                    if (union(edge.from, edge.to)) {
                        mstEdges.push(edge);
                        totalLength += edge.weight;

                        if (mstEdges.length === allPoints.length - 1) {
                            break;
                        }
                    }
                }

                state.mstEdges = mstEdges;
                state.totalLength = totalLength;

                // Update the total length display
                document.getElementById('text-total-length').textContent = totalLength.toFixed(5);
            }

            function drawCompleteGraph() {
                p.stroke(190, 160, 130, 127);
                p.strokeWeight(1);

                const allPoints = [...state.regularPoints, ...state.steinerPoints];
                for (let i = 0; i < allPoints.length; i++) {
                    for (let j = i + 1; j < allPoints.length; j++) {
                        const p1 = allPoints[i];
                        const p2 = allPoints[j];
                        p.line(p1.x, p1.y, p2.x, p2.y);
                    }
                }
            }

            function drawMST() {
                p.stroke(51);
                p.strokeWeight(3);

                const allPoints = [...state.regularPoints, ...state.steinerPoints];
                for (const edge of state.mstEdges) {
                    const p1 = allPoints[edge.from];
                    const p2 = allPoints[edge.to];
                    p.line(p1.x, p1.y, p2.x, p2.y);
                }
            }

            function drawPoints() {
                // Draw regular points
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.fill(255);

                for (const point of state.regularPoints) {
                    p.ellipse(point.x, point.y, 16, 16);
                }

                // Draw Steiner points
                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                p.fill(255);

                for (const point of state.steinerPoints) {
                    p.ellipse(point.x, point.y, 16, 16);
                    p.ellipse(point.x, point.y, 8, 8);
                }
            }

            function drawAngles() {
                p.fill(0);
                p.noStroke();
                p.textSize(12);
                p.textAlign(p.CENTER, p.CENTER);

                const allPoints = [...state.regularPoints, ...state.steinerPoints];

                // We only care about angles at Steiner points
                for (let i = 0; i < state.steinerPoints.length; i++) {
                    const steinerIndex = i + state.regularPoints.length;
                    const steinerPoint = state.steinerPoints[i];

                    // Find all edges connected to this Steiner point
                    const connectedEdges = state.mstEdges.filter(edge => 
                        edge.from === steinerIndex || edge.to === steinerIndex
                    );

                    if (connectedEdges.length < 2) continue;

                    // Get all connected points
                    const connectedPoints = connectedEdges.map(edge => {
                        const otherIndex = edge.from === steinerIndex ? edge.to : edge.from;
                        return allPoints[otherIndex];
                    });

                    // Calculate vectors from Steiner point to connected points
                    const vectors = connectedPoints.map(point => ({
                        x: point.x - steinerPoint.x,
                        y: point.y - steinerPoint.y
                    }));

                    // Calculate angles of these vectors
                    const angles = vectors.map(v => p.atan2(v.y, v.x));

                    // Sort angles clockwise
                    angles.sort((a, b) => a - b);

                    // Calculate angles between consecutive vectors
                    for (let j = 0; j < angles.length; j++) {
                        const nextAngle = angles[(j + 1) % angles.length];
                        let angleDiff = nextAngle - angles[j];

                        // Handle wrap-around
                        if (angleDiff < 0) angleDiff += p.TWO_PI;

                        const angleDeg = p.degrees(angleDiff);
                        const bisectorAngle = angles[j] + angleDiff / 2;

                        // Position text along bisector
                        const textRadius = 30;
                        const textX = steinerPoint.x + textRadius * p.cos(bisectorAngle);
                        const textY = steinerPoint.y + textRadius * p.sin(bisectorAngle);

                        p.text(Math.round(angleDeg) + '°', textX, textY);
                    }
                }
            }
        }

        // Create p5 instance
        new p5(sketch);

        // Set up UI event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Regular points selector
            document.getElementById('btn-regular-3').addEventListener('click', function() {
                updateRegularPoints(3);
            });
            document.getElementById('btn-regular-4').addEventListener('click', function() {
                updateRegularPoints(4);
            });
            document.getElementById('btn-regular-5').addEventListener('click', function() {
                updateRegularPoints(5);
            });

            // Steiner points selector
            document.getElementById('btn-steiner-0').addEventListener('click', function() {
                updateSteinerPoints(0);
            });
            document.getElementById('btn-steiner-1').addEventListener('click', function() {
                updateSteinerPoints(1);
            });
            document.getElementById('btn-steiner-2').addEventListener('click', function() {
                updateSteinerPoints(2);
            });
            document.getElementById('btn-steiner-3').addEventListener('click', function() {
                updateSteinerPoints(3);
            });

            // Point mode radio buttons
            document.getElementById('radio-regular').addEventListener('change', function() {
                if (this.checked) {
                    state.pointMode = 'regular';
                }
            });
            document.getElementById('radio-steiner').addEventListener('change', function() {
                if (this.checked) {
                    state.pointMode = 'steiner';
                }
            });

            // Show angles checkbox
            document.getElementById('checkbox-show-angles').addEventListener('change', function() {
                state.showAngles = this.checked;
            });

            function updateRegularPoints(num) {
                state.numRegularPoints = num;

                // Update button states
                document.querySelectorAll('#regular-points-selector button').forEach(btn => {
                    btn.classList.remove('active');
                });
                document.getElementById(`btn-regular-${num}`).classList.add('active');

                // Reinitialize points and recalculate
                initializePoints();
                calculateMST();
            }

            function updateSteinerPoints(num) {
                state.numSteinerPoints = num;

                // Update button states
                document.querySelectorAll('#steiner-points-selector button').forEach(btn => {
                    btn.classList.remove('active');
                });
                document.getElementById(`btn-steiner-${num}`).classList.add('active');

                // Reinitialize points and recalculate
                initializePoints();
                calculateMST();
            }

            function initializePoints() {
                // This will be handled by the p5 sketch
                // We just need to trigger a reset
                const sketchInstance = window[Object.keys(window).find(key => key.startsWith('p5_'))];
                if (sketchInstance) {
                    sketchInstance.setup();
                }
            }

            function calculateMST() {
                // This will be handled by the p5 sketch
                // We just need to trigger a redraw
                const sketchInstance = window[Object.keys(window).find(key => key.startsWith('p5_'))];
                if (sketchInstance) {
                    sketchInstance.redraw();
                }
            }
        });
    </script>
</body>
</html>
