                <h3>Standard Deviations</h3>
                <div class="slider-container">
                    <label for="slider-sd-w">W</label>
                    <input type="range" id="slider-sd-w" min="1" max="10" value="1" step="0.1">
                    <span id="value-sd-w">1</span>
                </div>
                <div class="slider-container">
                    <label for="slider-sd-x">X</label>
                    <input type="range" id="slider-sd-x" min="1" max="10" value="1" step="0.1">
                    <span id="value-sd-x">1</span>
                </div>
                <div class="slider-container">
                    <label for="slider-sd-y">Y</label>
                    <input type="range" id="slider-sd-y" min="1" max="10" value="1" step="0.1">
                    <span id="value-sd-y">1</span>
                </div>
                <div class="slider-container">
                    <label for="slider-sd-z">Z</label>
                    <input type="range" id="slider-sd-z" min="1" max="10" value="1" step="0.1">
                    <span id="value-sd-z">1</span>
                </div>
            </div>
        </div>
        <div id="visualization-panel"></div>
    </div>

    <script>
        let canvas;
        let groupMeans = [0, 0, 0, 0];
        let groupSDs = [1, 1, 1, 1];
        let fRatio = 0;
        let msBetween = 0;
        let msWithin = 0;
        let grandMean = 0;

        function setup() {
            canvas = createCanvas(document.getElementById('visualization-panel').offsetWidth - 40, 
                                document.getElementById('visualization-panel').offsetHeight - 40);
            canvas.parent('visualization-panel');
            noLoop();

            // Set up event listeners for all sliders
            document.querySelectorAll('input[type="range"]').forEach(slider => {
                slider.addEventListener('input', updateVisualization);
            });

            // Initial update
            updateVisualization();
        }

        function windowResized() {
            resizeCanvas(document.getElementById('visualization-panel').offsetWidth - 40, 
                        document.getElementById('visualization-panel').offsetHeight - 40);
            redraw();
        }

        function updateVisualization() {
            // Read slider values
            groupMeans = [
                parseFloat(document.getElementById('slider-mean-w').value),
                parseFloat(document.getElementById('slider-mean-x').value),
                parseFloat(document.getElementById('slider-mean-y').value),
                parseFloat(document.getElementById('slider-mean-z').value)
            ];

            groupSDs = [
                parseFloat(document.getElementById('slider-sd-w').value),
                parseFloat(document.getElementById('slider-sd-x').value),
                parseFloat(document.getElementById('slider-sd-y').value),
                parseFloat(document.getElementById('slider-sd-z').value)
            ];

            // Update displayed values
            document.getElementById('value-mean-w').textContent = groupMeans[0].toFixed(1);
            document.getElementById('value-mean-x').textContent = groupMeans[1].toFixed(1);
            document.getElementById('value-mean-y').textContent = groupMeans[2].toFixed(1);
            document.getElementById('value-mean-z').textContent = groupMeans[3].toFixed(1);

            document.getElementById('value-sd-w').textContent = groupSDs[0].toFixed(1);
            document.getElementById('value-sd-x').textContent = groupSDs[1].toFixed(1);
            document.getElementById('value-sd-y').textContent = groupSDs[2].toFixed(1);
            document.getElementById('value-sd-z').textContent = groupSDs[3].toFixed(1);

            // Perform ANOVA calculations
            calculateANOVA();

            // Redraw the visualization
            redraw();
        }

        function calculateANOVA() {
            const k = 4; // number of groups
            const n = 10; // number of subjects per group

            // Calculate Grand Mean
            grandMean = (groupMeans[0] + groupMeans[1] + groupMeans[2] + groupMeans[3]) / k;

            // Calculate SS Between
            let ssBetween = 0;
            for (let i = 0; i < k; i++) {
                ssBetween += n * Math.pow(groupMeans[i] - grandMean, 2);
            }

            // Calculate MS Between
            msBetween = ssBetween / (k - 1);

            // Calculate SS Within
            let ssWithin = 0;
            for (let i = 0; i < k; i++) {
                ssWithin += Math.pow(groupSDs[i], 2) * (n - 1);
            }

            // Calculate MS Within
            msWithin = ssWithin / (n * k - k);

            // Calculate F-Ratio
            fRatio = msBetween / msWithin;
        }

        function draw() {
            background(255);

            // Draw the top plot (group data)
            drawGroupDataPlot();

            // Draw the bottom plot (ANOVA results)
            drawANOVAResultsPlot();
        }

        function drawGroupDataPlot() {
            const plotHeight = height * 0.6;
            const plotY = 20;
            const plotWidth = width - 40;
            const plotX = 20;

            // Draw axes
            stroke(0);
            strokeWeight(1);
            line(plotX, plotY + plotHeight / 2, plotX + plotWidth, plotY + plotHeight / 2); // x-axis
            line(plotX, plotY, plotX, plotY + plotHeight); // y-axis

            // Draw y-axis labels
            textSize(12);
            textAlign(RIGHT, CENTER);
            for (let y = -10; y <= 10; y += 5) {
                const yPos = map(y, 10, -10, plotY, plotY + plotHeight);
                text(y, plotX - 5, yPos);
            }

            // Draw x-axis labels
            textAlign(CENTER, CENTER);
            const groupLabels = ['W', 'X', 'Y', 'Z'];
            const groupWidth = plotWidth / 4;

            for (let i = 0; i < 4; i++) {
                text(groupLabels[i], plotX + groupWidth * (i + 0.5), plotY + plotHeight + 20);
            }

            // Draw grand mean line
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            const grandMeanY = map(grandMean, 10, -10, plotY, plotY + plotHeight);
            line(plotX, grandMeanY, plotX + plotWidth, grandMeanY);
            drawingContext.setLineDash([]);

            // Draw group bars
            fill('#F57F29');
            noStroke();

            for (let i = 0; i < 4; i++) {
                const groupCenterX = plotX + groupWidth * (i + 0.5);
                const meanY = map(groupMeans[i], 10, -10, plotY, plotY + plotHeight);
                const topY = map(groupMeans[i] - groupSDs[i], 10, -10, plotY, plotY + plotHeight);
                const bottomY = map(groupMeans[i] + groupSDs[i], 10, -10, plotY, plotY + plotHeight);

                // Draw the SD bar
                rect(groupCenterX - 20, topY, 40, bottomY - topY);

                // Draw the mean line
                stroke(0);
                strokeWeight(2);
                line(groupCenterX - 20, meanY, groupCenterX + 20, meanY);
                noStroke();
            }

            // Draw F-ratio text
            textSize(16);
            textAlign(LEFT, TOP);
            text(`F ratio = ${fRatio.toFixed(4)}`, plotX + 10, plotY + 10);
        }

        function drawANOVAResultsPlot() {
            const plotHeight = height * 0.3;
            const plotY = height * 0.65;
            const plotWidth = width - 40;
            const plotX = 20;

            // Draw title
            textSize(16);
            textAlign(LEFT, TOP);
            text("Analysis of the F-Ratio", plotX, plotY - 20);

            // Draw x-axis
            stroke(0);
            strokeWeight(1);
            line(plotX, plotY + plotHeight, plotX + plotWidth, plotY + plotHeight);

            // Draw x-axis labels
            textSize(12);
            textAlign(CENTER, TOP);
            for (let x = 0; x <= 200; x += 50) {
                const xPos = map(x, 0, 200, plotX, plotX + plotWidth);
                text(x, xPos, plotY + plotHeight + 5);
                line(xPos, plotY + plotHeight, xPos, plotY + plotHeight + 5);
            }

            // Prepare data for the bars
            const stats = [
                { label: "MS Between", value: msBetween, color: '#4691A5' },
                { label: "MS Within", value: msWithin, color: '#D49D3C' },
                { label: "F-Ratio", value: fRatio, color: '#8C3A8D' }
            ];

            const barHeight = 30;
            const barSpacing = 40;

            // Draw bars and labels
            textAlign(LEFT, CENTER);
            for (let i = 0; i < stats.length; i++) {
                const barY = plotY + i * barSpacing;

                // Draw bar
                fill(stats[i].color);
                const barWidth = map(Math.min(stats[i].value, 200), 0, 200, 0, plotWidth);
                rect(plotX, barY, barWidth, barHeight);

                // Draw label
                fill(0);
                noStroke();
                text(stats[i].label, plotX - 10, barY + barHeight / 2);

                // Draw value if there's space
                if (barWidth > 60) {
                    fill(255);
                    text(stats[i].value.toFixed(2), plotX + 5, barY + barHeight / 2);
                }
            }
        }
    </script>
</body>
</html>
