        document.getElementById('display-sigma2').value = document.getElementById('slider-sigma2').value;
        document.getElementById('display-xi2').value = document.getElementById('slider-xi2').value;
        document.getElementById('display-pmax').value = document.getElementById('slider-pmax').value;

        // Add event listeners to all sliders
        const sliders = document.querySelectorAll('input[type="range"]');
        sliders.forEach(slider => {
            slider.addEventListener('input', function() {
                // Update the corresponding display value
                const displayId = 'display-' + this.id.split('-')[1];
                document.getElementById(displayId).value = this.value;

                // Update the plots
                updatePlots();
            });
        });

        // Function to calculate GEV PDF
        function gevPDF(x, mu, sigma, xi) {
            if (xi === 0) {
                // Gumbel case
                const z = (x - mu) / sigma;
                return (1/sigma) * Math.exp(-z - Math.exp(-z));
            } else {
                // General case
                const z = 1 + xi * (x - mu) / sigma;
                if (z <= 0) return 0; // Outside support
                const t = Math.pow(z, -1/xi);
                return (1/sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
            }
        }

        // Function to calculate GEV return level
        function gevReturnLevel(p, mu, sigma, xi) {
            if (xi === 0) {
                // Gumbel case
                return mu - sigma * Math.log(-Math.log(1 - 1/p));
            } else {
                // General case
                return mu - (sigma/xi) * (1 - Math.pow(-Math.log(1 - 1/p), -xi));
            }
        }

        // Function to generate PDF data
        function generatePDFData(mu, sigma, xi) {
            const xValues = [];
            const yValues = [];

            // Generate points from mu - 5*sigma to mu + 5*sigma (adjust as needed)
            const minX = 2.5;
            const maxX = 7;
            const step = (maxX - minX) / 100;

            for (let x = minX; x <= maxX; x += step) {
                xValues.push(x);
                yValues.push(gevPDF(x, mu, sigma, xi));
            }

            return {x: xValues, y: yValues};
        }

        // Function to generate return level data
        function generateReturnLevelData(pMax, mu, sigma, xi) {
            const pValues = [];
            const yValues = [];

            // Generate points from 1 to pMax
            const step = pMax / 100;

            for (let p = 1; p <= pMax; p += step) {
                pValues.push(p);
                yValues.push(gevReturnLevel(p, mu, sigma, xi));
            }

            return {x: pValues, y: yValues};
        }

