                y: document.getElementById('check-y'),
                z: document.getElementById('check-z'),
                w: document.getElementById('check-w')
            },
            plots: {
                top: document.getElementById('plot-top'),
                bottom: document.getElementById('plot-bottom')
            }
        };

        // Initialize plots
        let topPlot, bottomPlot;

        // Event listeners
        function setupEventListeners() {
            // Signal buttons
            Object.keys(elements.buttons).forEach(key => {
                elements.buttons[key].addEventListener('click', () => {
                    state.signal = key;
                    updateActiveButton();
                    updatePlots();
                });
            });

            // Sliders
            elements.sliders.a.addEventListener('input', () => {
                state.a = parseFloat(elements.sliders.a.value);
                state.T = state.b / state.a;
                updateSliderValues();
                updatePlots();
            });

            elements.sliders.b.addEventListener('input', () => {
                state.b = parseFloat(elements.sliders.b.value);
                state.T = state.b / state.a;
                updateSliderValues();
                updatePlots();
            });

            elements.sliders.t.addEventListener('input', () => {
                state.T = parseFloat(elements.sliders.t.value);
                state.b = state.a * state.T;
                updateSliderValues();
                updatePlots();
            });

            // Checkboxes
            Object.keys(elements.checkboxes).forEach(key => {
                elements.checkboxes[key].addEventListener('change', () => {
                    state.display[key] = elements.checkboxes[key].checked;
                    updatePlotVisibility();
                });
            });
        }

        // Update active button
        function updateActiveButton() {
            Object.keys(elements.buttons).forEach(key => {
                if (key === state.signal) {
                    elements.buttons[key].classList.add('active');
                } else {
                    elements.buttons[key].classList.remove('active');
                }
            });
        }

        // Update slider values
        function updateSliderValues() {
            elements.sliders.a.value = state.a;
            elements.sliders.b.value = state.b;
            elements.sliders.t.value = state.T;

            elements.sliderValues.a.textContent = state.a.toFixed(1);
            elements.sliderValues.b.textContent = state.b.toFixed(1);
            elements.sliderValues.t.textContent = state.T.toFixed(1);
        }

        // Generate interpolated signal data
        function generateSignal(signalName, tValues) {
            const signalPoints = signals[signalName];
            const result = [];

            for (let i = 0; i < tValues.length; i++) {
                const t = tValues[i];

                // Find the segment that contains this t value
                let segmentIndex = 0;
                while (segmentIndex < signalPoints.length - 1 && signalPoints[segmentIndex + 1][0] < t) {
                    segmentIndex++;
                }

                if (segmentIndex >= signalPoints.length - 1) {
                    // Beyond the last point
                    result.push(0);
                } else {
                    const [t0, y0] = signalPoints[segmentIndex];
                    const [t1, y1] = signalPoints[segmentIndex + 1];

                    if (t0 === t1) {
                        // Vertical segment (discontinuity)
                        result.push(y1);
                    } else {
                        // Linear interpolation
                        const y = y0 + (y1 - y0) * (t - t0) / (t1 - t0);
                        result.push(y);
                    }
                }
            }

            return result;
        }

        // Generate transformed signals
        function generateTransformedSignals(tValues) {
            const x = generateSignal(state.signal, tValues);

            const y = generateSignal(state.signal, tValues.map(t => state.a * t - state.b));
            const z = generateSignal(state.signal, tValues.map(t => t - state.b));
            const w = generateSignal(state.signal, tValues.map(t => state.a * t));

            return { x, y, z, w };
        }

        // Create plot data
        function createPlotData(tValues, signals) {
            return [
                { // x(t)
                    x: tValues,
                    y: signals.x,
                    mode: 'lines',
                    line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                    name: '\\\(x(t)\\\\)',
                    visible: true
                },
                { // y(t)
                    x: tValues,
                    y: signals.y,
                    mode: 'lines',
                    line: { color: 'red', width: 2.5 },
                    name: '\\\(y(t) = x(at - b) = x(a(t-T))\\\\)',
                    visible: state.display.y
                },
                { // z(t)
                    x: tValues,
                    y: signals.z,
                    mode: 'lines',
                    line: { color: 'olivedrab', width: 2.5 },
                    name: '\\\(z(t) = x(t - b)\\\\)',
                    visible: state.display.z
                },
                { // w(t)
                    x: tValues,
                    y: signals.w,
                    mode: 'lines',
                    line: { color: 'saddlebrown', width: 2.5 },
                    name: '\\\(w(t) = x(at)\\\\)',
                    visible: state.display.w
                }
            ];
        }

        // Create plot layout
        function createPlotLayout(title, showLegend = true) {
            return {
                title: title,
                xaxis: {
                    title: 'time (s)',
                    range: [-10, 20],
                    zeroline: true
                },
                yaxis: {
                    range: [-0.5, 4.5],
                    zeroline: true
                },
                showlegend: showLegend,
                legend: {
                    x: 0.01,
                    y: showLegend ? 0.98 : 0.75
                },
                shapes: [
                    { // T marker
                        type: 'circle',
                        xref: 'x',
                        yref: 'y',
                        x0: state.T - 0.1,
                        y0: -0.1,
                        x1: state.T + 0.1,
                        y1: 0.1,
                        fillcolor: 'red',
                        line: { color: 'red' }
                    },
                    { // b marker
                        type: 'circle',
                        xref: 'x',
                        yref: 'y',
                        x0: state.b - 0.1,
                        y0: -0.1,
                        x1: state.b + 0.1,
                        y1: 0.1,
                        fillcolor: 'green',
                        line: { color: 'green' }
                    }
                ],
                annotations: [
                    { // T label
                        x: state.T,
                        y: 0.3,
                        xref: 'x',
                        yref: 'y',
                        text: 'T',
                        showarrow: false,
                        font: { color: 'red', size: 14 }
                    },
                    { // b label
                        x: state.b,
                        y: 0.3,
                        xref: 'x',
                        yref: 'y',
                        text: 'b',
                        showarrow: false,
                        font: { color: 'green', size: 14 }
                    }
                ]
            };
        }

        // Update plots
        function updatePlots() {
            // Generate time values
            const tValues = [];
            for (let t = -10; t <= 20; t += 0.1) {
                tValues.push(t);
            }

            // Generate signals
            const signals = generateTransformedSignals(tValues);

            // Update top plot (only x(t))
            const topData = [{
                x: tValues,
                y: signals.x,
                mode: 'lines',
                line: { color: 'rgb(70, 130, 180)', width: 2.5 },
                name: '\\\(x(t)\\\\)'
            }];

            const topLayout = createPlotLayout('signal', false);

            if (!topPlot) {
                topPlot = Plotly.newPlot(elements.plots.top, topData, topLayout);
            } else {
                Plotly.react(elements.plots.top, topData, topLayout);
            }

            // Update bottom plot (all signals)
            const bottomData = createPlotData(tValues, signals);
            const bottomLayout = createPlotLayout('');

            if (!bottomPlot) {
                bottomPlot = Plotly.newPlot(elements.plots.bottom, bottomData, bottomLayout);
            } else {
                Plotly.react(elements.plots.bottom, bottomData, bottomLayout);
            }
        }

        // Update plot visibility based on checkboxes
        function updatePlotVisibility() {
            if (!bottomPlot) return;

            const visibility = [
                state.display.x,
                state.display.y,
                state.display.z,
                state.display.w
            ];

            Plotly.restyle(elements.plots.bottom, { visible: visibility }, [0, 1, 2, 3]);
        }

        // Initialize
        function init() {
            updateActiveButton();
            updateSliderValues();

            // Set checkbox states
            elements.checkboxes.x.checked = state.display.x;
            elements.checkboxes.y.checked = state.display.y;
            elements.checkboxes.z.checked = state.display.z;
            elements.checkboxes.w.checked = state.display.w;

            setupEventListeners();
            updatePlots();
        }

        // Start the application
        window.addEventListener('load', init);
    </script>
</body>
</html>
