    <script>
        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        // Camera setup
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(0, 0, 5);

        // Renderer setup
        const canvas = document.getElementById('main-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.setSize(window.innerWidth, window.innerHeight);

        // Lighting
        const hemisphereLight = new THREE.HemisphereLight(0xffffff, 0x444444, 0.6);
        scene.add(hemisphereLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight.position.set(1, 1, 1);
        scene.add(directionalLight);

        // Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Main group for all generated objects
        const mainGroup = new THREE.Group();
        scene.add(mainGroup);

        // State variables
        let currentUnitsX = 2;
        let currentUnitsY = 2;
        let currentTrim = 0.8;
        let showDouble = false;
        let showSphere = false;
        let showTriangles = false;

        // Event listeners
        document.querySelectorAll('input[name="unitsX"]').forEach(radio => {
            radio.addEventListener('change', function() {
                currentUnitsX = parseInt(this.value);
                updateScene();
            });
        });

        document.querySelectorAll('input[name="unitsY"]').forEach(radio => {
            radio.addEventListener('change', function() {
                currentUnitsY = parseInt(this.value);
                updateScene();
            });
        });

        const trimSlider = document.getElementById('slider-trim');
        const trimValueLabel = document.getElementById('label-trim-value');

        trimSlider.addEventListener('input', function() {
            currentTrim = parseFloat(this.value);
            trimValueLabel.textContent = currentTrim.toFixed(3);
            updateScene();
        });

        document.getElementById('checkbox-double').addEventListener('change', function() {
            showDouble = this.checked;
            updateScene();
        });

        document.getElementById('checkbox-sphere').addEventListener('change', function() {
            showSphere = this.checked;
            updateScene();
        });

        document.getElementById('checkbox-triangles').addEventListener('change', function() {
            showTriangles = this.checked;
            updateScene();
        });

        document.getElementById('btn-reset-view').addEventListener('click', function() {
            controls.reset();
        });

        // Window resize handler
        window.addEventListener('resize', function() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        // Helper function to create curved triangle geometry
        function createCurvedTriangleGeometry(trim, color1, color2) {
            const geometry = new THREE.BufferGeometry();

            // Parameters
            const radius = 1;
            const segments = 32;
            const center = new THREE.Vector3(0, 0, 0);

            // Create vertices for three points on a sphere (equilateral triangle)
            const angleStep = (2 * Math.PI) / 3;
            const vertices = [];
            const colors = [];
            const indices = [];

            // Main vertices
            const mainVertices = [];
            for (let i = 0; i < 3; i++) {
                const angle = i * angleStep - Math.PI / 2;
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = 0;
                mainVertices.push(new THREE.Vector3(x, y, z));
            }

            // Project vertices onto sphere
            mainVertices.forEach(v => v.normalize().multiplyScalar(radius));

            // Create center vertex (slightly raised)
            const centerVertex = center.clone().normalize().multiplyScalar(radius * 0.9);
            vertices.push(centerVertex.x, centerVertex.y, centerVertex.z);
            colors.push(color1.r, color1.g, color1.b);

            // Create curved edges
            for (let i = 0; i < 3; i++) {
                const v1 = mainVertices[i];
                const v2 = mainVertices[(i + 1) % 3];

                // Calculate control point for the circular arc
                const midPoint = new THREE.Vector3().addVectors(v1, v2).normalize().multiplyScalar(radius * trim);

                // Generate points along the curve
                for (let j = 0; j <= segments; j++) {
                    const t = j / segments;
                    const point = quadraticBezier(v1, midPoint, v2, t);
                    vertices.push(point.x, point.y, point.z);

                    // Color gradient from center to edge
                    const color = new THREE.Color().lerpColors(color1, color2, t);
                    colors.push(color.r, color.g, color.b);

                    // Create triangle fan indices
                    if (j < segments) {
                        indices.push(0, 1 + i * (segments + 1) + j, 1 + i * (segments + 1) + j + 1);
                    }
                }
            }

            // Set geometry attributes
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
            geometry.setIndex(indices);

            // Compute normals for lighting
            geometry.computeVertexNormals();

            return geometry;
        }

        // Quadratic Bezier curve helper
        function quadraticBezier(p0, p1, p2, t) {
            const x = Math.pow(1 - t, 2) * p0.x + 2 * (1 - t) * t * p1.x + Math.pow(t, 2) * p2.x;
            const y = Math.pow(1 - t, 2) * p0.y + 2 * (1 - t) * t * p1.y + Math.pow(t, 2) * p2.y;
            const z = Math.pow(1 - t, 2) * p0.z + 2 * (1 - t) * t * p1.z + Math.pow(t, 2) * p2.z;
            return new THREE.Vector3(x, y, z);
        }

        // Create outline geometry
        function createOutlineGeometry(geometry) {
            const edges = new THREE.EdgesGeometry(geometry);
            const outline = new THREE.LineSegments(
                edges,
                new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 2 })
            );
            return outline;
        }

        // Create flat triangle geometry
        function createFlatTriangleGeometry() {
            const geometry = new THREE.BufferGeometry();

            // Create vertices for equilateral triangle
            const radius = 1;
            const angleStep = (2 * Math.PI) / 3;
            const vertices = [];

            for (let i = 0; i < 3; i++) {
                const angle = i * angleStep - Math.PI / 2;
                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                vertices.push(x, y, 0);
            }

            // Create indices
            const indices = [0, 1, 2];

            // Set geometry attributes
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            geometry.setIndex(indices);

            return geometry;
        }

        // Update the scene based on current state
        function updateScene() {
            // Clear previous objects
            while (mainGroup.children.length > 0) {
                mainGroup.remove(mainGroup.children[0]);
            }

            // Colors
            const primaryColor1 = new THREE.Color(0xffc0cb); // light pink
            const primaryColor2 = new THREE.Color(0x904090); // dark magenta
            const secondaryColor1 = new THREE.Color(0x80ffff); // light cyan
            const secondaryColor2 = new THREE.Color(0x008080); // dark teal

            // Create primary lattice
            for (let x = -currentUnitsX; x <= currentUnitsX; x++) {
                for (let y = -currentUnitsY; y <= currentUnitsY; y++) {
                    // Skip if both x and y are odd or even (hexagonal grid pattern)
                    if ((x + y) % 2 !== 0) continue;

                    // Calculate position
                    const posX = x * 1.8;
                    const posY = y * 1.5;

                    // Create curved triangle
                    const geometry = createCurvedTriangleGeometry(currentTrim, primaryColor1, primaryColor2);
                    const material = new THREE.MeshStandardMaterial({
                        vertexColors: true,
                        side: THREE.DoubleSide,
                        flatShading: true
                    });

                    const mesh = new THREE.Mesh(geometry, material);
                    mesh.position.set(posX, posY, 0);

                    // Rotate every other triangle
                    if ((x + y) % 4 === 0) {
                        mesh.rotation.z = Math.PI;
                    }

                    mainGroup.add(mesh);

                    // Add outline
                    const outline = createOutlineGeometry(geometry);
                    outline.position.set(posX, posY, 0);
                    outline.rotation.copy(mesh.rotation);
                    mainGroup.add(outline);

                    // Add flat triangles if enabled
                    if (showTriangles) {
                        const flatGeometry = createFlatTriangleGeometry();
                        const flatMaterial = new THREE.MeshBasicMaterial({
                            color: 0x800080,
                            transparent: true,
                            opacity: 0.4,
                            side: THREE.DoubleSide
                        });

                        const flatMesh = new THREE.Mesh(flatGeometry, flatMaterial);
                        flatMesh.position.set(posX, posY, 0);
                        flatMesh.rotation.copy(mesh.rotation);
                        mainGroup.add(flatMesh);

                        // Add points at vertices
                        const pointsGeometry = new THREE.BufferGeometry();
                        pointsGeometry.setAttribute('position', flatGeometry.getAttribute('position'));
                        const pointsMaterial = new THREE.PointsMaterial({
                            color: 0x000000,
                            size: 3,
                            sizeAttenuation: false
                        });

                        const points = new THREE.Points(pointsGeometry, pointsMaterial);
                        points.position.set(posX, posY, 0);
                        mainGroup.add(points);
                    }
                }
            }

            // Create secondary lattice if enabled
            if (showDouble) {
                for (let x = -currentUnitsX; x <= currentUnitsX; x++) {
                    for (let y = -currentUnitsY; y <= currentUnitsY; y++) {
                        // Skip if both x and y are odd or even (hexagonal grid pattern)
                        if ((x + y) % 2 !== 0) continue;

                        // Calculate position (offset from primary lattice)
                        const posX = x * 1.8 + 0.9;
                        const posY = y * 1.5 + 0.75;

                        // Create curved triangle
                        const geometry = createCurvedTriangleGeometry(currentTrim, secondaryColor1, secondaryColor2);
                        const material = new THREE.MeshStandardMaterial({
                            vertexColors: true,
                            side: THREE.DoubleSide,
                            flatShading: true
                        });

                        const mesh = new THREE.Mesh(geometry, material);
                        mesh.position.set(posX, posY, 0);

                        // Rotate every other triangle (opposite to primary)
                        if ((x + y) % 4 !== 0) {
                            mesh.rotation.z = Math.PI;
                        }

                        mainGroup.add(mesh);

                        // Add outline
                        const outline = createOutlineGeometry(geometry);
                        outline.position.set(posX, posY, 0);
                        outline.rotation.copy(mesh.rotation);
                        mainGroup.add(outline);
                    }
                }
            }

            // Add reference sphere if enabled
            if (showSphere) {
                const sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
                const sphereMaterial = new THREE.MeshPhongMaterial({
                    color: 0xdddddd,
                    shininess: 80,
                    transparent: true,
                    opacity: 0.7
                });

                const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
                sphere.position.set(0, 0, 0);
                mainGroup.add(sphere);
            }
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize
        updateScene();
        animate();
    </script>
</body>
