        }

        .button-group button.active {
            background-color: #ccc;
            border-style: inset;
        }

        #canvas-container {
            background-color: white;
            border: 1px solid #ccc;
            border-radius: 5px;
            padding: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <button id="info-button">+</button>

        <div id="description-panel" style="display: none;">
            <p>This interactive visualization demonstrates time encoding of analog signals using different algorithms. The blue curve represents the original analog signal, while the red elements show the encoded output. Adjust the time window using the sliders, select different input functions, and compare the ASDM and IAF encoding methods.</p>
        </div>

        <h1>Time Encoding Machine Visualization</h1>

        <div id="control-panel">
            <div class="control-row">
                <label for="slider-start-time">start time</label>
                <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1.0">
                <span id="start-time-value">-1.0</span>
            </div>

            <div class="control-row">
                <label for="slider-total-time">total time</label>
                <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
                <span id="total-time-value">2.2</span>
            </div>

            <div class="control-row">
                <label>function</label>
                <div id="function-buttons" class="button-group">
                    <button id="btn-f">f</button>
                    <button id="btn-g" class="active">g</button>
                    <button id="btn-sin">sin</button>
                    <button id="btn-cos">cos</button>
                    <button id="btn-tan">tan</button>
                </div>
            </div>

            <div class="control-row">
                <label>encoding method</label>
                <div id="encoding-buttons" class="button-group">
                    <button id="btn-asdm" class="active">ASDM</button>
                    <button id="btn-iaf">IAF</button>
                </div>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // Global variables
        let startTime = -1.0;
        let totalTime = 2.2;
        let currentFunction = 'g';
        let encodingMethod = 'ASDM';
        let canvasWidth = 800;
        let canvasHeight = 500;
        let margin = 50;
        let p5Canvas;

        // Function definitions
        const functions = {
            f: t => 0.5 * t,
            g: t => Math.sin(2 * Math.PI * t) + 0.3 * Math.cos(3 * 2 * Math.PI * t),
            sin: t => Math.sin(2 * Math.PI * t),
            cos: t => Math.cos(2 * Math.PI * t),
            tan: t => Math.tan(Math.PI * t)
        };

        // ASDM simulation
        function simulateASDM(func, tStart, tEnd) {
            const B = 0.25;
            const delta = 0.05;
            const dt = 0.001;

            let outputLevel = B;
            let integral = 0;
            let currentTime = tStart;
            let flipTimes = [];

            while (currentTime < tEnd) {
                const input = func(currentTime);
                integral += (input - outputLevel) * dt;

                if ((outputLevel === B && integral < -delta) || 
                    (outputLevel === -B && integral > delta)) {
                    flipTimes.push(currentTime);
                    outputLevel = -outputLevel;
                    integral = 0;
                }

                currentTime += dt;
            }

            return flipTimes;
        }

        // IAF simulation
        function simulateIAF(func, tStart, tEnd) {
            const theta = 0.05;
            const dt = 0.001;

            let integral = 0;
            let currentTime = tStart;
            let spikeTimes = [];

            while (currentTime < tEnd) {
                const input = func(currentTime);
                integral += input * dt;

                if (integral >= theta) {
                    spikeTimes.push(currentTime);
                    integral = 0;
                }

                currentTime += dt;
            }

            return spikeTimes;
        }

        // Setup p5.js sketch
        function sketch(p) {
            p.setup = function() {
                pCanvas = p.createCanvas(canvasWidth, canvasHeight);
                pCanvas.parent('canvas-container');
                p.noLoop();
                drawVisualization(p);
            };

            p.draw = function() {
                drawVisualization(p);
            };
        }

        // Main drawing function
        function drawVisualization(p) {
            p.background(255);
            drawAxes(p);
            drawAnalogSignal(p);
            drawEncodedSignal(p);
        }

        // Draw coordinate axes
        function drawAxes(p) {
            p.stroke(0);
            p.strokeWeight(1);

            // X axis
            p.line(margin, canvasHeight - margin, canvasWidth - margin, canvasHeight - margin);

            // Y axis
            p.line(margin, margin, margin, canvasHeight - margin);

            // X ticks and labels
            const xMin = startTime;
            const xMax = startTime + totalTime;
            const xRange = xMax - xMin;

            for (let t = Math.ceil(xMin * 2) / 2; t <= xMax; t += 0.5) {
                if (t >= xMin && t <= xMax) {
                    const x = margin + ((t - xMin) / xRange) * (canvasWidth - 2 * margin);
                    p.line(x, canvasHeight - margin - 5, x, canvasHeight - margin + 5);
                    p.textAlign(p.CENTER, p.TOP);
                    p.text(t.toFixed(1), x, canvasHeight - margin + 10);
                }
            }

            // Y ticks and labels
            for (let y = -1; y <= 1; y += 0.5) {
                const yPos = p.map(y, 1.2, -1.2, margin, canvasHeight - margin);
                p.line(margin - 5, yPos, margin + 5, yPos);
                p.textAlign(p.RIGHT, p.CENTER);
                p.text(y.toFixed(1), margin - 10, yPos);
            }

            // Axis labels
            p.textAlign(p.CENTER, p.CENTER);
            p.text("time (t)", canvasWidth / 2, canvasHeight - margin / 2);
            p.push();
            p.translate(margin / 2, canvasHeight / 2);
            p.rotate(-p.HALF_PI);
            p.text("signal", 0, 0);
            p.pop();
        }

        // Draw analog signal
        function drawAnalogSignal(p) {
            p.stroke(0, 0, 255);
            p.strokeWeight(2);
            p.noFill();

            const xMin = startTime;
            const xMax = startTime + totalTime;
            const xRange = xMax - xMin;
            const func = functions[currentFunction];

            let prevX = margin;
            let prevY = p.map(func(xMin), 1.2, -1.2, margin, canvasHeight - margin);

            // For tan function, we need to handle discontinuities
            if (currentFunction === 'tan') {
                let drawing = true;
                for (let t = xMin + 0.01; t <= xMax; t += 0.01) {
                    const x = margin + ((t - xMin) / xRange) * (canvasWidth - 2 * margin);
                    let yValue;

                    try {
                        yValue = func(t);
                        if (Math.abs(yValue) > 10) {  // Approaching asymptote
                            drawing = false;
                            continue;
                        }

                        const y = p.map(yValue, 1.2, -1.2, margin, canvasHeight - margin);

                        if (drawing) {
                            p.line(prevX, prevY, x, y);
                        } else {
                            drawing = true;
                        }

                        prevX = x;
                        prevY = y;
                    } catch (e) {
                        drawing = false;
                    }
                }
            } else {
                for (let t = xMin + 0.01; t <= xMax; t += 0.01) {
                    const x = margin + ((t - xMin) / xRange) * (canvasWidth - 2 * margin);
                    const y = p.map(func(t), 1.2, -1.2, margin, canvasHeight - margin);
                    p.line(prevX, prevY, x, y);
                    prevX = x;
                    prevY = y;
                }
            }
        }

        // Draw encoded signal
        function drawEncodedSignal(p) {
            p.stroke(255, 0, 0);
            p.strokeWeight(1);

            const xMin = startTime;
            const xMax = startTime + totalTime;
            const xRange = xMax - xMin;
            const func = functions[currentFunction];

            if (encodingMethod === 'ASDM') {
                const flipTimes = simulateASDM(func, xMin, xMax);

                if (flipTimes.length === 0) return;

                let currentLevel = 0.25;
                let prevX = margin;
                let prevY = p.map(-currentLevel, 1.2, -1.2, margin, canvasHeight - margin);

                for (let i = 0; i < flipTimes.length; i++) {
                    const flipTime = flipTimes[i];
                    const x = margin + ((flipTime - xMin) / xRange) * (canvasWidth - 2 * margin);
                    const y = p.map(-currentLevel, 1.2, -1.2, margin, canvasHeight - margin);

                    // Horizontal line
                    p.line(prevX, prevY, x, prevY);

                    // Vertical line
                    p.line(x, prevY, x, y);

                    prevX = x;
                    prevY = y;
                    currentLevel = -currentLevel;
                }

                // Draw last horizontal line
                p.line(prevX, prevY, canvasWidth - margin, prevY);
            } else { // IAF
                const spikeTimes = simulateIAF(func, xMin, xMax);

                for (let i = 0; i < spikeTimes.length; i++) {
                    const spikeTime = spikeTimes[i];
                    const x = margin + ((spikeTime - xMin) / xRange) * (canvasWidth - 2 * margin);
                    const ySpike = p.map(0.3, 1.2, -1.2, margin, canvasHeight - margin);
                    const yBase = p.map(0, 1.2, -1.2, margin, canvasHeight - margin);

                    // Vertical line
                    p.line(x, yBase, x, ySpike);

                    // Spike dot
                    p.fill(255, 0, 0);
                    p.noStroke();
                    p.ellipse(x, ySpike, 6, 6);
                    p.stroke(255, 0, 0);
                    p.noFill();
                }
            }
        }

        // Initialize p5.js
        new p5(sketch);

        // DOM event handlers
        document.getElementById('info-button').addEventListener('click', function() {
            const panel = document.getElementById('description-panel');
            panel.style.display = panel.style.display === 'none' ? 'block' : 'none';
        });

        document.getElementById('slider-start-time').addEventListener('input', function() {
            startTime = parseFloat(this.value);
            document.getElementById('start-time-value').textContent = startTime.toFixed(1);
            redraw();
        });

        document.getElementById('slider-total-time').addEventListener('input', function() {
            totalTime = parseFloat(this.value);
            document.getElementById('total-time-value').textContent = totalTime.toFixed(1);
            redraw();
        });

        // Function buttons
        const functionButtons = document.querySelectorAll('#function-buttons button');
        functionButtons.forEach(button => {
            button.addEventListener('click', function() {
                functionButtons.forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                currentFunction = this.id.replace('btn-', '');
                redraw();
            });
        });

        // Encoding method buttons
        const encodingButtons = document.querySelectorAll('#encoding-buttons button');
        encodingButtons.forEach(button => {
            button.addEventListener('click', function() {
                encodingButtons.forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                encodingMethod = this.id.replace('btn-', '');
                redraw();
            });
        });

        // Redraw function
        function redraw() {
            if (p5Canvas) {
                p5Canvas.draw();
            }
        }
    </script>
</body>
</html>
