    <script>
        // Three.js setup
        let scene, renderer, planeMesh, wireframe, perspectiveCamera, orthographicCamera, activeCamera;
        let width = window.innerWidth - 300;
        let height = window.innerHeight;

        // Simulation parameters
        let time = 0;
        let isPlaying = false;
        let animationId = null;

        // Wave parameters
        let pWaveType = 'sine';
        let pWaveAmplitude = 0.1;
        let pWaveWavelength = 2.0;

        let sWaveType = 'sine';
        let sWaveAmplitude = 0.1;
        let sWaveWavelength = 2.5;
        let sWaveDirection = 'xy';

        // Grid parameters
        let gridSize = 10;
        let plotPoints = 40;

        // Initialize the scene
        function init() {
            // Create scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xE0E0E0);

            // Create renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(width, height);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            // Create lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(10, 10, 10);
            scene.add(directionalLight);

            // Create cameras
            perspectiveCamera = new THREE.PerspectiveCamera(45, width / height, 0.1, 1000);
            perspectiveCamera.position.set(10, 10, 10);
            perspectiveCamera.lookAt(0, 0, 0);

            orthographicCamera = new THREE.OrthographicCamera(
                -gridSize/2, gridSize/2, gridSize/2, -gridSize/2, 0.1, 1000
            );
            orthographicCamera.position.set(0, 0, 10);
            orthographicCamera.lookAt(0, 0, 0);

            activeCamera = orthographicCamera;

            // Create grid
            createGrid();

            // Event listeners
            setupEventListeners();

            // Start animation loop
            animate();
        }

        // Create or recreate the grid
        function createGrid() {
            // Remove existing grid if it exists
            if (planeMesh) {
                scene.remove(planeMesh);
                scene.remove(wireframe);
            }

            // Create plane geometry
            const geometry = new THREE.PlaneBufferGeometry(gridSize, gridSize, plotPoints, plotPoints);

            // Create colored material
            const material = new THREE.MeshLambertMaterial({
                side: THREE.DoubleSide,
                vertexColors: true
            });

            // Create mesh
            planeMesh = new THREE.Mesh(geometry, material);
            planeMesh.rotation.x = -Math.PI / 2;
            scene.add(planeMesh);

            // Create wireframe
            const wireframeGeometry = new THREE.WireframeGeometry(geometry);
            wireframe = new THREE.LineSegments(wireframeGeometry, new THREE.LineBasicMaterial({ 
                color: 0x556B2F,
                transparent: true,
                opacity: 0.5
            }));
            wireframe.rotation.x = -Math.PI / 2;
            scene.add(wireframe);

            // Initialize vertex colors
            updateVertices();
        }

        // Update vertex positions and colors based on current parameters
        function updateVertices() {
            const positions = planeMesh.geometry.attributes.position;
            const colors = planeMesh.geometry.attributes.color;

            // Initialize colors if not present
            if (!colors) {
                const colorArray = new Float32Array(positions.count * 3);
                planeMesh.geometry.setAttribute('color', new THREE.BufferAttribute(colorArray, 3));
            }

            for (let i = 0; i < positions.count; i++) {
                const x = positions.getX(i);
                const y = positions.getY(i);
                const r = Math.sqrt(x * x + y * y);

                // Calculate P-wave displacement
                let pDisplacement = 0;
                const pPhase = (r / pWaveWavelength) - time;

                if (pWaveType === 'sine') {
                    pDisplacement = pWaveAmplitude * Math.sin(2 * Math.PI * pPhase);
                } else { // pulse
                    pDisplacement = pWaveAmplitude * Math.exp(-pPhase * pPhase * 4);
                }

                // Calculate S-wave displacement
                let sDisplacement = 0;
                const sPhase = (r / sWaveWavelength) - time;

                if (sWaveType === 'sine') {
                    sDisplacement = sWaveAmplitude * Math.sin(2 * Math.PI * sPhase);
                } else { // pulse
                    sDisplacement = sWaveAmplitude * Math.exp(-sPhase * sPhase * 4);
                }

                // Calculate final position
                let newX = x;
                let newY = y;
                let newZ = 0;

                if (r > 0.001) { // Avoid division by zero at center
                    // P-wave displacement (radial direction)
                    newX += pDisplacement * (x / r);
                    newY += pDisplacement * (y / r);

                    // S-wave displacement
                    if (sWaveDirection === 'xy') {
                        // Perpendicular to radial direction
                        newX += sDisplacement * (-y / r);
                        newY += sDisplacement * (x / r);
                    } else { // z direction
                        newZ += sDisplacement;
                    }
                }

                // Set new position
                positions.setXYZ(i, newX, newY, newZ);

                // Set color based on z displacement
                const normalizedZ = Math.min(Math.max(newZ / 0.3, -1), 1);
                if (normalizedZ > 0) {
                    // Red gradient for positive z
                    colors.setXYZ(i, 1, 1 - normalizedZ, 1 - normalizedZ);
                } else {
                    // Blue gradient for negative z
                    colors.setXYZ(i, 1 + normalizedZ, 1 + normalizedZ, 1);
                }
            }

            // Mark attributes as needing update
            positions.needsUpdate = true;
            planeMesh.geometry.attributes.color.needsUpdate = true;

            // Update wireframe to match new positions
            if (wireframe) {
                scene.remove(wireframe);
                const wireframeGeometry = new THREE.WireframeGeometry(planeMesh.geometry);
                wireframe = new THREE.LineSegments(wireframeGeometry, new THREE.LineBasicMaterial({ 
                    color: 0x556B2F,
                    transparent: true,
                    opacity: 0.5
                }));
                wireframe.rotation.x = -Math.PI / 2;
                scene.add(wireframe);
            }
        }

        // Animation loop
        function animate() {
            animationId = requestAnimationFrame(animate);

            if (isPlaying) {
                time += 0.02;
                if (time > 20) time = 0;
                document.getElementById('time-slider').value = time;
                updateVertices();
            }

            renderer.render(scene, activeCamera);
        }

        // Setup event listeners for UI controls
        function setupEventListeners() {
            // Time controls
            document.getElementById('play-pause-button').addEventListener('click', function() {
                isPlaying = !isPlaying;
                this.textContent = isPlaying ? '❚❚' : '►';
            });

            document.getElementById('reset-button').addEventListener('click', function() {
                time = 0;
                isPlaying = false;
                document.getElementById('play-pause-button').textContent = '►';
                document.getElementById('time-slider').value = time;
                updateVertices();
            });

            document.getElementById('step-button').addEventListener('click', function() {
                time += 0.1;
                if (time > 20) time = 0;
                document.getElementById('time-slider').value = time;
                updateVertices();
            });

            document.getElementById('time-slider').addEventListener('input', function() {
                time = parseFloat(this.value);
                updateVertices();
            });

            // Plot points control
            document.getElementById('plot-points-slider').addEventListener('input', function() {
                plotPoints = parseInt(this.value);
                createGrid();
            });

            // P-wave controls
            document.getElementById('p-wave-sine-button').addEventListener('click', function() {
                pWaveType = 'sine';
                this.classList.add('active');
                document.getElementById('p-wave-pulse-button').classList.remove('active');
                updateVertices();
            });

            document.getElementById('p-wave-pulse-button').addEventListener('click', function() {
                pWaveType = 'pulse';
                this.classList.add('active');
                document.getElementById('p-wave-sine-button').classList.remove('active');
                updateVertices();
            });

            document.getElementById('p-wave-amplitude-slider').addEventListener('input', function() {
                pWaveAmplitude = parseFloat(this.value);
                updateVertices();
            });

            document.getElementById('p-wave-wavelength-slider').addEventListener('input', function() {
                pWaveWavelength = parseFloat(this.value);
                updateVertices();
            });

            // S-wave controls
            document.getElementById('s-wave-sine-button').addEventListener('click', function() {
                sWaveType = 'sine';
                this.classList.add('active');
                document.getElementById('s-wave-pulse-button').classList.remove('active');
                updateVertices();
            });

            document.getElementById('s-wave-pulse-button').addEventListener('click', function() {
                sWaveType = 'pulse';
                this.classList.add('active');
                document.getElementById('s-wave-sine-button').classList.remove('active');
                updateVertices();
            });

            document.getElementById('s-wave-amplitude-slider').addEventListener('input', function() {
                sWaveAmplitude = parseFloat(this.value);
                updateVertices();
            });

            document.getElementById('s-wave-wavelength-slider').addEventListener('input', function() {
                sWaveWavelength = parseFloat(this.value);
                updateVertices();
            });

            document.getElementById('s-wave-dir-xy-button').addEventListener('click', function() {
                sWaveDirection = 'xy';
                this.classList.add('active');
                document.getElementById('s-wave-dir-z-button').classList.remove('active');
                updateVertices();
            });

            document.getElementById('s-wave-dir-z-button').addEventListener('click', function() {
                sWaveDirection = 'z';
                this.classList.add('active');
                document.getElementById('s-wave-dir-xy-button').classList.remove('active');
                updateVertices();
            });

            // Viewpoint controls
            document.getElementById('viewpoint-top-button').addEventListener('click', function() {
                activeCamera = orthographicCamera;
                this.classList.add('active');
                document.getElementById('viewpoint-default-button').classList.remove('active');
            });

            document.getElementById('viewpoint-default-button').addEventListener('click', function() {
                activeCamera = perspectiveCamera;
                this.classList.add('active');
                document.getElementById('viewpoint-top-button').classList.remove('active');
            });

            // Window resize handler
            window.addEventListener('resize', function() {
                width = window.innerWidth - 300;
                height = window.innerHeight;

                renderer.setSize(width, height);

                // Update perspective camera
                perspectiveCamera.aspect = width / height;
                perspectiveCamera.updateProjectionMatrix();

                // Update orthographic camera
                orthographicCamera.left = -gridSize/2;
                orthographicCamera.right = gridSize/2;
                orthographicCamera.top = gridSize/2 * (height/width);
                orthographicCamera.bottom = -gridSize/2 * (height/width);
                orthographicCamera.updateProjectionMatrix();
            });
        }

        // Start the application
        init();
    </script>
</body>
</html>
