    <!-- MathJax CDN script -->
    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <!-- p5.js CDN script -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>

    <script>
        // State variables
        let a = 1, b = -1, c = -1;
        let z = 1.5;
        let plotRange = 4;
        let showLabels = false;
        let showCircle = false;
        let showAxes = false;
        let showGridLines = false;

        // Points for the coefficient path
        let O, L1, L2, L3;
        // Points for the test path
        let L1_prime, L2_prime;

        // p5.js sketch
        let sketch = function(p) {
            let canvas;

            p.setup = function() {
                canvas = p.createCanvas(800, 400);
                canvas.parent('canvas-container');
                p.noLoop();
                updatePoints();
                updateDisplay();
            };

            p.draw = function() {
                p.background(255);

                // Set up coordinate system
                p.translate(p.width/2, p.height/2);
                p.scale(1, -1); // Flip Y-axis

                // Calculate scaling factor based on plot range
                const scaleFactor = Math.min(p.width, p.height) / (2 * plotRange);
                p.scale(scaleFactor, scaleFactor);

                // Calculate center of all points
                const allPoints = [O, L1, L2, L3, L1_prime, L2_prime];
                let minX = Infinity, maxX = -Infinity, minY = Infinity, maxY = -Infinity;

                allPoints.forEach(pt => {
                    minX = Math.min(minX, pt.x);
                    maxX = Math.max(maxX, pt.x);
                    minY = Math.min(minY, pt.y);
                    maxY = Math.max(maxY, pt.y);
                });

                // Center the view
                const centerX = (minX + maxX) / 2;
                const centerY = (minY + maxY) / 2;
                p.translate(-centerX, -centerY);

                // Draw grid lines if enabled
                if (showGridLines) {
                    p.stroke(220);
                    p.strokeWeight(1 / scaleFactor);

                    const gridStep = 1;
                    const startX = Math.floor(minX / gridStep) * gridStep;
                    const endX = Math.ceil(maxX / gridStep) * gridStep;
                    const startY = Math.floor(minY / gridStep) * gridStep;
                    const endY = Math.ceil(maxY / gridStep) * gridStep;

                    for (let x = startX; x <= endX; x += gridStep) {
                        p.line(x, minY, x, maxY);
                    }

                    for (let y = startY; y <= endY; y += gridStep) {
                        p.line(minX, y, maxX, y);
                    }
                }

                // Draw axes if enabled
                if (showAxes) {
                    p.stroke(0);
                    p.strokeWeight(2 / scaleFactor);
                    p.line(minX, 0, maxX, 0); // X-axis
                    p.line(0, minY, 0, maxY); // Y-axis

                    if (showLabels) {
                        p.push();
                        p.scale(1, -1); // Flip back for text
                        p.textSize(12 / scaleFactor);
                        p.fill(0);
                        p.noStroke();

                        // X-axis labels
                        const xStep = Math.max(1, Math.ceil((maxX - minX) / 10));
                        for (let x = Math.floor(minX / xStep) * xStep; x <= maxX; x += xStep) {
                            if (Math.abs(x) > 0.1) { // Skip 0 to avoid overlap
                                p.text(x.toFixed(1), x, 15 / scaleFactor);
                            }
                        }

                        // Y-axis labels
                        const yStep = Math.max(1, Math.ceil((maxY - minY) / 10));
                        for (let y = Math.floor(minY / yStep) * yStep; y <= maxY; y += yStep) {
                            if (Math.abs(y) > 0.1) { // Skip 0 to avoid overlap
                                p.text(y.toFixed(1), -25 / scaleFactor, -y);
                            }
                        }

                        p.pop();
                    }
                }

                // Draw circle if enabled
                if (showCircle) {
                    const centerX = (a - c) / 2;
                    const centerY = b / 2;
                    const diameter = Math.sqrt(Math.pow(a - c, 2) + Math.pow(b, 2));

                    p.stroke(0);
                    p.strokeWeight(1 / scaleFactor);
                    p.noFill();
                    p.circle(centerX, centerY, diameter);
                }

                // Draw coefficient path (O -> L1 -> L2 -> L3)
                p.stroke(0);
                p.strokeWeight(2 / scaleFactor);
                p.line(O.x, O.y, L1.x, L1.y);
                p.line(L1.x, L1.y, L2.x, L2.y);
                p.line(L2.x, L2.y, L3.x, L3.y);

                // Draw points
                p.fill(0);
                p.noStroke();
                [O, L1, L2, L3].forEach(pt => {
                    p.circle(pt.x, pt.y, 6 / scaleFactor);
                });

                // Draw test path (O -> L1' -> L2')
                p.stroke(0);
                p.line(O.x, O.y, L1_prime.x, L1_prime.y);

                p.stroke(255, 0, 255); // Magenta
                p.line(L1_prime.x, L1_prime.y, L2_prime.x, L2_prime.y);

                // Draw error segment (L2' -> L3)
                p.stroke(255, 0, 0); // Red
                p.line(L2_prime.x, L2_prime.y, L3.x, L3.y);

                // Draw test points
                p.fill(0);
                [L1_prime, L2_prime].forEach(pt => {
                    p.circle(pt.x, pt.y, 6 / scaleFactor);
                });

                // Draw labels if enabled
                if (showLabels) {
                    p.push();
                    p.scale(1, -1); // Flip back for text
                    p.textSize(14 / scaleFactor);
                    p.fill(0);
                    p.noStroke();

                    p.text("O", O.x + 8 / scaleFactor, -O.y);
                    p.text("L1", L1.x + 8 / scaleFactor, -L1.y);
                    p.text("L2", L2.x + 8 / scaleFactor, -L2.y);
                    p.text("L3", L3.x + 8 / scaleFactor, -L3.y);
                    p.text("L1'", L1_prime.x + 8 / scaleFactor, -L1_prime.y);
                    p.text("L2'", L2_prime.x + 8 / scaleFactor, -L2_prime.y);

                    p.pop();
                }
            };
        };

        // Create p5 instance
        new p5(sketch);

        // Update all points based on current coefficients and z value
        function updatePoints() {
            // Coefficient path points
            O = { x: 0, y: 0 };
            L1 = { x: a, y: 0 };
            L2 = { x: a, y: b };
            L3 = { x: a - c, y: b };

            // Test path points
            L1_prime = { x: a, y: -z * a };
            L2_prime = { x: a + b * z + a * z * z, y: b };
        }

        // Update all display elements
        function updateDisplay() {
            // Update slider labels
            document.getElementById('label-z').textContent = z.toFixed(2);
            document.getElementById('label-plot-range').textContent = plotRange.toFixed(1);

            // Calculate P(z) and error
            const pz = a * z * z + b * z + c;
            const error = Math.sqrt(Math.pow(L2_prime.x - L3.x, 2) + Math.pow(L2_prime.y - L3.y, 2));

            // Update results table
            document.getElementById('value-pz').textContent = pz.toFixed(4);
            document.getElementById('value-l3l2').textContent = error.toFixed(4);
            document.getElementById('value-z').textContent = z.toFixed(4);

            // Update formula display
            document.getElementById('formula-text').textContent = 
                `${a === 1 ? '' : a}${a === -1 ? '-' : ''}z^2 ${b >= 0 ? '+' : '-'} ${Math.abs(b)}z ${c >= 0 ? '+' : '-'} ${Math.abs(c)}`;

            // Redraw canvas
            sketch.redraw();

            // Update MathJax rendering
            if (window.MathJax) {
                MathJax.typesetPromise();
            }
        }

        // Event listeners
        document.getElementById('slider-z').addEventListener('input', function() {
            z = parseFloat(this.value);
            updatePoints();
            updateDisplay();
        });

        document.getElementById('slider-plot-range').addEventListener('input', function() {
            plotRange = parseFloat(this.value);
            updateDisplay();
        });

        document.getElementById('check-show-labels').addEventListener('change', function() {
            showLabels = this.checked;
            updateDisplay();
        });

        document.getElementById('check-show-circle').addEventListener('change', function() {
            showCircle = this.checked;
            updateDisplay();
        });

        document.getElementById('check-show-axes').addEventListener('change', function() {
            showAxes = this.checked;
            updateDisplay();
        });

        document.getElementById('check-show-grid-lines').addEventListener('change', function() {
            showGridLines = this.checked;
            updateDisplay();
        });

        document.getElementById('btn-new-example').addEventListener('click', function() {
            // Generate random coefficients
            a = Math.floor(Math.random() * 3 + 1) * (Math.random() < 0.5 ? -1 : 1);
            b = Math.floor(Math.random() * 11) - 5;
            c = Math.floor(Math.random() * 11) - 5;

            // Ensure a is not zero
            if (a === 0) a = 1;

            updatePoints();
            updateDisplay();
        });

        document.getElementById('btn-golden-ratio').addEventListener('click', function() {
            // Set golden ratio coefficients
            a = 1;
            b = -1;
            c = -1;

            updatePoints();
            updateDisplay();
        });
    </script>
</body>
</html>
