        function switchView(view) {
            state.currentView = view;
            updateView();

            // Update button states
            elements.btnPlot.classList.toggle('active', view === 'plot_solutions');
            elements.btnTable.classList.toggle('active', view === 'table');
            elements.btnTurningPoints.classList.toggle('active', view === 'plot_turning_points');
        }

        // Handle alpha parameter changes
        function handleAlphaChange(e) {
            state.currentAlpha = parseFloat(e.target.value);
            elements.alphaValueDisplay.textContent = state.currentAlpha.toFixed(4);
            updateView();
        }

        // Toggle info panel (placeholder)
        function toggleInfo() {
            alert("Information about this visualization will appear here.");
        }

        // Update the view based on current state
        function updateView() {
            // Show/hide parameter controls
            elements.parameterControls.style.display = 
                state.currentView === 'plot_turning_points' ? 'none' : 'flex';

            // Show/hide plot and table views
            elements.plotView.style.display = 
                state.currentView === 'table' ? 'none' : 'block';
            elements.tableView.style.display = 
                state.currentView === 'table' ? 'block' : 'none';

            // Update the table if needed
            if (state.currentView === 'table') {
                updateTable();
            }
        }

        // Update the solutions table
        function updateTable() {
            // Clear existing rows
            elements.tableBody.innerHTML = '';

            // Find solutions for current alpha (simplified for demo)
            // In a real app, you would interpolate between data points
            const solutions = solutionCurveData.filter(
                point => Math.abs(point.alpha - state.currentAlpha) < 0.001
            );

            if (solutions.length === 0) {
                // If no exact match, find the closest point
                const closest = solutionCurveData.reduce((prev, curr) => 
                    Math.abs(curr.alpha - state.currentAlpha) < Math.abs(prev.alpha - state.currentAlpha) ? curr : prev
                );
                solutions.push(closest);
            }

            // Add rows to the table
            solutions.forEach((solution, index) => {
                const row = document.createElement('tr');

                row.innerHTML = `
                    <td>${index + 1}</td>
                    <td>${solution.x1.toFixed(4)}</td>
                    <td>${solution.x2.toFixed(4)}</td>
                    <td>${solution.t1.toFixed(4)}</td>
                    <td>${solution.t2.toFixed(4)}</td>
                `;

                elements.tableBody.appendChild(row);
            });
        }

        // p5.js sketch
        function sketch(p) {
            let canvas;
            const margin = 50;
            let plotWidth, plotHeight;
            let xScale, yScale;

            p.setup = function() {
                plotWidth = p.min(700, p.windowWidth - 40);
                plotHeight = 500;
                canvas = p.createCanvas(plotWidth, plotHeight);
                canvas.parent('plot-view');

                calculateScales();
            };

            p.draw = function() {
                p.background(255);
                drawGridAndAxes();
                drawSolutionCurve();

                if (state.currentView === 'plot_solutions') {
                    drawAlphaLine();
                    drawIntersectionPoints();
                } else if (state.currentView === 'plot_turning_points') {
                    drawTurningPoints();
                }
            };

            function calculateScales() {
                plotWidth = p.width - margin * 2;
                plotHeight = p.height - margin * 2;

                xScale = plotWidth / 0.07;
                yScale = plotHeight / 8;
            }

            function drawGridAndAxes() {
                p.stroke(200);
                p.strokeWeight(1);

                // Draw grid lines
                for (let alpha = 0; alpha <= 0.07; alpha += 0.01) {
                    const x = margin + alpha * xScale;
                    p.line(x, margin, x, p.height - margin);
                }

                for (let t2 = 0; t2 <= 8; t2 += 1) {
                    const y = p.height - margin - t2 * yScale;
                    p.line(margin, y, p.width - margin, y);
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);

                // X-axis (alpha)
                p.line(margin, p.height - margin, p.width - margin, p.height - margin);

                // Y-axis (T2)
                p.line(margin, margin, margin, p.height - margin);

                // Axis labels
                p.textSize(14);
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER, p.CENTER);

                // X-axis ticks and labels
                for (let alpha = 0; alpha <= 0.07; alpha += 0.01) {
                    const x = margin + alpha * xScale;
                    p.line(x, p.height - margin, x, p.height - margin + 5);
                    p.text(alpha.toFixed(2), x, p.height - margin + 20);
                }

                // Y-axis ticks and labels
                for (let t2 = 0; t2 <= 8; t2 += 1) {
                    const y = p.height - margin - t2 * yScale;
                    p.line(margin - 5, y, margin, y);
                    p.text(t2.toString(), margin - 20, y);
                }

                // Axis titles
                p.textSize(16);
                p.text("α", p.width / 2, p.height - margin + 40);
                p.text("T₂(α)", margin - 40, p.height / 2);
            }

            function drawSolutionCurve() {
                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                p.noFill();

                p.beginShape();
                solutionCurveData.forEach(point => {
                    const x = margin + point.alpha * xScale;
                    const y = p.height - margin - point.t2 * yScale;
                    p.vertex(x, y);
                });
                p.endShape();
            }

            function drawAlphaLine() {
                p.stroke(0, 255, 0);
                p.strokeWeight(1);
                p.drawingContext.setLineDash([5, 5]);

                const x = margin + state.currentAlpha * xScale;
                p.line(x, margin, x, p.height - margin);

                p.drawingContext.setLineDash([]);
            }

            function drawIntersectionPoints() {
                // Find points around current alpha
                let prevPoint = null;
                let nextPoint = null;

                for (let i = 0; i < solutionCurveData.length - 1; i++) {
                    if (solutionCurveData[i].alpha <= state.currentAlpha && 
                        solutionCurveData[i + 1].alpha >= state.currentAlpha) {
                        prevPoint = solutionCurveData[i];
                        nextPoint = solutionCurveData[i + 1];
                        break;
                    }
                }

                if (prevPoint && nextPoint) {
                    // Interpolate to find T2 value at current alpha
                    const alphaRatio = (state.currentAlpha - prevPoint.alpha) / 
                                      (nextPoint.alpha - prevPoint.alpha);
                    const t2 = prevPoint.t2 + alphaRatio * (nextPoint.t2 - prevPoint.t2);

                    // Draw the intersection point
                    const x = margin + state.currentAlpha * xScale;
                    const y = p.height - margin - t2 * yScale;

                    p.fill(0, 0, 255);
                    p.noStroke();
                    p.ellipse(x, y, 10, 10);
                }
            }

            function drawTurningPoints() {
                p.fill(0, 255, 0);
                p.noStroke();

                turningPointsData.forEach(point => {
                    const x = margin + point.alpha * xScale;
                    const y = p.height - margin - point.t2 * yScale;
                    p.ellipse(x, y, 10, 10);
                });
            }

            p.windowResized = function() {
                plotWidth = p.min(700, p.windowWidth - 40);
                p.resizeCanvas(plotWidth, plotHeight);
                calculateScales();
            };
        }

        // Start the application when the page loads
        window.addEventListener('DOMContentLoaded', init);
    </script>
</body>
</html>
