    <script>
        // State variables
        let spiralType = 'double';
        let itemType = 'point';
        let spiralGrowth = 0.15;

        // Locator positions (complex numbers as {x, y} objects)
        let z0_loc = { x: 0, y: 0 };
        let z_plus_loc = { x: -100, y: 0 };
        let z_minus_loc = { x: 100, y: 0 };

        // Dragging state
        let isDragging = false;
        let draggedLocator = null;

        // Fox vertices (relative to origin)
        const fox_vertices = [
            {x:-15, y:0}, {x:-10, y:-5}, {x:-5, y:-4}, {x:0, y:-5}, 
            {x:5, y:-5}, {x:8, y:-2}, {x:10, y:-6}, {x:12, y:-6}, 
            {x:15, y:0}, {x:12, y:6}, {x:10, y:6}, {x:8, y:2}, 
            {x:5, y:5}, {x:0, y:5}, {x:-5, y:4}, {x:-10, y:5}
        ];

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');

            // Set up event listeners
            document.getElementById('btn-type-single').addEventListener('click', () => {
                setSpiralType('single');
            });

            document.getElementById('btn-type-double').addEventListener('click', () => {
                setSpiralType('double');
            });

            document.getElementById('btn-item-none').addEventListener('click', () => {
                setItemType('none');
            });

            document.getElementById('btn-item-point').addEventListener('click', () => {
                setItemType('point');
            });

            document.getElementById('btn-item-fox').addEventListener('click', () => {
                setItemType('fox');
            });

            document.getElementById('btn-item-filled-fox').addEventListener('click', () => {
                setItemType('filled-fox');
            });

            document.getElementById('slider-growth').addEventListener('input', (e) => {
                spiralGrowth = parseFloat(e.target.value);
                document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
                redraw();
            });

            document.getElementById('btn-reset-growth').addEventListener('click', () => {
                spiralGrowth = 0.25;
                document.getElementById('slider-growth').value = spiralGrowth;
                document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
                redraw();
            });

            // Canvas event listeners
            canvas.elt.addEventListener('mousedown', handleMouseDown);
            canvas.elt.addEventListener('mousemove', handleMouseMove);
            canvas.elt.addEventListener('mouseup', handleMouseUp);
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);

            // Draw the spiral(s)
            if (spiralType === 'single') {
                drawSingleSpiral();
            } else {
                drawDoubleSpiral();
            }

            // Draw locators
            drawLocators();
        }

        function drawSingleSpiral() {
            const k = spiralGrowth;
            const centerX = z0_loc.x;
            const centerY = z0_loc.y;

            // Draw the spiral curve
            noFill();
            stroke('#4A6DA8');
            strokeWeight(2);

            beginShape();
            for (let t = -8 * PI; t <= 8 * PI; t += 0.1) {
                const r = exp(k * t);
                const x = centerX + r * cos(t);
                const y = centerY - r * sin(t); // Negative y because canvas y increases downward
                curveVertex(x, y);
            }
            endShape();

            // Draw items along the curve
            if (itemType !== 'none') {
                for (let t = -8 * PI; t <= 8 * PI; t += 0.5) {
                    const r = exp(k * t);
                    const x = centerX + r * cos(t);
                    const y = centerY - r * sin(t);

                    if (itemType === 'point') {
                        fill('#CB3335');
                        noStroke();
                        circle(x, y, 5);
                    } else if (itemType === 'fox' || itemType === 'filled-fox') {
                        // Calculate tangent angle
                        const dx = k * r * cos(t) - r * sin(t);
                        const dy = -(k * r * sin(t) + r * cos(t));
                        const angle = atan2(dy, dx);

                        push();
                        translate(x, y);
                        rotate(angle);
                        scale(0.5);

                        if (itemType === 'fox') {
                            noFill();
                            stroke('#D6292D');
                            strokeWeight(1);
                        } else {
                            fill('#D6292D');
                            noStroke();
                        }

                        beginShape();
                        for (const v of fox_vertices) {
                            vertex(v.x, v.y);
                        }
                        endShape(CLOSE);
                        pop();
                    }
                }
            }
        }

        function drawDoubleSpiral() {
            const k = spiralGrowth;
            const p = z_plus_loc;
            const q = z_minus_loc;
            const epsilon = 0.01;

            // Draw the spiral curves
            noFill();
            stroke('#4A6DA8');
            strokeWeight(2);

            // First branch (negative t)
            beginShape();
            for (let t = -8 * PI; t <= -epsilon; t += 0.1) {
                const w = exp((k + 1) * t); // Simplified for demo
                const denominator = w - 1;
                if (abs(denominator) > 0.001) { // Avoid division by near-zero
                    const x = (p.x * w - q.x) / denominator;
                    const y = (p.y * w - q.y) / denominator;
                    curveVertex(x, y);
                }
            }
            endShape();

            // Second branch (positive t)
            beginShape();
            for (let t = epsilon; t <= 8 * PI; t += 0.1) {
                const w = exp((k + 1) * t); // Simplified for demo
                const denominator = w - 1;
                if (abs(denominator) > 0.001) { // Avoid division by near-zero
                    const x = (p.x * w - q.x) / denominator;
                    const y = (p.y * w - q.y) / denominator;
                    curveVertex(x, y);
                }
