            // Draw items along the curves
            if (itemType !== 'none') {
                // First branch items
                for (let t = -8 * PI; t <= -epsilon; t += 0.5) {
                    const w = exp((k + 1) * t);
                    const denominator = w - 1;
                    if (abs(denominator) > 0.001) {
                        const x = (p.x * w - q.x) / denominator;
                        const y = (p.y * w - q.y) / denominator;
                        drawItemAt(x, y, t, true);
                    }
                }

                // Second branch items
                for (let t = epsilon; t <= 8 * PI; t += 0.5) {
                    const w = exp((k + 1) * t);
                    const denominator = w - 1;
                    if (abs(denominator) > 0.001) {
                        const x = (p.x * w - q.x) / denominator;
                        const y = (p.y * w - q.y) / denominator;
                        drawItemAt(x, y, t, true);
                    }
                }
            }

            // Draw z0Loc label at (p+q)/2
            const z0X = (p.x + q.x) / 2;
            const z0Y = (p.y + q.y) / 2;
            textSize(12);
            textAlign(CENTER, CENTER);
            fill(0);
            noStroke();
            text("z0Loc", z0X, z0Y - 20);
        }

        function drawItemAt(x, y, t, isDoubleSpiral) {
            if (itemType === 'point') {
                fill('#CB3335');
                noStroke();
                circle(x, y, 5);
            } else if (itemType === 'fox' || itemType === 'filled-fox') {
                // Calculate tangent angle
                let dx, dy;
                if (isDoubleSpiral) {
                    const k = spiralGrowth;
                    const p = z_plus_loc;
                    const q = z_minus_loc;
                    const w = exp((k + 1) * t);
                    const denominator = (w - 1) * (w - 1);

                    // Derivative of z(t) = (p*w - q)/(w - 1)
                    // dz/dt = (p*(k+1)*w*(w-1) - (p*w - q)*(k+1)*w) / (w-1)^2
                    // Simplified to: (k+1)*w*(p - q) / (w-1)^2
                    const numerator = (k + 1) * w * (p.x - q.x);
                    dx = numerator / denominator;

                    const numeratorY = (k + 1) * w * (p.y - q.y);
                    dy = -numeratorY / denominator; // Negative because canvas y is inverted
                } else {
                    const r = exp(spiralGrowth * t);
                    dx = spiralGrowth * r * cos(t) - r * sin(t);
                    dy = -(spiralGrowth * r * sin(t) + r * cos(t));
                }

                const angle = atan2(dy, dx);

                push();
                translate(x, y);
                rotate(angle);
                scale(0.5);

                if (itemType === 'fox') {
                    noFill();
                    stroke('#D6292D');
                    strokeWeight(1);
