                    fill('#D6292D');
                    noStroke();
                }

                beginShape();
                for (const v of fox_vertices) {
                    vertex(v.x, v.y);
                }
                endShape(CLOSE);
                pop();
            }
        }

        function drawLocators() {
            const locators = [];

            if (spiralType === 'single') {
                locators.push({ pos: z0_loc, color: color(255, 255, 0, 200), label: '' });
            } else {
                locators.push({ pos: z_plus_loc, color: color(255, 255, 0, 200), label: '' });
                locators.push({ pos: z_minus_loc, color: color(0, 255, 0, 200), label: '' });
            }

            for (const loc of locators) {
                const { pos, color: locColor, label } = loc;

                // Draw locator circle
                fill(locColor);
                stroke(0);
                strokeWeight(1);
                circle(pos.x, pos.y, 20);

                // Draw crosshair
                line(pos.x - 10, pos.y, pos.x + 10, pos.y);
                line(pos.x, pos.y - 10, pos.x, pos.y + 10);

                // Draw label if any
                if (label) {
                    textSize(12);
                    textAlign(CENTER, CENTER);
                    fill(0);
                    noStroke();
                    text(label, pos.x, pos.y - 20);
                }
            }
        }

        function setSpiralType(type) {
            spiralType = type;

            // Update button states
            document.getElementById('btn-type-single').classList.toggle('active', type === 'single');
            document.getElementById('btn-type-double').classList.toggle('active', type === 'double');

            redraw();
        }

        function setItemType(type) {
            itemType = type;

            // Update button states
            document.getElementById('btn-item-none').classList.toggle('active', type === 'none');
            document.getElementById('btn-item-point').classList.toggle('active', type === 'point');
            document.getElementById('btn-item-fox').classList.toggle('active', type === 'fox');
            document.getElementById('btn-item-filled-fox').classList.toggle('active', type === 'filled-fox');

            redraw();
        }

        function handleMouseDown(e) {
            const mouseX = e.clientX - width/2 - (window.innerWidth - width)/2;
            const mouseY = e.clientY - height/2 - (window.innerHeight - height)/2;

            if (spiralType === 'single') {
                if (dist(mouseX, mouseY, z0_loc.x, z0_loc.y) < 10) {
                    isDragging = true;
                    draggedLocator = 'z0';
                }
            } else {
                if (dist(mouseX, mouseY, z_plus_loc.x, z_plus_loc.y) < 10) {
                    isDragging = true;
                    draggedLocator = 'z_plus';
                } else if (dist(mouseX, mouseY, z_minus_loc.x, z_minus_loc.y) < 10) {
                    isDragging = true;
                    draggedLocator = 'z_minus';
                }
            }
        }

        function handleMouseMove(e) {
            if (!isDragging) return;

            const mouseX = e.clientX - width/2 - (window.innerWidth - width)/2;
            const mouseY = e.clientY - height/2 - (window.innerHeight - height)/2;

            switch (draggedLocator) {
                case 'z0':
                    z0_loc.x = mouseX;
                    z0_loc.y = mouseY;
                    break;
                case 'z_plus':
                    z_plus_loc.x = mouseX;
                    z_plus_loc.y = mouseY;
                    break;
                case 'z_minus':
                    z_minus_loc.x = mouseX;
                    z_minus_loc.y = mouseY;
                    break;
            }

            redraw();
        }

        function handleMouseUp() {
            isDragging = false;
            draggedLocator = null;
        }
    </script>
