            background-color: #4CAF50;
            color: white;
        }

        #vertex-grid {
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            gap: 5px;
        }

        .control-group {
            margin-bottom: 20px;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <button id="start-over-btn">Start Over</button>

        <div class="control-group">
            <label class="section-label">Initial Vertex</label>
            <div id="vertex-grid">
                <button id="btn-m" class="active">m</button>
                <button id="btn-n">n</button>
                <button id="btn-o">o</button>
                <button id="btn-p">p</button>
                <button id="btn-i">i</button>
                <button id="btn-j">j</button>
                <button id="btn-k">k</button>
                <button id="btn-l">l</button>
                <button id="btn-e">e</button>
                <button id="btn-f">f</button>
                <button id="btn-g">g</button>
                <button id="btn-h">h</button>
                <button id="btn-a">a</button>
                <button id="btn-b">b</button>
                <button id="btn-c">c</button>
                <button id="btn-d">d</button>
            </div>
        </div>

        <div class="control-group">
            <label class="section-label">Example Type</label>
            <button id="fixed-example-btn">Fixed Example</button>
            <button id="random-example-btn">Random Example</button>
        </div>

        <div class="control-group">
            <label class="section-label">Implement Algorithm</label>
            <button id="next-step-btn">Next Step</button>
        </div>
    </div>

    <div id="viz-container">
        <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
        <div id="canvas-container"></div>
    </div>

    <script>
        // Graph data and algorithm state
        const vertices = ['m', 'n', 'o', 'p', 'i', 'j', 'k', 'l', 'e', 'f', 'g', 'h', 'a', 'b', 'c', 'd'];
        let edges = {};
        let fixedEdges = {
            'm-n': 8, 'n-o': 7, 'o-p': 7,
            'i-j': 9, 'j-k': 7, 'k-l': 1,
            'e-f': 1, 'f-g': 7, 'g-h': 6,
            'a-b': 10, 'b-c': 4, 'c-d': 8,
            'm-i': 6, 'i-e': 9, 'e-a': 9,
            'n-j': 7, 'j-f': 6, 'f-b': 5,
            'o-k': 1, 'k-g': 8, 'g-c': 8,
            'p-l': 2, 'l-h': 1, 'h-d': 7
        };

        // Algorithm state
        let startVertex = 'm';
        let distances = {};
        let visited = new Set();
        let predecessors = {};
        let algorithmComplete = false;

        // p5.js sketch
        let sketch = function(p) {
            let canvas;
            let vertexPositions = {};

            p.setup = function() {
                canvas = p.createCanvas(600, 600);
                canvas.parent('canvas-container');
                p.noLoop();

                // Calculate vertex positions in a 4x4 grid
                const gridSize = 4;
                const spacing = p.width / (gridSize + 1);

                for (let i = 0; i < gridSize; i++) {
                    for (let j = 0; j < gridSize; j++) {
                        const vertex = String.fromCharCode(109 - i * 4 + j); // m, n, o, p, etc.
                        vertexPositions[vertex] = {
                            x: (j + 1) * spacing,
                            y: (i + 1) * spacing
                        };
                    }
                }

                resetAlgorithm();
                drawGraph();
            };

            function drawGraph() {
                p.background(255);
                p.strokeWeight(1);
                p.textSize(14);

                // Draw edges
                for (const [edge, weight] of Object.entries(edges)) {
                    const [u, v] = edge.split('-');
                    const uPos = vertexPositions[u];
                    const vPos = vertexPositions[v];

                    // Check if this edge is part of the shortest path tree
                    const isTreeEdge = (predecessors[u] === v) || (predecessors[v] === u);

                    if (isTreeEdge) {
                        p.stroke(0, 0, 255); // Blue for tree edges
                        p.strokeWeight(3);
                    } else {
                        p.stroke(0); // Black for regular edges
                        p.strokeWeight(1);
                    }

                    p.line(uPos.x, uPos.y, vPos.x, vPos.y);

                    // Draw edge weight
                    const midX = (uPos.x + vPos.x) / 2;
                    const midY = (uPos.y + vPos.y) / 2;

                    p.fill(255);
                    p.noStroke();
                    p.rectMode(p.CENTER);
                    p.rect(midX, midY, 30, 20);

                    p.fill(0);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text(weight, midX, midY);
                }

                // Draw vertices
                for (const [vertex, pos] of Object.entries(vertexPositions)) {
                    const isVisited = visited.has(vertex);

                    // Draw vertex circle
                    p.fill(isVisited ? 255 : 0);
                    p.stroke(isVisited ? 255 : 0);
                    p.strokeWeight(1);
                    p.ellipse(pos.x, pos.y, 20, 20);

                    // Draw vertex label (a-p)
                    p.fill(0);
                    p.noStroke();
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text(vertex, pos.x, pos.y);

                    // Draw distance label
                    const distance = distances[vertex] === Infinity ? '∞' : distances[vertex];
                    p.fill(isVisited ? 255 : 0);
                    p.text(`(${distance})`, pos.x, pos.y - 25);
                }
            }

            p.draw = function() {
                drawGraph();
            };
        };

        // Initialize p5 sketch
        new p5(sketch);

        // Initialize the application
        function initializeApp() {
            // Set up fixed edges by default
            edges = {...fixedEdges};

            // Set up event listeners
            document.getElementById('start-over-btn').addEventListener('click', startOver);

            // Vertex buttons
            vertices.forEach(v => {
                document.getElementById(`btn-${v}`).addEventListener('click', () => {
                    setStartVertex(v);
                });
            });

            document.getElementById('fixed-example-btn').addEventListener('click', () => {
                edges = {...fixedEdges};
                resetAlgorithm();
                drawGraph();
            });

            document.getElementById('random-example-btn').addEventListener('click', () => {
                generateRandomWeights();
                resetAlgorithm();
                drawGraph();
            });

            document.getElementById('next-step-btn').addEventListener('click', nextStep);

            // Initial setup
            resetAlgorithm();
            drawGraph();
        }

        // Reset algorithm state for current start vertex
        function resetAlgorithm() {
            distances = {};
            vertices.forEach(v => {
                distances[v] = v === startVertex ? 0 : Infinity;
            });

            visited = new Set();
            predecessors = {};
            algorithmComplete = false;

            document.getElementById('next-step-btn').disabled = false;
            updateTitle();
        }

        // Set the start vertex and reset algorithm
        function setStartVertex(v) {
            // Update button styles
            document.querySelectorAll('#vertex-grid button').forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById(`btn-${v}`).classList.add('active');

            startVertex = v;
            resetAlgorithm();
            drawGraph();
        }

        // Start over - reset to initial state
        function startOver() {
            setStartVertex('m');
            edges = {...fixedEdges};
            resetAlgorithm();
            drawGraph();
        }

        // Generate random edge weights (1-10)
        function generateRandomWeights() {
            edges = {};
            for (const edge of Object.keys(fixedEdges)) {
                edges[edge] = Math.floor(Math.random() * 10) + 1;
            }
        }

        // Execute one step of Dijkstra's algorithm
        function nextStep() {
            if (algorithmComplete) return;

            // Find unvisited vertex with smallest distance
            let minDist = Infinity;
            let currentVertex = null;

            for (const v of vertices) {
                if (!visited.has(v) && distances[v] < minDist) {
                    minDist = distances[v];
                    currentVertex = v;
                }
            }

            if (currentVertex === null) {
                // All vertices visited or remaining are unreachable
                algorithmComplete = true;
                document.getElementById('next-step-btn').disabled = true;
                return;
            }

            // Mark current vertex as visited
            visited.add(currentVertex);

            // Update distances for neighbors
            for (const v of vertices) {
                if (visited.has(v)) continue;

                // Check if edge exists in either direction
                const edge1 = `${currentVertex}-${v}`;
                const edge2 = `${v}-${currentVertex}`;
                const edge = edges[edge1] ? edge1 : (edges[edge2] ? edge2 : null);

                if (edge) {
                    const newDist = distances[currentVertex] + edges[edge];
                    if (newDist < distances[v]) {
                        distances[v] = newDist;
                        predecessors[v] = currentVertex;
                    }
                }
            }

            // Check if algorithm is complete
            if (visited.size === vertices.length) {
                algorithmComplete = true;
                document.getElementById('next-step-btn').disabled = true;
            }

            drawGraph();
        }

        // Update the title display
        function updateTitle() {
            document.getElementById('title-display').textContent = 
                `Dijkstra's algorithm starting at vertex ${startVertex}`;
        }

        // Redraw the graph
        function drawGraph() {
            if (sketch.redraw) sketch.redraw();
        }

        // Initialize the app when the page loads
        window.addEventListener('DOMContentLoaded', initializeApp);
    </script>
</body>
</html>
