    <script src="https://cdn.jsdelivr.net/npm/three@0.132.2/build/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.132.2/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        // Camera setup
        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(0, 0, 8);

        // Renderer setup
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.setSize(window.innerWidth - 320, window.innerHeight);
        document.getElementById('canvas-container').appendChild(renderer.domElement);

        // Controls setup
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(1, 1, 1);
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight2.position.set(-1, -1, -1);
        scene.add(directionalLight2);

        // Geometry and materials
        const sphereMaterial = new THREE.MeshPhongMaterial({ 
            color: 0xdddddd, 
            shininess: 50 
        });

        const coneMaterial = new THREE.MeshPhongMaterial({ 
            color: 0xffa500, 
            shininess: 30 
        });

        // Default values
        const defaultSphereDiameter = 1.5;
        let sphereRadius = defaultSphereDiameter / 2;
        let coneHeight = 1.5;
        let coneBase = 0.4;
        let currentPolyhedron = 'octahedron';

        // Create sphere
        const sphereGeometry = new THREE.SphereGeometry(sphereRadius, 32, 32);
        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(sphere);

        // Array to store cone meshes
        let cones = [];

        // Function to get vertices for different polyhedrons
        function getPolyhedronVertices(type) {
            let geometry;

            switch(type) {
                case 'tetrahedron':
                    geometry = new THREE.TetrahedronGeometry(1);
                    break;
                case 'cube':
                    geometry = new THREE.BoxGeometry(1, 1, 1);
                    break;
                case 'octahedron':
                    geometry = new THREE.OctahedronGeometry(1);
                    break;
                case 'dodecahedron':
                    geometry = new THREE.DodecahedronGeometry(1);
                    break;
                case 'icosahedron':
                    geometry = new THREE.IcosahedronGeometry(1);
                    break;
                default:
                    geometry = new THREE.OctahedronGeometry(1);
            }

            const vertices = [];
            const positions = geometry.attributes.position.array;

            for (let i = 0; i < positions.length; i += 3) {
                vertices.push(new THREE.Vector3(
                    positions[i],
                    positions[i+1],
                    positions[i+2]
                ));
            }

            return vertices;
        }

        // Function to create cones at polyhedron vertices
        function createCones(vertices) {
            // Remove existing cones
            cones.forEach(cone => scene.remove(cone));
            cones = [];

            vertices.forEach(vertex => {
                const direction = vertex.clone().normalize();

                // Calculate cone position
                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);

                // Create cone geometry
                const coneGeometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);
                const cone = new THREE.Mesh(coneGeometry, coneMaterial);

                // Position and orient the cone
                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(
                    new THREE.Vector3(0, 1, 0),
                    direction
                );

                scene.add(cone);
                cones.push(cone);
            });
        }

        // Initialize with octahedron
        const initialVertices = getPolyhedronVertices(currentPolyhedron);
        createCones(initialVertices);

        // Event listeners for controls
        document.getElementById('slider-sphere-diameter').addEventListener('input', function(e) {
            const newDiameter = parseFloat(e.target.value);
            sphereRadius = newDiameter / 2;

            // Scale the sphere
            sphere.scale.set(newDiameter / defaultSphereDiameter, 
                            newDiameter / defaultSphereDiameter, 
                            newDiameter / defaultSphereDiameter);

            // Update cone positions
            const vertices = getPolyhedronVertices(currentPolyhedron);
            cones.forEach((cone, index) => {
                const direction = vertices[index].clone().normalize();
                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);
                cone.position.copy(position);
            });
        });

        document.getElementById('slider-cone-height').addEventListener('input', function(e) {
            coneHeight = parseFloat(e.target.value);

            // Update all cones
            const vertices = getPolyhedronVertices(currentPolyhedron);
            cones.forEach((cone, index) => {
                // Create new geometry
                const newGeometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);
                cone.geometry.dispose();
                cone.geometry = newGeometry;

                // Update position
                const direction = vertices[index].clone().normalize();
                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);
                cone.position.copy(position);

                // Update orientation
                cone.quaternion.setFromUnitVectors(
                    new THREE.Vector3(0, 1, 0),
                    direction
                );
            });
        });

        document.getElementById('slider-cone-base').addEventListener('input', function(e) {
            coneBase = parseFloat(e.target.value);

            // Update all cones
            cones.forEach(cone => {
                const newGeometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);
                cone.geometry.dispose();
                cone.geometry = newGeometry;
            });
        });

        // Polyhedron button event listeners
        const polyhedronButtons = document.querySelectorAll('#polyhedron-buttons button');
        polyhedronButtons.forEach(button => {
            button.addEventListener('click', function() {
                // Update active button
                polyhedronButtons.forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');

                // Get selected polyhedron type
                currentPolyhedron = this.id.replace('btn-', '');

                // Create new cones
                const vertices = getPolyhedronVertices(currentPolyhedron);
                createCones(vertices);
            });
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            const width = window.innerWidth - 320;
            const height = window.innerHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();

            renderer.setSize(width, height);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        animate();
    </script>
</body>
</html>
