    <script>
        const planetData = {
            "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
            "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        let p5Instance;
        let analemmaPoints = [];
        let canvasWidth, canvasHeight;
        let scaleFactor = 200;

        function setup() {
            const canvasContainer = document.getElementById('visualization-area');
            canvasWidth = canvasContainer.clientWidth - 40;
            canvasHeight = canvasContainer.clientHeight - 40;

            let canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('visualization-area');
            canvas.id('analemma-canvas');

            calculateAnalemma();

            // Setup event listeners
            document.getElementById('select-planet').addEventListener('change', handlePlanetSelect);
            document.getElementById('checkbox-manual').addEventListener('change', handleManualCheckbox);

            const sliders = ['tilt', 'equinox', 'eccentricity'];
            sliders.forEach(slider => {
                document.getElementById(`slider-${slider}`).addEventListener('input', () => {
                    document.getElementById(`${slider}-value`).textContent = document.getElementById(`slider-${slider}`).value;
                    calculateAnalemma();
                    redraw();
                });
            });

            document.getElementById('checkbox-snapshots').addEventListener('change', redraw);
            document.getElementById('checkbox-scales').addEventListener('change', redraw);
        }

        function handlePlanetSelect() {
            const select = document.getElementById('select-planet');
            const manualCheckbox = document.getElementById('checkbox-manual');

            if (select.value === 'manual') {
                manualCheckbox.checked = true;
                enableSliders(true);
            } else {
                manualCheckbox.checked = false;
                enableSliders(false);

                const planet = planetData[select.value];
                document.getElementById('slider-tilt').value = planet.tilt;
                document.getElementById('slider-eccentricity').value = planet.eccentricity;
                document.getElementById('slider-equinox').value = planet.equinox;

                document.getElementById('tilt-value').textContent = planet.tilt;
                document.getElementById('eccentricity-value').textContent = planet.eccentricity;
                document.getElementById('equinox-value').textContent = planet.equinox;
            }

            calculateAnalemma();
            redraw();
        }

        function handleManualCheckbox() {
            const manualCheckbox = document.getElementById('checkbox-manual');
            const select = document.getElementById('select-planet');

            if (manualCheckbox.checked) {
                select.value = 'manual';
                enableSliders(true);
            } else {
                enableSliders(false);
            }

            calculateAnalemma();
            redraw();
        }

        function enableSliders(enable) {
            document.getElementById('slider-tilt').disabled = !enable;
            document.getElementById('slider-equinox').disabled = !enable;
            document.getElementById('slider-eccentricity').disabled = !enable;
        }

        function calculateAnalemma() {
            analemmaPoints = [];

            const tilt = radians(parseFloat(document.getElementById('slider-tilt').value));
            const ecc = parseFloat(document.getElementById('slider-eccentricity').value);
            const equinoxAngle = radians(parseFloat(document.getElementById('slider-equinox').value));

            for (let d = 0; d < 365; d++) {
                // Mean anomaly
                const M = (2 * Math.PI * d) / 365.25;

                // Solve Kepler's equation for eccentric anomaly (E)
                let E = M;
                for (let i = 0; i < 10; i++) {
                    E = M + ecc * Math.sin(E);
                }

                // True anomaly
                const v = 2 * Math.atan2(
                    Math.sqrt(1 + ecc) * Math.sin(E / 2),
                    Math.sqrt(1 - ecc) * Math.cos(E / 2)
                );

                // Ecliptic longitude
                const lambda = v + equinoxAngle;

                // Declination (y-coordinate)
                const y = Math.asin(Math.sin(tilt) * Math.sin(lambda));

                // Right ascension and equation of time (x-coordinate)
                const alpha = Math.atan2(Math.cos(tilt) * Math.sin(lambda), Math.cos(lambda));
                let EoT = M - alpha;

                // Handle wrapping around -π/π boundary
                if (EoT > Math.PI) EoT -= 2 * Math.PI;
                if (EoT < -Math.PI) EoT += 2 * Math.PI;

                analemmaPoints.push({
                    x: EoT * scaleFactor,
                    y: -y * scaleFactor  // Invert y for canvas coordinates
                });
            }
        }

        function draw() {
            background(255);
            translate(width / 2, height / 2);

            const showScales = document.getElementById('checkbox-scales').checked;
            const showSnapshots = document.getElementById('checkbox-snapshots').checked;
            const ecc = parseFloat(document.getElementById('slider-eccentricity').value);

            // Draw orbit ellipse at top
            push();
            translate(0, -height / 2 + 100);
            noFill();
            stroke(139, 69, 19);  // Brown color
            strokeWeight(1);

            const ellipseWidth = 100 * (1 + ecc);
            const ellipseHeight = 100 * Math.sqrt(1 - ecc * ecc);
            ellipse(0, 0, ellipseWidth, ellipseHeight);
            pop();

            // Draw scales if enabled
            if (showScales) {
                stroke(200);
                strokeWeight(1);

                // X axis
                line(-width / 2 + 50, 0, width / 2 - 50, 0);
                // Y axis
                line(0, -height / 2 + 100, 0, height / 2 - 50);

                // Tick marks and labels
                textSize(12);
                textAlign(CENTER, CENTER);
                fill(100);
                noStroke();

                // X axis ticks
                for (let x = -40; x <= 40; x += 10) {
                    const px = x * (scaleFactor / 10);
                    line(px, -5, px, 5);
                    text(x, px, 20);
                }

                // Y axis ticks
                for (let y = -40; y <= 40; y += 10) {
                    const py = y * (scaleFactor / 10);
                    line(-5, py, 5, py);
                    text(y, -20, py);
                }
            }

            // Draw analemma curve
            noFill();
            stroke(255, 165, 0);  // Orange
            strokeWeight(2);
            beginShape();
            for (let pt of analemmaPoints) {
                vertex(pt.x, pt.y);
            }
            endShape();

            // Draw sun snapshots if enabled
            if (showSnapshots) {
                fill(255, 165, 0);
                noStroke();
                for (let i = 0; i < analemmaPoints.length; i += 20) {
                    const pt = analemmaPoints[i];
                    circle(pt.x, pt.y, 8);
                }
            }
        }

        function windowResized() {
            const canvasContainer = document.getElementById('visualization-area');
            canvasWidth = canvasContainer.clientWidth - 40;
            canvasHeight = canvasContainer.clientHeight - 40;
            resizeCanvas(canvasWidth, canvasHeight);
            redraw();
        }

        // Initialize p5 instance
        p5Instance = new p5(function(sketch) {
            sketch.setup = setup;
            sketch.draw = draw;
            sketch.windowResized = windowResized;
        }, document.body);
    </script>
</body>
</html>
