            textOptimalWeight: document.getElementById('text-optimal-weight'),
            optimalContainer: document.getElementById('optimal-result-container'),
            btnNextStep: document.getElementById('btn-next-step'),
            btnStartOver: document.getElementById('btn-start-over'),
            btnFixedExample: document.getElementById('btn-fixed-example'),
