        // Check if an edge is part of the optimal path
        function isEdgeInOptimalPath(v1, v2) {
            const optimalPath = state.graph.optimalPath;
            for (let i = 0; i < optimalPath.length - 1; i++) {
                if ((optimalPath[i] === v1 && optimalPath[i+1] === v2) || 
                    (optimalPath[i] === v2 && optimalPath[i+1] === v1)) {
                    return true;
                }
            }
            return false;
        }

        // Handle algorithm selection change
        function handleAlgorithmChange() {
            state.currentAlgorithm = document.querySelector('input[name="algorithm-choice"]:checked').value;
            elements.startingVertexSelector.style.display = state.currentAlgorithm === 'nearest-neighbor' ? 'block' : 'none';
