                generateRandomGraph();
            }

            resetAlgorithmState();
            updateUI();
        }

        // Generate a random graph with 7 vertices
        function generateRandomGraph() {
            const vertices = [];
            const edges = [];

            // Generate vertices in a circular pattern
            const centerX = 200;
            const centerY = 200;
            const radius = 150;
            const angleStep = (2 * Math.PI) / 7;

            for (let i = 0; i < 7; i++) {
                const angle = i * angleStep;
                const x = centerX + radius * Math.cos(angle);
                const y = centerY + radius * Math.sin(angle);
                vertices.push({
                    id: String.fromCharCode(65 + i), // A-G
                    x: x,
                    y: y
                });
            }

            // Generate edges with random weights (10-50)
            for (let i = 0; i < vertices.length; i++) {
                for (let j = i + 1; j < vertices.length; j++) {
                    const weight = Math.floor(Math.random() * 41) + 10; // 10-50
                    edges.push([vertices[i].id, vertices[j].id, weight]);
                }
            }

            // Calculate optimal path (brute force for N=7)
            const optimal = findOptimalPath(vertices.map(v => v.id), edges);

            state.graph = {
                vertices: vertices,
                edges: edges,
                optimalPath: optimal.path,
                optimalWeight: optimal.weight
            };
        }

        // Brute force search for optimal path (only feasible for N=7)
        function findOptimalPath(vertices, edges) {
            const allPermutations = permute(vertices.filter(v => v !== 'A'));
            let minWeight = Infinity;
            let bestPath = null;

            // Create a map for quick edge weight lookup
            const edgeMap = {};
            edges.forEach(edge => {
                edgeMap[`${edge[0]}-${edge[1]}`] = edge[2];
                edgeMap[`${edge[1]}-${edge[0]}`] = edge[2];
            });

            // Evaluate all permutations starting and ending with 'A'
            for (const perm of allPermutations) {
                const path = ['A', ...perm, 'A'];
                let weight = 0;
                let valid = true;

                // Calculate path weight
                for (let i = 0; i < path.length - 1; i++) {
                    const key = `${path[i]}-${path[i+1]}`;
                    if (edgeMap[key] === undefined) {
                        valid = false;
                        break;
                    }
                    weight += edgeMap[key];
                }

                if (valid && weight < minWeight) {
                    minWeight = weight;
                    bestPath = path;
                }
            }

            return {
                path: bestPath,
                weight: minWeight
            };
        }

        // Generate all permutations of an array (for brute force optimal path)
        function permute(arr) {
            if (arr.length === 1) return [arr];

            const result = [];
            for (let i = 0; i < arr.length; i++) {
                const current = arr[i];
                const remaining = [...arr.slice(0, i), ...arr.slice(i + 1)];
                const remainingPerms = permute(remaining);

                for (const perm of remainingPerms) {
                    result.push([current, ...perm]);
                }
            }

            return result;
        }

        // Handle optimal solution toggle
        function handleOptimalToggle() {
            elements.optimalContainer.style.display = this.checked ? 'block' : 'none';
            if (this.checked) {
                optimalSketch.redraw();
            }
        }

        // Handle vertex selection
        function handleVertexSelect(e) {
            const vertex = e.target.dataset.vertex;

            // Disable all vertex buttons
            elements.vertexButtons.forEach(btn => {
                btn.disabled = true;
            });

            // Enable next step button
            elements.btnNextStep.disabled = false;

            // Update algorithm state
            state.algorithmState.currentVertex = vertex;
            state.algorithmState.visitedVertices.push(vertex);

            // Update title
            elements.titleAlgo.textContent = `The nearest neighbor algorithm starting at vertex ${vertex}`;

            // Prepare available edges for cheapest link if needed
            if (state.currentAlgorithm === 'cheapest-link') {
                prepareCheapestLinkEdges();
            }
        }

        // Prepare sorted list of edges for cheapest link algorithm
        function prepareCheapestLinkEdges() {
            state.algorithmState.availableEdges = [...state.graph.edges]
                .map(edge => [...edge]) // Clone each edge
                .sort((a, b) => a[2] - b[2]); // Sort by weight
        }

        // Handle next step button
        function handleNextStep() {
            if (state.algorithmState.isComplete) return;

            if (state.currentAlgorithm === 'nearest-neighbor') {
                executeNearestNeighborStep();
            } else {
                executeCheapestLinkStep();
            }

            state.algorithmState.currentStep++;
            updateUI();
        }

        // Execute one step of nearest neighbor algorithm
        function executeNearestNeighborStep() {
            const currentVertex = state.algorithmState.currentVertex;
            let nearestVertex = null;
            let minWeight = Infinity;
            let selectedEdge = null;

            // Find the nearest unvisited neighbor
            for (const edge of state.graph.edges) {
                const [v1, v2, weight] = edge;

                if (v1 === currentVertex && !state.algorithmState.visitedVertices.includes(v2)) {
                    if (weight < minWeight) {
                        minWeight = weight;
                        nearestVertex = v2;
                        selectedEdge = [v1, v2, weight];
                    }
                } else if (v2 === currentVertex && !state.algorithmState.visitedVertices.includes(v1)) {
                    if (weight < minWeight) {
                        minWeight = weight;
                        nearestVertex = v1;
                        selectedEdge = [v2, v1, weight];
                    }
                }
            }

            // If we've visited all vertices, return to start
            if (nearestVertex === null && state.algorithmState.visitedVertices.length === state.graph.vertices.length) {
                // Find edge back to start
                const startVertex = state.algorithmState.visitedVertices[0];
                for (const edge of state.graph.edges) {
                    const [v1, v2, weight] = edge;
                    if ((v1 === currentVertex && v2 === startVertex) || 
                        (v2 === currentVertex && v1 === startVertex)) {
                        selectedEdge = [currentVertex, startVertex, weight];
                        state.algorithmState.totalWeight += weight;
                        state.algorithmState.selectedEdges.push(selectedEdge);
                        state.algorithmState.isComplete = true;
                        break;
                    }
                }
                return;
            }

            // If no neighbor found but not all vertices visited, mark as complete (invalid case)
            if (nearestVertex === null) {
                state.algorithmState.isComplete = true;
                return;
            }

            // Update state
            state.algorithmState.currentVertex = nearestVertex;
            state.algorithmState.visitedVertices.push(nearestVertex);
            state.algorithmState.totalWeight += minWeight;
            state.algorithmState.selectedEdges.push(selectedEdge);

            // Check if all vertices have been visited
            if (state.algorithmState.visitedVertices.length === state.graph.vertices.length) {
                // Next step will complete the circuit
            }
        }

        // Execute one step of cheapest link algorithm
        function executeCheapestLinkStep() {
            while (state.algorithmState.availableEdges.length > 0) {
                const edge = state.algorithmState.availableEdges.shift();
                const [v1, v2, weight] = edge;

                // Check if adding this edge would violate the rules
                if (canAddEdgeToPath(v1, v2)) {
                    state.algorithmState.selectedEdges.push(edge);
                    state.algorithmState.totalWeight += weight;

                    // Check if the circuit is complete
                    if (isCircuitComplete()) {
                        state.algorithmState.isComplete = true;
                    }
                    return;
                }
            }

            // If we run out of edges, mark as complete
            state.algorithmState.isComplete = true;
        }

        // Check if an edge can be added to the current path (for cheapest link)
        function canAddEdgeToPath(v1, v2) {
            // Count current edges for each vertex
            const edgeCounts = {};
            state.graph.vertices.forEach(v => {
                edgeCounts[v.id] = 0;
            });

            state.algorithmState.selectedEdges.forEach(edge => {
                edgeCounts[edge[0]]++;
                edgeCounts[edge[1]]++;
            });

            // Rule 1: No vertex can have more than 2 edges
            if (edgeCounts[v1] >= 2 || edgeCounts[v2] >= 2) {
                return false;
            }

            // Rule 2: No premature cycles (unless it's the last edge)
            const tempEdges = [...state.algorithmState.selectedEdges, [v1, v2, 0]];
            if (formsPrematureCycle(tempEdges) && 
                tempEdges.length < state.graph.vertices.length) {
                return false;
            }

            return true;
        }

        // Check if adding an edge would form a premature cycle (using union-find)
        function formsPrematureCycle(edges) {
            const parent = {};
            state.graph.vertices.forEach(v => {
                parent[v.id] = v.id;
            });

            function find(u) {
                if (parent[u] !== u) {
                    parent[u] = find(parent[u]);
                }
                return parent[u];
            }

            for (const edge of edges) {
                const [u, v] = edge;
                const rootU = find(u);
                const rootV = find(v);

                if (rootU === rootV) {
                    return true;
                }

                parent[rootU] = rootV;
            }

            return false;
        }

        // Check if the circuit is complete (all vertices have exactly 2 edges)
        function isCircuitComplete() {
            if (state.algorithmState.selectedEdges.length < state.graph.vertices.length) {
                return false;
            }

            const edgeCounts = {};
            state.graph.vertices.forEach(v => {
                edgeCounts[v.id] = 0;
            });

            state.algorithmState.selectedEdges.forEach(edge => {
                edgeCounts[edge[0]]++;
                edgeCounts[edge[1]]++;
            });

            for (const count of Object.values(edgeCounts)) {
                if (count !== 2) {
                    return false;
                }
            }

            return !formsPrematureCycle(state.algorithmState.selectedEdges);
        }

        // Reset algorithm state
        function resetAlgorithmState() {
            state.algorithmState = {
                currentStep: 0,
                selectedEdges: [],
                totalWeight: 0,
                visitedVertices: [],
                currentVertex: null,
                availableEdges: [],
                isComplete: false
            };

            // Update UI elements
            elements.btnNextStep.disabled = true;
            elements.vertexButtons.forEach(btn => {
                btn.disabled = state.currentAlgorithm !== 'nearest-neighbor';
            });

            // Update title based on algorithm
            if (state.currentAlgorithm === 'nearest-neighbor') {
                elements.titleAlgo.textContent = 'Select a starting vertex';
            } else {
                elements.titleAlgo.textContent = 'The cheapest link algorithm';
            }

            // Update example type text
            elements.textExampleTypeAlgo.textContent = `${state.exampleType} example`;
            elements.textExampleTypeOptimal.textContent = `${state.exampleType} example`;

            // Clear table and total weight
            elements.tableEdgeList.innerHTML = '';
            elements.textTotalWeight.textContent = '';

            // Redraw canvas
            algoSketch.redraw();
        }

        // Update the UI based on current state
        function updateUI() {
            // Update edge table
            elements.tableEdgeList.innerHTML = '';
            state.algorithmState.selectedEdges.forEach((edge, index) => {
                const row = document.createElement('tr');
                row.innerHTML = `<td>${edge[0]}-${edge[1]}</td><td>${edge[2]}</td>`;
                elements.tableEdgeList.appendChild(row);
            });

            // Update total weight if we have edges
            if (state.algorithmState.selectedEdges.length > 0) {
                elements.textTotalWeight.textContent = `Total weight: ${state.algorithmState.totalWeight}`;
            }

            // Update optimal solution weight
            elements.textOptimalWeight.textContent = `Optimal weight: ${state.graph.optimalWeight}`;

            // Redraw canvas
            algoSketch.redraw();

            // Disable next step button if algorithm is complete
            elements.btnNextStep.disabled = state.algorithmState.isComplete;
        }

        // Initialize the application when the page loads
        window.addEventListener('DOMContentLoaded', init);
    </script>
</body>
</html>
