                function drawProjection(p, point, axis, dotProduct, label) {
                    // Calculate projection point
                    const projX = dotProduct * axis.x;
                    const projY = dotProduct * axis.y;

                    // Draw line from point to projection
                    p.stroke(128);
                    p.strokeWeight(1 / xScale);
                    p.line(point.x, point.y, projX, projY);

                    // Draw projection point
                    p.fill(0, 255, 0);
                    p.noStroke();
                    p.ellipse(projX, projY, 6 / xScale, 6 / xScale);

                    // Draw label
                    p.fill(0);
                    p.textSize(8 / xScale);
                    p.text(label, projX + 0.05, projY - 0.05);
                }

                p.windowResized = function() {
                    const canvasContainer = document.getElementById('canvas-container');
                    canvasWidth = canvasContainer.clientWidth;
                    canvasHeight = canvasContainer.clientHeight;
                    p.resizeCanvas(canvasWidth, canvasHeight);
                    p.redraw();
                };
            };

