        // Function to update calculations when slider values change
        function updateCalculations() {
            // Get current dot product values from sliders
            const au = parseFloat(document.getElementById('slider-au').value);
            const bu = parseFloat(document.getElementById('slider-bu').value);
            const av = parseFloat(document.getElementById('slider-av').value);
            const bv = parseFloat(document.getElementById('slider-bv').value);
            const aw = parseFloat(document.getElementById('slider-aw').value);
            const bw = parseFloat(document.getElementById('slider-bw').value);

            // Construct the measurement matrix M
            const M = math.matrix([
                [au, bu],
                [av, bv],
                [aw, bw]
            ]);

            try {
                // Perform SVD on M
                const svd = math.svd(M);
                const U = svd.U;
                const D = math.diag(svd.s);
                const V = svd.V;

                // Construct K matrix for solving L
                const u1 = U._data[0][0], u2 = U._data[1][0], u3 = U._data[2][0];
                const v1 = U._data[0][1], v2 = U._data[1][1], v3 = U._data[2][1];
                const w1 = U._data[0][2], w2 = U._data[1][2], w3 = U._data[2][2];

                const K = math.matrix([
                    [u1*u1, 2*u1*v1, v1*v1],
                    [u2*u2, 2*u2*v2, v2*v2],
                    [u3*u3, 2*u3*v3, v3*v3]
                ]);

                // Solve K * l_vec = [1,1,1] for l_vec
                const ones = math.matrix([1, 1, 1]);
                const l_vec = math.lusolve(K, ones);

                // Extract elements of L matrix
                const l11 = l_vec._data[0][0];
                const l12 = l_vec._data[1][0];
                const l22 = l_vec._data[2][0];

                // Construct L matrix
                const L = math.matrix([
                    [l11, l12],
                    [l12, l22]
                ]);

                // Perform Cholesky decomposition on L
                const Q = math.chol(L);

                // Calculate motion matrix S = U * Q
                const S = math.multiply(U, Q);

                // Calculate shape matrix P = inv(Q) * D * V'
                const invQ = math.inv(Q);
                const V_transpose = math.transpose(V);
                const P = math.multiply(math.multiply(invQ, D), V_transpose);

                // Extract vectors
                u = { x: S._data[0][0], y: S._data[0][1] };
                v = { x: S._data[1][0], y: S._data[1][1] };
                w = { x: S._data[2][0], y: S._data[2][1] };

                A = { x: P._data[0][0], y: P._data[0][1] };
                B = { x: P._data[1][0], y: P._data[1][1] };

            } catch (error) {
                console.error("Calculation error:", error);
                // Clear vectors to indicate error state
                A = B = u = v = w = null;
            }
        }

        // Initialize when the page loads
        window.addEventListener('load', setup);
    </script>
