            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function initUI() {
            // Initialize button interactions
            const buttons2D = document.querySelectorAll('#buttons-2d-container button');
            const buttons3D = document.querySelectorAll('#buttons-3d-container button');

            buttons2D.forEach(button => {
                button.addEventListener('click', function() {
                    // Update active button
                    buttons2D.forEach(btn => btn.classList.remove('active'));
                    buttons3D.forEach(btn => btn.classList.remove('active'));
                    this.classList.add('active');

                    // Update state
