                // Update visualization
                updateVisualization();
            });

            document.addEventListener('mouseup', function() {
                isDragging = false;
            });

            timePad.addEventListener('click', function(e) {
                const padRect = timePad.getBoundingClientRect();
                let x = e.clientX - padRect.left;
                let y = e.clientY - padRect.top;

                // Update handle position
                timeHandle.style.left = x + 'px';
                timeHandle.style.top = y + 'px';

                // Convert to t1, t2 values (-1 to 1)
                t1 = (x / padRect.width) * 2 - 1;
                t2 = 1 - (y / padRect.height) * 2; // Invert y axis

                // Update visualization
                updateVisualization();
            });
        }

        function updateVisualization() {
            // Clear previous objects
            if (currentMesh) scene.remove(currentMesh);
            if (currentLine) scene.remove(currentLine);

            // Calculate scaled time parameters
            const T1 = t1 * 1.5;
            const T2 = t2 * 1.5;

            if (currentDimension === '2D') {
                // Switch to orthographic camera if needed
                if (camera instanceof THREE.PerspectiveCamera) {
                    camera = new THREE.OrthographicCamera(
                        window.innerWidth / -2, window.innerWidth / 2,
                        window.innerHeight / 2, window.innerHeight / -2,
                        1, 1000
                    );
                    camera.position.z = 100;
                    if (controls) {
                        controls.dispose();
                        controls = null;
                    }
                }

                // Create 2D curve
                const points = [];
                const segments = 200;

                for (let i = 0; i <= segments; i++) {
                    const u = (i / segments) * 4 - 2; // u from -2 to 2
                    let x, y;

                    switch (currentType) {
                        case '2A1':
                            x = u + T1;
                            y = u * u + T2;
                            break;
                        case '2A2':
                            x = 2 * Math.pow(u, 3) + T1;
                            y = 3 * u * u + T2;
                            break;
                        case '2B2':
                            x = u * u + T1;
                            y = Math.pow(u, 4) + T2;
                            break;
                        default:
                            x = 0;
                            y = 0;
                    }

                    points.push(new THREE.Vector3(x * 20, y * 20, 0));
                }

                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: 0x4a69bd });
                currentLine = new THREE.Line(geometry, material);
                scene.add(currentLine);

            } else {
                // 3D visualization
                // Switch to perspective camera if needed
                if (camera instanceof THREE.OrthographicCamera) {
                    camera = new THREE.PerspectiveCamera(75, (window.innerWidth - 300) / window.innerHeight, 0.1, 1000);
                    camera.position.set(3, 3, 3);
                    camera.lookAt(0, 0, 0);

                    // Add orbit controls
                    controls = new THREE.OrbitControls(camera, renderer.domElement);
                    controls.enableDamping = true;
                    controls.dampingFactor = 0.05;
                }

                // Create 3D surface
                const geometry = new THREE.ParametricGeometry((u, v, target) => {
                    // Map u,v from [0,1] to appropriate ranges
                    let U, V;

                    // Default ranges
                    U = (u - 0.5) * 4; // -2 to 2
                    V = (v - 0.5) * 4; // -2 to 2

                    // Special cases with different ranges
                    if (currentType === '3A2') {
                        U = (u - 0.5) * 3; // -1.5 to 1.5
                    } else if (currentType === '3A3') {
                        U = (u - 0.5) * 3; // -1.5 to 1.5
                        V = (v - 0.5) * 8; // -4 to 4
                    }

                    let x, y, z;

                    switch (currentType) {
                        case '3A1p':
                            x = U;
                            y = V;
                            z = 0.3 * (U * U + V * V) + T1 * U + T2 * V;
                            break;
                        case '3A1m':
                            x = U;
                            y = V;
                            z = -0.3 * (U * U + V * V) + T1 * U + T2 * V;
                            break;
                        case '3A2':
                            x = 2 * Math.pow(U, 3) + T1;
                            y = 3 * U * U + T2;
                            z = V;
                            break;
                        case '3A3':
                            x = 3 * Math.pow(U, 4) + U * U * V;
                            y = -4 * Math.pow(U, 3) - 2 * U * V + T1 * 2;
                            z = V + T2 * 2;
                            break;
                        case '3B2p':
                            x = U;
                            y = V;
                            z = Math.pow(U * U + T1, 2) + V * V + T2;
                            break;
                        case '3B2m':
                            x = U;
                            y = V;
                            z = -(Math.pow(U, 4) + T1 * 2 * U * U + V * V - T2);
                            break;
                        case '3B3':
                            x = U;
                            y = V;
                            z = Math.pow(U, 3) + T1 * U + V * V + T2;
                            break;
                        case '3C3p':
                            x = U;
                            y = V;
                            z = Math.pow(U, 3) - 3 * U * V * V + T1 * 2 * (U * U + V * V) + T2 * 4 * U;
                            break;
                        case '3C3m':
                            x = U;
                            y = V;
                            z = Math.pow(U, 3) + Math.pow(V, 3) + T1 * 4 * U + T2 * 4 * V;
                            break;
                        default:
                            x = 0;
                            y = 0;
                            z = 0;
                    }

                    target.set(x, y, z);
                }, 50, 50);

                const material = new THREE.MeshPhongMaterial({
                    color: 0xffc34d,
                    side: THREE.DoubleSide,
                    flatShading: true
                });

                currentMesh = new THREE.Mesh(geometry, material);
                scene.add(currentMesh);

                // For some types, add a wireframe
                if (['3A3', '3B2m', '3C3m'].includes(currentType)) {
                    const wireMaterial = new THREE.LineBasicMaterial({ 
                        color: 0x000000,
                        linewidth: 1
                    });
                    const wireGeometry = new THREE.WireframeGeometry(geometry);
                    currentLine = new THREE.LineSegments(wireGeometry, wireMaterial);
                    scene.add(currentLine);
                }
            }

            // Start animation loop if not already running
            if (!animateID) {
                animate();
            }
        }

        let animateID = null;
        function animate() {
            animateID = requestAnimationFrame(animate);

            if (controls) {
                controls.update();
            }

            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const width = window.innerWidth - 300;
            const height = window.innerHeight;

            if (camera instanceof THREE.PerspectiveCamera) {
                camera.aspect = width / height;
                camera.updateProjectionMatrix();
            } else {
                camera.left = width / -2;
                camera.right = width / 2;
                camera.top = height / 2;
                camera.bottom = height / -2;
                camera.updateProjectionMatrix();
            }

            renderer.setSize(width, height);
        }

        // Clean up on page unload
        window.addEventListener('beforeunload', function() {
            if (animateID) {
                cancelAnimationFrame(animateID);
            }
            if (controls) {
                controls.dispose();
            }
        });
    </script>
</body>
