    <script>
        // Constants
        const V = 100; // Reactor volume (L)
        const T_initial = 330; // Initial temperature (K)
        const T_coolant = 330; // Coolant temperature (K)
        const T_feed = 330; // Feed temperature (K)
        const R = 8.314; // Gas constant (J/mol/K)
        const Cp_total = 150000; // Total heat capacity (J/K)
        const k1_0 = 3e4; // Pre-exponential factor for reaction 1 (L²/(mol²·h))
        const E1 = 30000; // Activation energy for reaction 1 (J/mol)
        const k2_0 = 1e5; // Pre-exponential factor for reaction 2 (L/(mol·h))
        const E2 = 40000; // Activation energy for reaction 2 (J/mol)
        const initial_moles = 100; // Initial moles of reactants (mol)

        // Simulation parameters
        let t_start = 0;
        let t_end = 2.0;
        let dt = 0.01;

        // State variables
        let plotType = 'semibatch';
        let feedConfig = 'A to B';
        let feedRate = 10;
        let UA = 15000;
        let dh1 = 1000;
        let dh2 = 3000;

        // Simulation results
        let batchResults = [];
        let semiResults = [];
        let selectivityRatio = [];

        // p5.js sketch
        let canvas;
        let p5sketch = function(p) {
            p.setup = function() {
                canvas = p.createCanvas(p.select('#plot-canvas').width, p.select('#plot-canvas').height);
                canvas.parent('plot-canvas');
                updateSimulation();
            };

            p.draw = function() {
                p.background(240);
                drawAxesAndGrid(p);
                drawDataCurves(p);
            };

            p.windowResized = function() {
                p.resizeCanvas(p.select('#plot-canvas').width, p.select('#plot-canvas').height);
            };
        };

        new p5(p5sketch);

        // Initialize controls and event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Set initial values
            document.getElementById('select-plot').value = plotType;
            document.getElementById('cycler-feed-label').textContent = `feed ${feedConfig}`;
            document.getElementById('slider-feed-rate').value = feedRate;
            document.getElementById('input-feed-rate').value = feedRate;
            document.getElementById('slider-ua').value = UA;
            document.getElementById('input-ua').value = UA;
            document.getElementById('slider-dh1').value = dh1;
            document.getElementById('input-dh1').value = dh1;
            document.getElementById('slider-dh2').value = dh2;
            document.getElementById('input-dh2').value = dh2;

            // Add event listeners
            document.getElementById('select-plot').addEventListener('change', function() {
                plotType = this.value;
                updateSimulation();
                updateLegend();
            });

            document.getElementById('btn-feed-up').addEventListener('click', function() {
                feedConfig = feedConfig === 'A to B' ? 'B to A' : 'A to B';
                document.getElementById('cycler-feed-label').textContent = `feed ${feedConfig}`;
                updateSimulation();
            });

            document.getElementById('btn-feed-down').addEventListener('click', function() {
                feedConfig = feedConfig === 'A to B' ? 'B to A' : 'A to B';
                document.getElementById('cycler-feed-label').textContent = `feed ${feedConfig}`;
                updateSimulation();
            });

            // Slider and input synchronization
            const syncInputs = (sliderId, inputId, property) => {
                const slider = document.getElementById(sliderId);
                const input = document.getElementById(inputId);

                slider.addEventListener('input', function() {
                    input.value = this.value;
                    window[property] = parseFloat(this.value);
                    updateSimulation();
                });

                input.addEventListener('input', function() {
                    slider.value = this.value;
                    window[property] = parseFloat(this.value);
                    updateSimulation();
                });
            };

            syncInputs('slider-feed-rate', 'input-feed-rate', 'feedRate');
            syncInputs('slider-ua', 'input-ua', 'UA');
            syncInputs('slider-dh1', 'input-dh1', 'dh1');
            syncInputs('slider-dh2', 'input-dh2', 'dh2');

            // Initial legend setup
            updateLegend();
        });

        // Update the legend based on current plot type
        function updateLegend() {
            const legend = document.getElementById('legend');
            legend.innerHTML = '';

            if (plotType === 'selectivity') {
                const item = document.createElement('div');
                item.className = 'legend-item';
                item.innerHTML = `
                    <div class="legend-line" style="background-color: #800080;"></div>
                    <span>semibatch \\(S_{d/u}\\) / batch \\(S_{d/u}\\)</span>
                `;
                legend.appendChild(item);
            } else {
                const items = [
                    { color: '#2ca02c', label: '\\(N_a\\)', dashed: true },
                    { color: '#000000', label: '\\(N_b\\)', dashed: true },
                    { color: '#1f77b4', label: '\\(N_d\\)', dashed: false },
                    { color: '#d62728', label: '\\(N_u\\)', dashed: false }
                ];

                items.forEach(item => {
                    const div = document.createElement('div');
                    div.className = 'legend-item';
                    div.innerHTML = `
                        <div class="legend-line ${item.dashed ? 'dashed' : ''}" style="color: ${item.color};"></div>
                        <span>${item.label}</span>
                    `;
                    legend.appendChild(div);
                });
            }

            // Render MathJax
            if (typeof MathJax !== 'undefined') {
                MathJax.typesetPromise();
            }
        }

        // Run the simulation and update results
        function updateSimulation() {
            // Run batch simulation
            batchResults = runSimulation('batch');

            // Run semibatch simulation if needed
            if (plotType === 'semibatch' || plotType === 'selectivity') {
                semiResults = runSimulation('semibatch');
            }

            // Calculate selectivity ratio if needed
            if (plotType === 'selectivity') {
                selectivityRatio = [];
                for (let i = 1; i < batchResults.length; i++) {
                    const batchPoint = batchResults[i];
                    const semiPoint = semiResults[i];

                    const S_batch = batchPoint.N_d / batchPoint.N_u;
                    const S_semi = semiPoint.N_d / semiPoint.N_u;

                    selectivityRatio.push({
                        t: batchPoint.t,
                        value: S_semi / S_batch
                    });
                }
            }

            // Redraw the plot
            if (canvas) {
                canvas.draw();
            }
        }

        // Run a simulation for the given type (batch or semibatch)
        function runSimulation(type) {
            const results = [];
            const t_f = initial_moles / feedRate; // Feed duration

            // Initial conditions
            let Y;
            if (type === 'batch') {
                Y = [initial_moles, initial_moles, 0, 0, T_initial];
            } else if (feedConfig === 'A to B') {
                Y = [0, initial_moles, 0, 0, T_initial];
            } else {
                Y = [initial_moles, 0, 0, 0, T_initial];
            }

            // Time stepping
            for (let t = t_start; t <= t_end; t += dt) {
                // Store current state
                results.push({
                    t: t,
                    N_a: Y[0],
                    N_b: Y[1],
                    N_d: Y[2],
                    N_u: Y[3],
                    T: Y[4]
                });

                // RK4 integration
                const k1 = derivatives(t, Y, type, t_f);
                const k2 = derivatives(t + dt/2, addArrays(Y, multiplyArray(k1, dt/2)), type, t_f);
                const k3 = derivatives(t + dt/2, addArrays(Y, multiplyArray(k2, dt/2)), type, t_f);
                const k4 = derivatives(t + dt, addArrays(Y, multiplyArray(k3, dt)), type, t_f);

                const nextY = addArrays(Y, multiplyArray(addArrays(
                    addArrays(k1, multiplyArray(k2, 2)),
                    addArrays(multiplyArray(k3, 2), k4)
                ), dt/6));

                Y = nextY;
            }

            return results;
        }

        // Calculate derivatives for the system
        function derivatives(t, Y, type, t_f) {
            const [N_a, N_b, N_d, N_u, T] = Y;

            // Calculate rate constants
            const k1 = k1_0 * Math.exp(-E1 / (R * T));
            const k2 = k2_0 * Math.exp(-E2 / (R * T));

            // Calculate reaction rates
            const r1 = k1 * (N_a/V) * Math.pow(N_b/V, 2);
            const r2 = k2 * (N_a/V) * (N_b/V);

            // Calculate energy balance
            const dTdt = (UA * (T_coolant - T) + (-dh1 * r1 - dh2 * r2) * V) / Cp_total;

            // Initialize derivatives
            let dNadt, dNbdt;

            if (type === 'batch') {
                dNadt = -(r1 + r2) * V;
                dNbdt = -(r1 + r2) * V;
            } else {
                // Semibatch case
                if (feedConfig === 'A to B') {
                    const F_A0 = t < t_f ? feedRate : 0;
                    dNadt = F_A0 - (r1 + r2) * V;
                    dNbdt = -(r1 + r2) * V;
                } else {
                    const F_B0 = t < t_f ? feedRate : 0;
                    dNadt = -(r1 + r2) * V;
                    dNbdt = F_B0 - (r1 + r2) * V;
                }
            }

            return [
                dNadt,
                dNbdt,
                r1 * V,    // dN_d/dt
                r2 * V,    // dN_u/dt
                dTdt
            ];
        }

        // Helper functions for array math
        function addArrays(a, b) {
            return a.map((val, i) => val + b[i]);
        }

        function multiplyArray(a, factor) {
            return a.map(val => val * factor);
        }

        // Draw axes and grid
        function drawAxesAndGrid(p) {
            p.push();
            p.stroke(0);
            p.strokeWeight(1);
            p.fill(0);
            p.textSize(12);

            // X-axis (time)
            const margin = 50;
            const plotWidth = p.width - 2 * margin;
            const plotHeight = p.height - 2 * margin;

            // Draw axes
            p.line(margin, p.height - margin, p.width - margin, p.height - margin); // X-axis
            p.line(margin, margin, margin, p.height - margin); // Y-axis

            // X-axis ticks and labels
            for (let t = 0; t <= 2.0; t += 0.5) {
                const x = margin + (t / 2.0) * plotWidth;
                p.line(x, p.height - margin, x, p.height - margin + 5);
                p.text(t.toFixed(1), x - 5, p.height - margin + 20);
            }
            p.text('time (h)', p.width / 2, p.height - margin + 40);

            // Y-axis ticks and labels
            if (plotType === 'selectivity') {
                // Dynamic y-range for selectivity plot
                let minY = Infinity, maxY = -Infinity;
                selectivityRatio.forEach(point => {
                    minY = Math.min(minY, point.value);
                    maxY = Math.max(maxY, point.value);
                });

                const padding = (maxY - minY) * 0.1;
                minY -= padding;
                maxY += padding;

                const yTicks = 5;
                const yStep = (maxY - minY) / yTicks;

                for (let i = 0; i <= yTicks; i++) {
                    const yValue = minY + i * yStep;
                    const y = p.height - margin - (i / yTicks) * plotHeight;
                    p.line(margin - 5, y, margin, y);
                    p.text(yValue.toFixed(2), margin - 40, y + 5);
                }
                p.text('selectivity ratio', margin - 60, margin - 20);
            } else {
                // Fixed y-range for mole plots
                for (let moles = 0; moles <= 100; moles += 20) {
                    const y = p.height - margin - (moles / 100) * plotHeight;
                    p.line(margin - 5, y, margin, y);
                    p.text(moles, margin - 30, y + 5);
                }
                p.text('moles (mol)', margin - 60, margin - 20);
            }

            // Grid lines
            p.stroke(200);
            if (plotType === 'selectivity') {
                // Vertical grid lines (same as x-axis ticks)
                for (let t = 0; t <= 2.0; t += 0.5) {
                    const x = margin + (t / 2.0) * plotWidth;
                    p.line(x, margin, x, p.height - margin);
                }

                // Horizontal grid lines (same as y-axis ticks)
                const yTicks = 5;
                for (let i = 0; i <= yTicks; i++) {
                    const y = p.height - margin - (i / yTicks) * plotHeight;
                    p.line(margin, y, p.width - margin, y);
                }
            } else {
                // Vertical grid lines (same as x-axis ticks)
                for (let t = 0; t <= 2.0; t += 0.5) {
                    const x = margin + (t / 2.0) * plotWidth;
                    p.line(x, margin, x, p.height - margin);
                }

                // Horizontal grid lines (same as y-axis ticks)
                for (let moles = 0; moles <= 100; moles += 20) {
                    const y = p.height - margin - (moles / 100) * plotHeight;
                    p.line(margin, y, p.width - margin, y);
                }
            }

            p.pop();
        }

        // Draw data curves
        function drawDataCurves(p) {
            const margin = 50;
            const plotWidth = p.width - 2 * margin;
            const plotHeight = p.height - 2 * margin;

            p.push();
            p.noFill();
            p.strokeWeight(2);

            if (plotType === 'selectivity') {
                // Draw selectivity ratio curve
                if (selectivityRatio.length === 0) return;

                // Calculate y-range
                let minY = Infinity, maxY = -Infinity;
                selectivityRatio.forEach(point => {
                    minY = Math.min(minY, point.value);
                    maxY = Math.max(maxY, point.value);
                });

                const padding = (maxY - minY) * 0.1;
                minY -= padding;
                maxY += padding;

                // Draw curve
                p.beginShape();
                p.stroke('#800080');
                selectivityRatio.forEach(point => {
                    const x = margin + (point.t / 2.0) * plotWidth;
                    const y = p.height - margin - ((point.value - minY) / (maxY - minY)) * plotHeight;
                    p.vertex(x, y);
                });
                p.endShape();
            } else {
                // Draw mole curves
                const results = plotType === 'batch' ? batchResults : semiResults;

                // Draw N_a (green, dashed)
                p.beginShape();
                p.stroke('#2ca02c');
                results.forEach(point => {
                    const x = margin + (point.t / 2.0) * plotWidth;
                    const y = p.height - margin - (point.N_a / 100) * plotHeight;
                    p.vertex(x, y);
                });
                p.endShape();

                // Draw N_b (black, dashed)
                p.beginShape();
                p.stroke('#000000');
                results.forEach(point => {
                    const x = margin + (point.t / 2.0) * plotWidth;
                    const y = p.height - margin - (point.N_b / 100) * plotHeight;
                    p.vertex(x, y);
                });
                p.endShape();

                // Draw N_d (blue, solid)
                p.beginShape();
                p.stroke('#1f77b4');
                results.forEach(point => {
                    const x = margin + (point.t / 2.0) * plotWidth;
                    const y = p.height - margin - (point.N_d / 100) * plotHeight;
                    p.vertex(x, y);
                });
                p.endShape();

                // Draw N_u (red, solid)
                p.beginShape();
                p.stroke('#d62728');
                results.forEach(point => {
                    const x = margin + (point.t / 2.0) * plotWidth;
                    const y = p.height - margin - (point.N_u / 100) * plotHeight;
                    p.vertex(x, y);
                });
                p.endShape();
            }

            p.pop();
        }
    </script>
</body>
