                state.edges.push({ from: bestParent, to: newNode });

                // Rewire the tree
                for (const node of nearbyNodes) {
                    const cost = newNode.cost + distance(newNode, node);
                    if (cost < node.cost && isCollisionFree(newNode, node)) {
                        // Find and update the edge
                        const edgeIndex = state.edges.findIndex(e => e.to === node);
                        if (edgeIndex !== -1) {
                            state.edges.splice(edgeIndex, 1);
                        }

                        node.parent = newNode;
                        node.cost = cost;
                        state.edges.push({ from: newNode, to: node });
                    }
                }

                // Check if goal is reached
                if (checkGoalReached(newNode)) {
                    const newPath = tracePath(newNode);
                    const newPathLength = calculatePathLength(newPath);

                    if (!state.goalReached || newPathLength < state.pathLength) {
                        state.goalReached = true;
                        state.path = newPath;
                        state.pathLength = newPathLength;
                    }
                }

                return true;
            }
            return false;
        }

