                switch (state.treeType) {
                    case 'Random Tree':
                        success = addRandomTreeNode();
                        break;
                    case 'RRT':
                        success = addRRTNode();
                        break;
                    case 'RRT*':
                        success = addRRTStarNode();
                        break;
                }

                if (success) added++;
            }

            updateStatusText();
        }

        function updateStatusText() {
