        document.getElementById('btn-add-100').addEventListener('click', function() {
            addNodes(100);
        });

        document.getElementById('btn-add-200').addEventListener('click', function() {
            addNodes(200);
        });

        document.getElementById('btn-add-500').addEventListener('click', function() {
