        });

        document.getElementById('slider-bias').addEventListener('input', function() {
            state.explorationBias = parseFloat(this.value);
            document.getElementById('bias-value-label').textContent = this.value;
        });

        document.getElementById('slider-radius').addEventListener('input', function() {
            state.goalRadius = parseFloat(this.value);
            document.getElementById('radius-value-label').textContent = this.value;

            // Check if any existing node now reaches the goal
            if (!state.goalReached) {
                for (const node of state.nodes) {
                    if (checkGoalReached(node)) {
                        state.goalReached = true;
                        state.path = tracePath(node);
                        state.pathLength = calculatePathLength(state.path);
