            <input type="range" id="slider-v" min="0" max="2.0" step="0.01" value="0">
            <input type="number" id="input-v" min="0" max="2.0" step="0.01" value="0">
        </div>

        <div class="control-group">
            <label for="slider-lambda">\( \lambda \)</label>
            <input type="range" id="slider-lambda" min="0" max="2.0" step="0.01" value="1.1">
            <input type="number" id="input-lambda" min="0" max="2.0" step="0.01" value="1.1">
        </div>
    </div>

    <div id="visualization-container">
        <canvas id="three-canvas"></canvas>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        // Initialize Three.js scene
        const canvas = document.getElementById('three-canvas');
        const renderer = new THREE.WebGLRenderer({ canvas, antialias: true });
        renderer.setPixelRatio(window.devicePixelRatio);
        renderer.setClearColor(0xf0f0f0);

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(60, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(4, 4, 4);
        camera.lookAt(0, 1, 0);

        const controls = new THREE.OrbitControls(camera, canvas);
        controls.target.set(0, 1, 0);

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x666666);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
        directionalLight.position.set(5, 5, 5);
        scene.add(directionalLight);

        // Axes helper
        const axesHelper = new THREE.AxesHelper(4);
        scene.add(axesHelper);

        // Potential surface
        const surfaceGeometry = new THREE.ParametricGeometry((u, v, target) => {
            const rho = 0.4 + u * 2.1;
            const theta = v * 2 * Math.PI;
            const x = rho * Math.cos(theta);
            const y_surf = rho * rho + 1 / (rho * rho);
            const z_surf = -rho * Math.sin(theta);
            target.set(x, y_surf, z_surf);
        }, 50, 50);

        const surfaceMaterial = new THREE.MeshStandardMaterial({
            color: 0xE69943,
            metalness: 0.4,
            roughness: 0.5,
            side: THREE.DoubleSide
        });

        const surface = new THREE.Mesh(surfaceGeometry, surfaceMaterial);
        scene.add(surface);

        // Central cylinder
        const cylinderGeometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
        const cylinderMaterial = new THREE.MeshStandardMaterial({
            color: 0xcccccc,
            metalness: 0.1,
            roughness: 0.9
        });

        const cylinder = new THREE.Mesh(cylinderGeometry, cylinderMaterial);
        cylinder.position.set(0, 1.25, 0);
        scene.add(cylinder);

        // Ion path variables
        let ionPath = null;
        const pathMaterial = new THREE.MeshBasicMaterial({ color: 0x61AEEF });

        // RK4 solver
        function rk4(dyds, y0, t0, t1, dt) {
            const result = [];
            let t = t0;
            let y = [...y0];

            while (t <= t1) {
                result.push({ t, y: [...y] });

                const k1 = dyds(t, y);
                const k2 = dyds(t + dt/2, y.map((yi, i) => yi + dt/2 * k1[i]));
                const k3 = dyds(t + dt/2, y.map((yi, i) => yi + dt/2 * k2[i]));
                const k4 = dyds(t + dt, y.map((yi, i) => yi + dt * k3[i]));

                for (let i = 0; i < y.length; i++) {
                    y[i] += dt/6 * (k1[i] + 2*k2[i] + 2*k3[i] + k4[i]);
                }

                t += dt;
            }

            return result;
        }

        // Update visualization function
        function updateVisualization() {
            // Get current parameter values
            const t = parseFloat(document.getElementById('input-t').value);
            const rho0 = parseFloat(document.getElementById('input-rho0').value);
            const zeta0 = parseFloat(document.getElementById('input-zeta0').value);
            const e0 = parseFloat(document.getElementById('input-e0').value);
            const v = parseFloat(document.getElementById('input-v').value);
            const lambda = parseFloat(document.getElementById('input-lambda').value);

            // Define ODE system
            function dyds(s, y) {
                const [rho, drho, phi] = y;
                const d2rho = -(2*rho - 2/Math.pow(rho,3)) - 4*lambda*lambda*rho + 4*e0/Math.pow(rho,3);
                const dphi = 2*(e0/Math.pow(rho,2) - lambda);
                return [drho, d2rho, dphi];
            }

            // Solve ODE
            const solution = rk4(dyds, [rho0, 0, 0], 0, t, 0.05);

            // Generate points
            const points = [];
            for (const step of solution) {
                const [rho_i, drho_i, phi_i] = step.y;
                const u_rho = rho_i*rho_i + 1/(rho_i*rho_i);
                const sqrt_arg = e0 - u_rho;
                const z_i = Math.sqrt(Math.max(0, sqrt_arg)) * Math.sin(Math.sqrt(2) * v * step.t + zeta0);
                const x_i = rho_i * Math.cos(phi_i);
                const y_i = rho_i * Math.sin(phi_i);
                points.push(new THREE.Vector3(x_i, z_i, -y_i));
            }

            // Remove old path if exists
            if (ionPath) {
                scene.remove(ionPath);
                ionPath.geometry.dispose();
            }

            // Create new path
            if (points.length > 1) {
                const curve = new THREE.CatmullRomCurve3(points);
                const tubeGeometry = new THREE.TubeGeometry(curve, 512, 0.04, 8, false);
                ionPath = new THREE.Mesh(tubeGeometry, pathMaterial);
                ionPath.name = 'ionPath';
                scene.add(ionPath);
            }
        }

        // Link sliders and number inputs
        function linkInputs(sliderId, inputId) {
            const slider = document.getElementById(sliderId);
            const input = document.getElementById(inputId);

            slider.addEventListener('input', () => {
                input.value = slider.value;
                updateVisualization();
            });

            input.addEventListener('input', () => {
                slider.value = input.value;
                updateVisualization();
            });
        }

        linkInputs('slider-t', 'input-t');
        linkInputs('slider-rho0', 'input-rho0');
        linkInputs('slider-zeta0', 'input-zeta0');
        linkInputs('slider-e0', 'input-e0');
        linkInputs('slider-v', 'input-v');
        linkInputs('slider-lambda', 'input-lambda');

        // Initial visualization
        updateVisualization();

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Handle window resize
        function onWindowResize() {
            camera.aspect = (window.innerWidth - 280) / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth - 280, window.innerHeight);
        }

        window.addEventListener('resize', onWindowResize);
        onWindowResize();
        animate();
    </script>
</body>
</html>
