    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="slider-container">
                <label for="slider-r">R (Å):</label>
                <input type="range" id="slider-r" min="0.001" max="4.0" step="0.001" value="0.001">
                <output id="slider-r-value">0.001</output>
            </div>
        </div>

        <div id="potential-energy-plot-container"></div>

        <div id="density-plots-container">
            <div id="bonding-plot-container"></div>
            <div id="antibonding-plot-container"></div>
        </div>
    </div>

    <script>
        // Constants
        const ANGSTROM_TO_BOHR = 1 / 0.529177;

        // Initialize plots
        document.addEventListener('DOMContentLoaded', function() {
            // Initialize potential energy plot
            initPotentialEnergyPlot();

            // Initialize 3D density plots with default R value
            updateDensityPlots(0.001 * ANGSTROM_TO_BOHR);

            // Set up slider event listener
            document.getElementById('slider-r').addEventListener('input', function(e) {
                const R_angstroms = parseFloat(e.target.value);
                document.getElementById('slider-r-value').textContent = R_angstroms.toFixed(3);

                // Convert to atomic units and update density plots
                const R_au = R_angstroms * ANGSTROM_TO_BOHR;
                updateDensityPlots(R_au);
            });
        });

        function initPotentialEnergyPlot() {
            // Generate R values from 0.2 to 15 Bohr radii
            const R_au = [];
            for (let r = 0.2; r <= 15; r += 0.1) {
                R_au.push(r);
            }

            // Calculate energies
            const E_b = []; // Bonding energy
            const E_a = []; // Antibonding energy
            const R_angstrom = []; // For x-axis

            R_au.forEach(r => {
                const S_ab = (1 + r + r*r/3) * Math.exp(-r);
                const J = (1/r) - (1 + 1/r) * Math.exp(-2*r);
                const K = (1 + r) * Math.exp(-r);

                E_b.push((J + K) / (1 + S_ab));
                E_a.push((J - K) / (1 - S_ab));
                R_angstrom.push(r / ANGSTROM_TO_BOHR);
            });

            // Create traces
            const trace1 = {
                x: R_angstrom,
                y: E_b,
                type: 'scatter',
                mode: 'lines',
                name: 'bonding PE',
                line: {color: 'blue'}
            };

            const trace2 = {
                x: R_angstrom,
                y: E_a,
                type: 'scatter',
                mode: 'lines',
                name: 'antibonding PE',
                line: {color: 'red'}
            };

            // Layout
            const layout = {
                title: 'potential energy',
                xaxis: {
                    title: 'R (Å)',
                    range: [0, 8]
                },
                yaxis: {
                    title: 'E - E_H(1s) (Hartrees)',
                    range: [-0.6, 0.4]
                },
                legend: {
                    x: 0.8,
                    y: 0.9
                },
                margin: {t: 40, b: 60, l: 60, r: 40}
            };

            // Plot
            Plotly.newPlot('potential-energy-plot-container', [trace1, trace2], layout);
        }

        function updateDensityPlots(R_au) {
            // Create grid for x and y coordinates
            const gridSize = 60;
            const x = [];
            const y = [];
            for (let i = -3; i <= 3; i += 6/(gridSize-1)) {
                x.push(i);
                y.push(i);
            }

            // Calculate electron densities
            const z_bonding = [];
            const z_antibonding = [];

            // Calculate overlap integral
            const S_ab = (1 + R_au + R_au*R_au/3) * Math.exp(-R_au);
            const N_b = 1 / Math.sqrt(2 * (1 + S_ab));
            const N_a = 1 / Math.sqrt(2 * (1 - S_ab));

            y.forEach(yi => {
                const row_bonding = [];
                const row_antibonding = [];

                x.forEach(xi => {
                    // Calculate distances to nuclei
                    const distA = Math.sqrt(Math.pow(xi + R_au/2, 2) + yi*yi);
                    const distB = Math.sqrt(Math.pow(xi - R_au/2, 2) + yi*yi);

                    // Calculate atomic orbitals
                    const phiA = Math.exp(-distA);
                    const phiB = Math.exp(-distB);

                    // Calculate molecular orbitals
                    const psi_b = N_b * (phiA + phiB);
                    const psi_a = N_a * (phiA - phiB);

                    // Calculate probability densities
                    row_bonding.push(psi_b * psi_b);
                    row_antibonding.push(psi_a * psi_a);
                });

                z_bonding.push(row_bonding);
                z_antibonding.push(row_antibonding);
            });

            // Create bonding plot data
            const bondingData = {
                x: x,
                y: y,
                z: z_bonding,
                type: 'surface',
                colorscale: 'YlOrBr',
                showscale: false
            };

            // Create antibonding plot data
            const antibondingData = {
                x: x,
                y: y,
                z: z_antibonding,
                type: 'surface',
                colorscale: 'YlOrBr',
                showscale: false
            };

            // Common layout for both plots
            const layout = {
                margin: {t: 20, b: 20, l: 20, r: 20},
                scene: {
                    xaxis: {range: [-3, 3], showbackground: false, showticklabels: false, title: ''},
                    yaxis: {range: [-3, 3], showbackground: false, showticklabels: false, title: ''},
                    zaxis: {range: [0, 0.5], showbackground: false, showticklabels: false, title: ''},
                    camera: {
                        eye: {x: 1.5, y: 1.5, z: 0.8}
                    }
                }
            };

            // Update or create plots
            if (window.bondingPlot) {
                Plotly.restyle('bonding-plot-container', 'z', [z_bonding]);
                Plotly.restyle('antibonding-plot-container', 'z', [z_antibonding]);
            } else {
                window.bondingPlot = Plotly.newPlot('bonding-plot-container', [bondingData], layout);
                window.antibondingPlot = Plotly.newPlot('antibonding-plot-container', [antibondingData], layout);
            }
        }
    </script>
</body>
</html>
