    <script>
        // Simulation state
        let time = 0;
        let isPlaying = false;
        let animationId = null;
        let moonRotationMode = 'normal';

        // Visualization constants
        const EARTH_RADIUS = 80;
        const MOON_RADIUS = 25;
        const ORBIT_RADIUS = 250;
        const EARTH_ROTATION_PERIOD = 24; // hours
        const MOON_ORBIT_PERIOD = 672; // hours (28 days)

        // Checkbox states
        let showDayNight = false;
        let showNearSide = true;
        let showEarthMoonLine = true;
        let showMoonOnly = false;

        // p5.js sketch
        let sketch = function(p) {
            let canvas;

            p.setup = function() {
                let container = document.getElementById('visualization-area');
                canvas = p.createCanvas(container.clientWidth, container.clientHeight);
                canvas.parent('visualization-area');
                p.noLoop();
                updateDisplayTime();
            };

            p.draw = function() {
                p.background(0);
                p.translate(p.width / 2, p.height / 2);

                // Calculate angles based on time
                const earthRotation = (time / EARTH_ROTATION_PERIOD) * p.TWO_PI;
                const moonOrbit = (time / MOON_ORBIT_PERIOD) * p.TWO_PI;

                // Calculate moon rotation based on selected mode
                let moonRotation;
                switch (moonRotationMode) {
                    case 'half':
                        moonRotation = moonOrbit * 2;
                        break;
                    case 'double':
                        moonRotation = moonOrbit / 2;
                        break;
                    case 'none':
                        moonRotation = 0;
                        break;
                    case 'normal':
                    default:
                        moonRotation = moonOrbit;
                }

                // Draw Earth if not in moon-only mode
                if (!showMoonOnly) {
                    // Draw Earth
                    p.fill('#3B82F6');
                    p.noStroke();
                    p.circle(0, 0, EARTH_RADIUS * 2);

                    // Draw North Pole
                    p.fill(255);
                    p.circle(0, -EARTH_RADIUS * 0.8, 10);

                    // Draw simplified landmasses
                    p.fill('#22C55E');
                    p.beginShape();
                    p.vertex(-30, -20);
                    p.vertex(-10, -30);
                    p.vertex(20, -10);
                    p.vertex(40, 0);
                    p.vertex(30, 20);
                    p.vertex(0, 30);
                    p.vertex(-20, 10);
                    p.endShape(p.CLOSE);

                    // Rotate for Earth's rotation and draw stick figure
                    p.push();
                    p.rotate(earthRotation);
                    p.stroke('#EF4444');
                    p.strokeWeight(2);
                    p.line(0, -EARTH_RADIUS, 0, -EARTH_RADIUS + 15);
                    p.line(-10, -EARTH_RADIUS + 5, 10, -EARTH_RADIUS + 5);
                    p.line(0, -EARTH_RADIUS + 15, -10, -EARTH_RADIUS + 25);
                    p.line(0, -EARTH_RADIUS + 15, 10, -EARTH_RADIUS + 25);
                    p.fill('#EF4444');
                    p.noStroke();
                    p.circle(0, -EARTH_RADIUS, 10);
                    p.pop();

                    // Draw day/night terminator if enabled
                    if (showDayNight) {
                        p.fill(0, 0, 0, 128);
                        p.arc(0, 0, EARTH_RADIUS * 2, EARTH_RADIUS * 2, p.PI / 2, p.PI * 1.5);
                    }
                }

                // Calculate Moon's position (starting at top)
                let mx = showMoonOnly ? 0 : ORBIT_RADIUS * p.cos(moonOrbit - p.PI/2);
                let my = showMoonOnly ? 0 : ORBIT_RADIUS * p.sin(moonOrbit - p.PI/2);

                // Draw Earth-Moon line if enabled
                if (showEarthMoonLine && !showMoonOnly) {
                    p.stroke(255, 100);
                    p.strokeWeight(1);
                    p.drawingContext.setLineDash([5, 5]);
                    p.line(0, 0, mx, my);
                    p.drawingContext.setLineDash([]);
                }

                // Draw Moon
                p.push();
                p.translate(mx, my);

                // Moon body
                p.fill('#808080');
                p.noStroke();
                p.circle(0, 0, MOON_RADIUS * 2);

                // Near side indicator if enabled
                if (showNearSide) {
                    p.push();
                    p.rotate(moonRotation);
                    p.fill('#22C55E');
                    p.arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, -p.PI/2, p.PI/2);

                    // Draw stick figure on near side
                    p.stroke('#D946EF');
                    p.strokeWeight(2);
                    p.line(0, -MOON_RADIUS * 0.5, 0, -MOON_RADIUS * 0.2);
                    p.line(-MOON_RADIUS * 0.3, -MOON_RADIUS * 0.4, MOON_RADIUS * 0.3, -MOON_RADIUS * 0.4);
                    p.line(0, -MOON_RADIUS * 0.2, -MOON_RADIUS * 0.2, 0);
                    p.line(0, -MOON_RADIUS * 0.2, MOON_RADIUS * 0.2, 0);
                    p.fill('#D946EF');
                    p.noStroke();
                    p.circle(0, -MOON_RADIUS * 0.5, MOON_RADIUS * 0.4);
                    p.pop();
                }

                // Draw day/night terminator if enabled
                if (showDayNight) {
                    p.fill(0, 0, 0, 128);
                    p.arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, p.PI / 2, p.PI * 1.5);
                }

                p.pop();
            };

            p.windowResized = function() {
                let container = document.getElementById('visualization-area');
                p.resizeCanvas(container.clientWidth, container.clientHeight);
                p.redraw();
            };
        };

        // Create p5 instance
        new p5(sketch);

        // Helper function to update time display
        function updateDisplayTime() {
            const days = Math.floor(time / 24);
            const hours = Math.floor(time % 24);
            document.getElementById('display-time').textContent = `${days} d ${hours} h`;
        }

        // Event listeners
        document.getElementById('slider-time').addEventListener('input', function() {
            time = parseInt(this.value);
            updateDisplayTime();
            sketch.redraw();
        });

        document.getElementById('btn-play-pause').addEventListener('click', function() {
            isPlaying = !isPlaying;
            if (isPlaying) {
                this.textContent = '❚❚';
                function animate() {
                    if (time >= 672) time = 0;
                    time += 1;
                    document.getElementById('slider-time').value = time;
                    updateDisplayTime();
                    sketch.redraw();
                    if (isPlaying) {
                        animationId = requestAnimationFrame(animate);
                    }
                }
                animate();
            } else {
                this.textContent = '▶';
                if (animationId) {
                    cancelAnimationFrame(animationId);
                }
            }
        });

        document.getElementById('btn-step-back').addEventListener('click', function() {
            time = Math.max(0, time - 1);
            document.getElementById('slider-time').value = time;
            updateDisplayTime();
            sketch.redraw();
        });

        document.getElementById('btn-step-forward').addEventListener('click', function() {
            time = Math.min(672, time + 1);
            document.getElementById('slider-time').value = time;
            updateDisplayTime();
            sketch.redraw();
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            time = 0;
            document.getElementById('slider-time').value = time;
            updateDisplayTime();
            sketch.redraw();
        });

        // Rotation mode buttons
        const rotationButtons = ['btn-rot-half', 'btn-rot-normal', 'btn-rot-double', 'btn-rot-none'];
        rotationButtons.forEach(id => {
            document.getElementById(id).addEventListener('click', function() {
                // Update active state
                rotationButtons.forEach(btnId => {
                    document.getElementById(btnId).classList.remove('active');
                });
                this.classList.add('active');

                // Set rotation mode
                moonRotationMode = id.split('-')[2];
                sketch.redraw();
            });
        });

        // Checkbox event listeners
        document.getElementById('check-day-night').addEventListener('change', function() {
            showDayNight = this.checked;
            sketch.redraw();
        });

        document.getElementById('check-near-side').addEventListener('change', function() {
            showNearSide = this.checked;
            sketch.redraw();
        });

        document.getElementById('check-earth-moon-line').addEventListener('change', function() {
            showEarthMoonLine = this.checked;
            sketch.redraw();
        });

        document.getElementById('check-moon-only').addEventListener('change', function() {
            showMoonOnly = this.checked;
            sketch.redraw();
        });
    </script>
</body>
</html>
