    <script>
        // Constants for calculations
        const constants = {
            ν₀: 1000,       // band origin (cm⁻¹)
            B_upper: 0.98,  // B' (cm⁻¹)
            A_upper: 1.95,  // A' (cm⁻¹)
            B_lower: 1.0,   // B" (cm⁻¹)
            A_lower: 2.0,   // A" (cm⁻¹)
            zeta: 0.2,      // Coriolis coupling
            T: 300,         // Temperature (K)
            k: 0.695,       // Boltzmann constant (cm⁻¹/K)
            J_max: 60       // Maximum J value to calculate
        };

        // Initialize Plotly plot
        let plotDiv = document.getElementById('plot-div');
        let plotData = [];
        let plotLayout = {
            title: 'Rovibronic Spectrum',
            xaxis: {
                title: 'Wavenumber (cm⁻¹)',
                range: [902, 1091]
            },
            yaxis: {
                title: 'Absorbance',
                range: [0, 1.2]
            },
            showlegend: false,
            bargap: 0
        };

        Plotly.newPlot(plotDiv, plotData, plotLayout);

        // Generate spectrum data
        function generateSpectrumData() {
            let traces = [];
            let view = document.querySelector('input[name="view-selector"]:checked').value;

            if (view === 'full') {
                // Generate full spectrum (all K values, all sub-bands)
                traces = generateFullSpectrum();
            } else {
                // Generate sub-band view
                let K = parseInt(document.querySelector('input[name="k-selector"]:checked').value);
                let subbandType = document.querySelector('input[name="subband-type"]:checked').value;

                if (subbandType === 'full') {
                    // Both positive and negative sub-bands
                    traces.push(...generateSubBand(K, 'positive'));
                    traces.push(...generateSubBand(K, 'negative'));
                } else {
                    // Only positive or negative sub-band
                    traces.push(...generateSubBand(K, subbandType));
                }
            }

            return traces;
        }

        function generateFullSpectrum() {
            let traces = [];

            // Generate all K values (0-5)
            for (let K = 0; K <= 5; K++) {
                // Positive sub-band (ΔK=+1)
                traces.push(...generateSubBand(K, 'positive'));

                // Negative sub-band (ΔK=-1), except for K=0
                if (K > 0) {
                    traces.push(...generateSubBand(K, 'negative'));
                }
            }

            // For full spectrum, make all traces black
            traces.forEach(trace => {
                trace.marker.color = 'black';
            });

            return traces;
        }

        function generateSubBand(K, subbandType) {
            let traces = [];
            let branchSelector = document.querySelector('input[name="branch-selector"]:checked');
            let branch = branchSelector ? branchSelector.value : 'all';

            // Generate data for each branch
            let branches = [];
            if (branch === 'all') {
                branches = ['P', 'Q', 'R'];
            } else {
                branches = [branch];
            }

            branches.forEach(b => {
                let { x, y } = calculateSubBand(K, subbandType, b);

                traces.push({
                    x: x,
                    y: y,
                    type: 'bar',
                    marker: {
                        color: subbandType === 'positive' ? 'red' : 'blue',
                        line: {
                            width: 0
                        }
                    },
                    width: 0.1,
                    name: `K=${K} ${subbandType} ${b}`
                });
            });

            return traces;
        }

        function calculateSubBand(K, subbandType, branch) {
            let x = [];
            let y = [];
            let { ν₀, B_upper, A_upper, B_lower, A_lower, zeta, T, k, J_max } = constants;

            // Calculate ΔK based on subband type
            let ΔK = subbandType === 'positive' ? +1 : -1;
            let K_upper = K + ΔK;

            // Calculate Boltzmann factor for the K level
            let E_K = K * (A_lower - B_lower) + K * K * (B_lower - A_lower);
            let population = Math.exp(-E_K / (k * T));

            // Calculate lines for each J
            for (let J = Math.max(K, 1); J <= J_max; J++) {
                let ν = 0;
                let intensity = 0;

                // Calculate wavenumber based on branch
                if (branch === 'P' && J > 0) {
                    ν = ν₀ + (B_upper - B_lower) * J * (J - 1) + (A_upper - B_upper - A_lower + B_lower) * K * K;
                    intensity = J * population;
                } else if (branch === 'Q') {
                    ν = ν₀ + (B_upper - B_lower) * J * (J + 1) + (A_upper - B_upper - A_lower + B_lower) * K * K;
                    intensity = (2 * J + 1) * population;
                } else if (branch === 'R') {
                    ν = ν₀ + (B_upper - B_lower) * (J + 1) * (J + 2) + (A_upper - B_upper - A_lower + B_lower) * K * K;
                    intensity = (J + 1) * population;
                }

                // Apply Coriolis coupling effect
                if (branch === 'Q') {
                    ν += 2 * zeta * Math.sqrt(J * (J + 1)) * K;
                }

                if (ν > 0) {
                    x.push(ν);
                    y.push(intensity);
                }
            }

            return { x, y };
        }

        // Update plot based on current settings
        function updatePlot() {
            let view = document.querySelector('input[name="view-selector"]:checked').value;
            let traces = generateSpectrumData();

            // Update plot title
            let title = 'Rovibronic Spectrum';
            if (view === 'sub') {
                let K = document.querySelector('input[name="k-selector"]:checked').value;
                let subbandType = document.querySelector('input[name="subband-type"]:checked').value;

                title = `K = ${K} Sub-band`;

                if (subbandType !== 'full') {
                    title += `\n"${subbandType}" Sub-band`;

                    let branch = document.querySelector('input[name="branch-selector"]:checked').value;
                    if (branch !== 'all') {
                        title += ` (${branch} Branch)`;
                    }
                }
            }

            Plotly.react(plotDiv, traces, {
                ...plotLayout,
                title: title,
                xaxis: {
                    ...plotLayout.xaxis,
                    range: [
                        parseInt(document.getElementById('slider-x-lower').value),
                        parseInt(document.getElementById('slider-x-upper').value)
                    ]
                }
            });
        }

        // Event listeners for controls
        document.querySelectorAll('input[name="view-selector"]').forEach(radio => {
            radio.addEventListener('change', function() {
                let isSubView = this.value === 'sub';

                // Enable/disable K selector and sub-band type selector
                document.getElementById('k-selector-group').classList.toggle('disabled', !isSubView);
                document.getElementById('subband-type-group').classList.toggle('disabled', !isSubView);

                // Hide branch selector unless in specific sub-band view
                if (!isSubView || document.querySelector('input[name="subband-type"]:checked').value === 'full') {
                    document.getElementById('branch-selector-group').classList.add('hidden');
                }

                updatePlot();
            });
        });

        document.querySelectorAll('input[name="k-selector"]').forEach(radio => {
            radio.addEventListener('change', function() {
                let K = parseInt(this.value);

                // Disable negative sub-band option for K=0
                document.getElementById('radio-subband-negative').disabled = K === 0;

                // If negative was selected but now disabled, switch to full
                if (K === 0 && document.querySelector('input[name="subband-type"]:checked').value === 'negative') {
                    document.getElementById('radio-subband-full').checked = true;
                    document.getElementById('branch-selector-group').classList.add('hidden');
                }

                updatePlot();
            });
        });

        document.querySelectorAll('input[name="subband-type"]').forEach(radio => {
            radio.addEventListener('change', function() {
                let isFull = this.value === 'full';

                // Show/hide branch selector
                document.getElementById('branch-selector-group').classList.toggle('hidden', isFull);

                updatePlot();
            });
        });

        document.querySelectorAll('input[name="branch-selector"]').forEach(radio => {
            radio.addEventListener('change', updatePlot);
        });

        // Zoom controls
        document.getElementById('slider-x-lower').addEventListener('input', function() {
            let lower = parseInt(this.value);
            let upper = parseInt(document.getElementById('slider-x-upper').value);

            document.getElementById('span-x-lower-value').textContent = lower;

            // Prevent lower >= upper
            if (lower >= upper) {
                let newUpper = lower + 1;
                if (newUpper <= 1100) {
                    document.getElementById('slider-x-upper').value = newUpper;
                    document.getElementById('span-x-upper-value').textContent = newUpper;
                }
            }

            updatePlot();
        });

        document.getElementById('slider-x-upper').addEventListener('input', function() {
            let upper = parseInt(this.value);
            let lower = parseInt(document.getElementById('slider-x-lower').value);

            document.getElementById('span-x-upper-value').textContent = upper;

            // Prevent upper <= lower
            if (upper <= lower) {
                let newLower = upper - 1;
                if (newLower >= 900) {
                    document.getElementById('slider-x-lower').value = newLower;
                    document.getElementById('span-x-lower-value').textContent = newLower;
                }
            }

            updatePlot();
        });

        // Initial plot update
        updatePlot();
    </script>
