            boundingBox = new THREE.BoxHelper(radiationPattern, 0x000000);
            boundingBox.visible = false;
            scene.add(boundingBox);

            // Setup event listeners
            setupEventListeners();

            // Start animation loop
            animate();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        // Create radiation pattern based on wave type
        function createRadiationPattern(waveType) {
            // Remove existing pattern if it exists
            if (radiationPattern) {
                scene.remove(radiationPattern);
            }

            // Define the parametric function based on wave type
            let parametricFunction;
            switch (waveType) {
                case 'P':
                    parametricFunction = (u, v, target) => {
                        const theta = Math.PI * (u - 0.5); // -π/2 to π/2
                        const phi = 2 * Math.PI * v; // 0 to 2π
                        const r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                        target.set(
                            r * Math.cos(theta) * Math.sin(phi),
                            r * Math.sin(theta),
                            r * Math.cos(theta) * Math.cos(phi)
                        );
                    };
                    break;
                case 'SV':
                    parametricFunction = (u, v, target) => {
                        const theta = Math.PI * (u - 0.5);
                        const phi = 2 * Math.PI * v;
                        const r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                        target.set(
                            r * Math.cos(theta) * Math.sin(phi),
                            r * Math.sin(theta),
                            r * Math.cos(theta) * Math.cos(phi)
                        );
                    };
                    break;
                case 'SH':
                    parametricFunction = (u, v, target) => {
                        const theta = Math.PI * (u - 0.5);
                        const phi = 2 * Math.PI * v;
                        const r = Math.abs(Math.cos(theta) * Math.cos(phi));
                        target.set(
                            r * Math.cos(theta) * Math.sin(phi),
                            r * Math.sin(theta),
                            r * Math.cos(theta) * Math.cos(phi)
                        );
                    };
                    break;
            }

            // Create geometry and mesh
            const geometry = new THREE.ParametricGeometry(parametricFunction, 50, 50);
            const material = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                side: THREE.DoubleSide,
                flatShading: true
            });

            radiationPattern = new THREE.Mesh(geometry, material);

            // Apply initial rotations based on slider values
            updateRadiationPatternRotation();

            scene.add(radiationPattern);

            // Update bounding box if it exists
            if (boundingBox) {
                boundingBox.update();
            }
        }

        // Create compass overlay
        function createCompass() {
            compass = new THREE.Group();

            // Create compass circle
            const circleGeometry = new THREE.CircleGeometry(2.5, 64);
            const circleEdges = new THREE.EdgesGeometry(circleGeometry);
            const circleMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
            const circle = new THREE.LineSegments(circleEdges, circleMaterial);
            circle.rotation.x = Math.PI / 2;
            compass.add(circle);

            // Create cardinal direction markers
            const directions = ['N', 'E', 'S', 'W'];
            const angles = [0, Math.PI/2, Math.PI, 3*Math.PI/2];

            directions.forEach((dir, i) => {
                const sprite = createTextSprite(dir);
                sprite.position.set(
                    2.7 * Math.cos(angles[i]),
                    0,
                    2.7 * Math.sin(angles[i])
                );
                compass.add(sprite);
            });

            // Create degree markings
            for (let deg = 0; deg < 360; deg += 30) {
                if (deg % 90 === 0) continue; // Skip cardinal directions

                const angle = THREE.MathUtils.degToRad(deg);
                const sprite = createTextSprite(deg + '°');
                sprite.position.set(
                    2.7 * Math.cos(angle),
                    0,
                    2.7 * Math.sin(angle)
                );
                compass.add(sprite);
            }

            scene.add(compass);
        }

        // Create text sprite for compass
        function createTextSprite(text) {
            const canvas = document.createElement('canvas');
            canvas.width = 256;
            canvas.height = 128;
            const context = canvas.getContext('2d');
            context.fillStyle = 'black';
            context.font = 'Bold 20px Arial';
            context.textAlign = 'center';
            context.textBaseline = 'middle';
            context.fillText(text, canvas.width/2, canvas.height/2);

            const texture = new THREE.CanvasTexture(canvas);
            const material = new THREE.SpriteMaterial({ map: texture });
            const sprite = new THREE.Sprite(material);
            sprite.scale.set(0.2, 0.1, 1);

            return sprite;
        }

        // Create up arrow indicator
        function createUpArrow() {
            const dir = new THREE.Vector3(0, 1, 0);
            const origin = new THREE.Vector3(0, 0, 0);
            const length = 0.5;
            const color = 0x000000;

            upArrow = new THREE.ArrowHelper(dir, origin, length, color);
            scene.add(upArrow);

            // Add 'U' label
            const sprite = createTextSprite('U');
            sprite.position.set(0, 0.6, 0);
            scene.add(sprite);
        }

        // Update radiation pattern rotation based on slider values
        function updateRadiationPatternRotation() {
            if (!radiationPattern) return;

            const strike = THREE.MathUtils.degToRad(parseInt(document.getElementById('slider-strike').value));
            const dip = THREE.MathUtils.degToRad(parseInt(document.getElementById('slider-dip').value));
            const rake = THREE.MathUtils.degToRad(parseInt(document.getElementById('slider-rake').value));

            radiationPattern.rotation.set(0, 0, 0);
            radiationPattern.rotateZ(strike);
            radiationPattern.rotateX(dip);
            radiationPattern.rotateY(rake);

            if (boundingBox) {
                boundingBox.update();
            }
        }

        // Setup event listeners for UI controls
        function setupEventListeners() {
            // Sliders
            document.getElementById('slider-strike').addEventListener('input', function() {
                document.getElementById('strike-value').textContent = this.value;
                updateRadiationPatternRotation();
            });

            document.getElementById('slider-dip').addEventListener('input', function() {
                document.getElementById('dip-value').textContent = this.value;
                updateRadiationPatternRotation();
            });

            document.getElementById('slider-rake').addEventListener('input', function() {
                document.getElementById('rake-value').textContent = this.value;
                updateRadiationPatternRotation();
            });

            // Wave type buttons
            document.getElementById('btn-p').addEventListener('click', function() {
                if (currentWaveType === 'P') return;
                document.getElementById('btn-p').classList.add('active');
                document.getElementById('btn-sv').classList.remove('active');
                document.getElementById('btn-sh').classList.remove('active');
                currentWaveType = 'P';
                createRadiationPattern(currentWaveType);
            });

            document.getElementById('btn-sv').addEventListener('click', function() {
                if (currentWaveType === 'SV') return;
                document.getElementById('btn-p').classList.remove('active');
                document.getElementById('btn-sv').classList.add('active');
                document.getElementById('btn-sh').classList.remove('active');
                currentWaveType = 'SV';
                createRadiationPattern(currentWaveType);
            });

            document.getElementById('btn-sh').addEventListener('click', function() {
                if (currentWaveType === 'SH') return;
                document.getElementById('btn-p').classList.remove('active');
                document.getElementById('btn-sv').classList.remove('active');
                document.getElementById('btn-sh').classList.add('active');
                currentWaveType = 'SH';
                createRadiationPattern(currentWaveType);
            });

            // Bounding box checkbox
            document.getElementById('checkbox-box').addEventListener('change', function() {
                boundingBox.visible = this.checked;
            });
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Handle window resize
        function onWindowResize() {
            const controlPanelHeight = document.getElementById('control-panel').offsetHeight;
            camera.aspect = window.innerWidth / (window.innerHeight - controlPanelHeight);
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight - controlPanelHeight);
        }

        // Initialize the application when the page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
